INSERT INTO `posts` (`id`, `title`, `date`, `description`, `content`, `link_post`, `image`, `country_id`, `category_id`, `sport_id`, `slug`, `hot_news`, `status`) VALUES
(1, 'Văn Toàn: “Tôi chưa gặp và chưa biết rõ về bóng đá của HLV Troussier\"', '2023-05-20', 'Trả lời phỏng vấn tờ Best Eleven (Hàn Quốc), Văn Toàn tiết lộ chưa gặp và chưa hiểu rõ về triết lý bóng đá của HLV Troussier. Tuy nhiên, tiền đạo đang khoác áo Seoul E-Land cũng khẳng định các cầu thủ phải thích nghi và thi đấu thật tốt.', 'Tại Asian Cup 2023, ĐT Việt Nam nằm ở bảng D cùng với đương kim á quân Nhật Bản, Iraq và Indonesia. Dù bảng đấu này không hề dễ dàng với thầy trò HLV Troussier nhưng Văn Toàn vẫn tin tưởng vào khả năng đi tiếp của đội nhà.\r\n\r\n\"Tôi tin Việt Nam sẽ đạt kết quả tốt. Chúng tôi đã có kinh nghiệm ở Asian Cup\", Văn Toàn trả lời Best Eleven, tờ báo lâu đời của Hàn Quốc.\r\n\r\nAsian Cup 2023 cũng là giải đấu chính thức đầu tiên của ĐT Việt Nam dưới thời HLV Troussier. Dù chưa trực tiếp gặp mặt tân HLV của tuyển Việt Nam nhưng Văn Toàn khẳng định cầu thru cần phải thích nghi và thi đấu thật tốt.\r\n\r\n\"Tôi chưa gặp và chưa biết rõ về bóng đá của Troussier. Nhưng bóng đá luôn thay đổi. Cầu thủ cần phải thích nghi với lối chơi của HLV mới và thi đấu thật tốt\", tiền đạo đang khoác áo Seoul E-Land cho biết.\r\n\r\nTrước đó, trong lần đầu tiên ĐT Việt Nam tập trung dưới thời HLV Troussier vào đầu tháng 3, Văn Toàn, Công Phượng và Quang Hải vắng mặt do thi đấu ở nước ngoài. Vào giữa thagns 6 tới đây, ĐT Việt Nam sẽ tập trung đợt 2 theo lịch FIFA Days. Văn Toàn nhiều khả năng sẽ trở lại đội tuyển dịp này.\r\n\r\nVăn Toàn rời HAGL gia nhập Seoul E-Land FC ở K-League 2 sau khi mùa giải 2022 kết thúc. Tính đến hiện tại, tiền đạo sinh năm 1996 đã chơi 7 trận chính thức cho Seoul E-Land, với 253 phút. Anh cũng có 1 pha kiến tạo cho đội bóng đang chơi ở giải hạng 2 Hàn Quốc.', 'https://onsports.vn/post/van-toan-toi-chua-gap-va-chua-biet-ro-ve-bong-da-cua-hlv-troussier', 'https://stvinaprod.vtvcab.vn/3e712a76-2a00-476c-9077-ed28f8418112.jpg', 1, 1, 1, 'van-toan-“toi-chua-gap-va-chua-biet-ro-ve-bong-da-cua-hlv-troussier', 1, 1),
(2, 'Công Phượng: \"Muốn trở lại ĐTQG, tôi phải được đá chính tại Yokohama FC\"', '2023-05-19', 'Công Phượng vẫn đang chật vật để tìm kiếm lần đá chính đầu tiên trong màu áo Yokohama FC. Điều đó sẽ ảnh hưởng trực tiếp tới một suất lên ĐTQG của tiền đạo gốc Nghệ An dưới triều đại mới của HLV Philippe Troussier.', 'Mới đây, tiền đạo Công Phượng đã có những chia sẻ với tờ Hochi News xoay quanh cuộc đọ sức với đội tuyển Nhật Bản tại Asian Cup diễn ra vào đầu năm 2024. Cựu cầu thủ HAGL cho biết: \"Với Việt Nam, việc được đối đầu với đội tuyển mạnh như Nhật Bản là điều hết sức quan trọng. Về cá nhân, tôi đã thích nghi được với cuộc sống ở đây cũng như nắm được cách mọi thứ vận hành ra sao\".\r\n\r\nTại AFF Cup 2022, Công Phượng đã chủ động xin HLV Park Hang-seo không lên tập trung ĐTQG để có sự chuẩn bị tốt nhất cho chuyến xuất ngoại được xem là cuối cùng của mình. Dù vậy, mọi thứ vẫn đang rất khó khăn với cựu cầu thủ HAGL. Anh chưa được ra sân tại J.League cũng như mới chỉ vào sân được 1 phút tại Cúp Hoàng Đế. Điều này chắc chắn sẽ ảnh hưởng tới suất lên ĐTQG của Công Phượng dưới thời tân HLV trưởng Philippe Troussier. Và cầu thủ người Nghệ An cũng nhận thức rõ được điều đó.\r\n\r\n\"Tôi vẫn chưa biết nhiều về HLV Troussier. Có lẽ, người dân Nhật Bản còn biết về ông ấy nhiều hơn tôi. Nhưng tôi biết rằng, muốn được triệu tập lên ĐTQG, tôi sẽ phải được đá chính thường xuyên tại Yokohama FC. Và tôi sẽ nỗ lực để thực hiện điều đó\", Công Phượng chia sẻ.\r\n\r\nTại Asian Cup 2024, Việt Nam nằm cùng bảng với đội tuyển Nhật Bản. Cuộc đọ sức giữa hai đội sẽ tái hiện tại màn đối đầu ở vòng 1/16 của Asian Cup 2019. Khi trả lời Hochi News, Công Phượng cũng không ngần ngại thể hiện: \"Đó sẽ là trận đấu quan trọng bởi nó sẽ giúp Việt Nam trở nên mạnh mẽ hơn. Tôi muốn ghi bàn và giành chiến thắng trước Nhật Bản giống như cách mà HLV Moriyasu đã giúp Nhật Bản khiến cả thế giới phải kinh ngạc tại World Cup vừa rồi\".', 'https://onsports.vn/post/cong-phuong-muon-tro-lai-dtqg-toi-phai-duoc-da-chinh-tai-yokohama-fc', 'https://stvinaprod.vtvcab.vn/40d41133-2a4b-4967-8fdc-8d442ed3c5c3.jpg', 1, 1, 1, 'cong-phuong-muon-tro-lai-dtqg-toi-phai-duoc-da-chinh-tai-yokohama-fc', 1, 1),
(3, 'U23 Việt Nam nhận tin vui sau quyết định của LĐBĐ châu Á', '2023-05-18', 'U23 Việt Nam nhận tin vui sau quyết định của LĐBĐ châu Á', 'U22 Việt Nam vừa qua kỳ SEA Games 32 không thực sự thành công khi chỉ giành được tấm HCĐ. Tuy nhiên qua giải đấu vừa rồi, thầy trò HLV Troussier đã có thêm những tích lũy cho giai đoạn quan trọng hơn ở phía trước.\r\n\r\nTrở về từ SEA Games, lược gia người Pháp tiếp tục bắt tay vào củng cố đội hình cho U22 Việt Nam để tham dự Panda Cup, một giải giao hữu diễn ra tại Trung Quốc vào tháng 6/2023. Giải đấu này cũng là bước khởi động cho chiến dịch chinh phục vé dự VCK U23 châu Á 2024.\r\n\r\nU22 Việt Nam vừa qua kỳ SEA Games 32 không thực sự thành công khi chỉ giành được tấm HCĐ. Tuy nhiên qua giải đấu vừa rồi, thầy trò HLV Troussier đã có thêm những tích lũy cho giai đoạn quan trọng hơn ở phía trước.\r\n\r\nTrở về từ SEA Games, lược gia người Pháp tiếp tục bắt tay vào củng cố đội hình cho U22 Việt Nam để tham dự Panda Cup, một giải giao hữu diễn ra tại Trung Quốc vào tháng 6/2023. Giải đấu này cũng là bước khởi động cho chiến dịch chinh phục vé dự VCK U23 châu Á 2024.', 'https://onsports.vn/post/u23-viet-nam-nhan-tin-vui-sau-quyet-dinh-cua-ldbd-chau-a', 'https://stvinaprod.vtvcab.vn/46270359-a2e8-4cd1-a297-c3d7b43742e4.jpg', 1, 1, 1, 'u23-viet-nam-nhan-tin-vui-sau-quyet-dinh-cua-ldbd-chau-a', 1, 1),
(4, 'Đánh bại Myanmar thuyết phục, U22 Việt Nam giành huy chương đồng SEA Games 32', '2023-05-16', 'Dễ dàng đánh bại U22 Myanmar, U22 Việt Nam giành huy chương đồng môn bóng đá nam SEA Games 32.', 'Tạm quên đi nỗi buồn từ trận thua bán kết trước U22 Indonesia, U22 Việt Nam nhập cuộc với tâm lý thoải mái khi đối đầu với U22 Myanmar trong trận tranh HCĐ SEA Games 32.\r\n\r\nNgay ở phút thứ 9, từ một pha tấn công tốc độ và bài bản bên phía cánh trái, Hồ Văn Cường băng lên từ phía cánh phải và đón đường tạt bóng của đồng đội trong vòng cấm. Cầu thủ số 13 không chế bóng gọn gàng trước khi sút tung lưới U22 Myanmar để giúp U22 Việt Nam có lợi thế dẫn trước 1-0.\r\n\r\nSau khi có bàn thắng dẫn trước, đội bóng áo đỏ càng chơi hưng phấn hơn. Phút 33, Lê Văn Đô có cơ hội nhân đôi cách biệt cho U22 Việt Nam, tuy nhiên cú sút của anh đưa bóng đi trúng cột dọc.\r\n\r\nĐến phút 34, lại là Hồ Văn Cường điền tên lên bảng tỷ số với một pha dứt điểm hiểm hóc đánh bại thủ thành U22 Myanmar. Thầy trò HLV Troussier có lợi thế dẫn 2-0 sau khi hiệp 1 khép lại.\r\n\r\nBước sang hiệp 2, các cầu thủ U22 Việt Nam tiếp tục được duy trì được thế trận. Đội bóng áo đỏ tạo ra được thêm những cơ hội ngon ăn. Ngay ở phút 51, Khuất Văn Khang đã có cơ hội giúp U22 Việt Nam gia tăng cách biệt nhưng lại sút bóng đi nhẹ và không trúng đích. Tuy nhiên cầu thủ này cũng không phải tiếc nuối quá lâu. Đến phút 57, chính anh đã cụ thể hóa một cơ hội thành bàn thắng, nâng tỷ số trận đấu lên thành 3-0.\r\n\r\nỞ những phút còn lại, U22 Myanmar nỗ lực tìm bàn thắng danh dự. Phút 89, đội bóng áo trắng có bàn thắng rút ngắn tỷ số xuống còn 1-3. Tỷ số trên được giữ nguyên cho đến khi trọng tài nổi hồi còn mãn cuộc.\r\n\r\nVới trận thắng này, thầy trò HLV Troussier chính thức nói lời chia tay SEA Games 32 bằng tấm huy chương đồng.\r\n\r\nTỷ số trận đấu:\r\n\r\nU22 Việt Nam 3-1 U22 Myanmar.', 'https://onsports.vn/post/danh-bai-myanmar-thuyet-phuc-u22-viet-nam-gianh-huy-chuong-dong-sea-games-32', 'https://stvinaprod.vtvcab.vn/4791495d-e824-463d-a8a7-23b7a1938fde.jpg', 1, 1, 1, 'danh-bai-myanmar-thuyet-phuc-u22-viet-nam-gianh-huy-chuong-dong-sea-games-32', 1, 1),
(5, 'Đạt thành tích vô tiền khoáng hậu, ĐT nữ Việt Nam nhận thưởng nóng cực khủng', '2023-05-16', 'Đội tuyển nữ Việt Nam nhận thưởng cực khủng sau những chiến tích vô tiền khoáng hậu tại SEA Games.', 'Hai bàn thắng của Huỳnh Như và Thanh Nhã giúp ĐT nữ Việt Nam ấn định chiến thắng 2-0 trước ĐT nữ Myanmar, qua đó bảo vệ thành công tấm HCV môn bóng đá nữ tại SEA Games 32.\r\n\r\nĐây đã là lần thứ 4 liên tiếp thầy trò HLV Mai Đức Chung giành được tấm HCV SEA Games. Nếu tính xuyên suốt lịch sử giải đấu, ĐT nữ Việt Nam đã có 8 lần lên ngôi vô địch. Có thể nói, đó là một kỷ lục vô tiền khoáng hậu.\r\n\r\nHai bàn thắng của Huỳnh Như và Thanh Nhã giúp ĐT nữ Việt Nam ấn định chiến thắng 2-0 trước ĐT nữ Myanmar, qua đó bảo vệ thành công tấm HCV môn bóng đá nữ tại SEA Games 32.\r\n\r\nĐây đã là lần thứ 4 liên tiếp thầy trò HLV Mai Đức Chung giành được tấm HCV SEA Games. Nếu tính xuyên suốt lịch sử giải đấu, ĐT nữ Việt Nam đã có 8 lần lên ngôi vô địch. Có thể nói, đó là một kỷ lục vô tiền khoáng hậu.', 'https://onsports.vn/post/dat-thanh-tich-vo-tien-khoang-hau-dt-nu-viet-nam-nhan-thuong-nong-cuc-khung', 'https://stvinaprod.vtvcab.vn/4c871192-1787-44d2-8547-7d226ca32ed2.jpg', 1, 1, 1, 'dat-thanh-tich-vo-tien-khoang-hau-dt-nu-viet-nam-nhan-thuong-nong-cuc-khung', 1, 1),
(6, 'Đội nhà thua ĐT nữ Việt Nam, truyền thông Myanmar chỉ ra nguyên nhân \"chí mạng\"', '2023-05-16', 'Truyền thông Myanmar đã chỉ ra nguyên nhân khiến đội nhà thất bại 0-2 trước ĐT nữ Việt Nam trong trận tranh HCV bóng đá nữ SEA Games 32.', 'Với sự chặt chẽ nơi hàng phòng ngự cùng với hai pha lập công của Huỳnh Như (12\') và Thanh Nhã (76\'), ĐT nữ Việt Nam đã giành chiến thắng thuyết phục 2-0 trước ĐT nữ Myanmar để giành tấm HCV bóng đá nữ SEA Games 32.\r\n\r\nĐây là lần thứ 4 liên tiếp và là lần thứ 8 trong lịch sử các kỳ SEA Games, đội tuyển nữ Việt Nam giành được tấm huy chương cao quý nhất của giải đấu.\r\n\r\nNói về trận chung kết, tờ Siamport (Thái Lan) nhận định rằng hàng thủ chơi chắc chắn và kỷ luật đã góp công không nhỏ vào chiến thắng của ĐT nữ Việt Nam. Dù ĐT nữ Myanmar đã chơi rất nỗ lực và cũng tạo ra nhiều cơ hội nhưng không thể một lần chọc thủng lưới của đội bóng áo đỏ. Tờ báo này cũng đánh giá rằng chính tấm HCV của ĐT nữ đã \'giải cứu\' đội nam của Việt Nam khi để thua ở bán kết.\r\n\r\nTrong khi đó, tờ 90 Minutes Journal của Myanmar cho rằng đội nhà không hề lép vế trước Việt Nam. Nguyên nhân \"chí mạng\" khiến ĐT nữ Myanmar nhận thất bại là hàng thủ bị mất tập trung trong một số thời điểm quan trọng và các chân sút trên hàng công thiếu sắc bén. Trước đó, thầy trò HLV Tetsuro Uki đã thua 1-3 trước ĐT nữ Việt Nam tại vòng bảng.\r\n\r\nMột tờ báo khác của Myanmar là Duwun đánh giá cao sự quyết tâm của thầy trò HLV Tetsuro Uki. Tuy nhiên, sai lầm hàng thủ khiến cho ĐT nữ Myanmar phải nhận bàn thua sớm và gặp bất lợi về mặt tâm lý khi phải trong tình thế rượt đuổi.\r\n\r\nMặc dù sau đó, ĐT nữ Myanmar đã rất nỗ lực để tìm kiếm bàn gỡ hòa nhưng không thành công. Chẳng những thế, họ đã phải nhận thêm bàn thua thứ hai ở cuối hiệp 2 và chấp nhận về nhì.\r\n\r\nTrang chủ Olympics cũng dành lời tán dương ĐT Việt Nam với thành tích 4 lần liên tiếp giành HCV môn bóng đá nữ SEA Games. Trong thành công này, tiền đạo đội trưởng Huỳnh Như được đánh giá là nhân tố quan trọng nhất.\r\n\r\nNhững thành tích ấn tượng tại khu vực Đông Nam Á cũng được xem là bước chạy đà hoàn hảo của ĐT nữ Việt Nam cho World Cup nữ 2023 vào tháng 7 tới.', 'https://onsports.vn/post/doi-nha-thua-dt-nu-viet-nam-truyen-thong-myanmar-chi-ra-nguyen-nhan-chi-mang', 'https://stvinaprod.vtvcab.vn/50ed981a-a6ae-4d27-9d47-5c0d29f1b066.jpeg', 1, 1, 1, 'doi-nha-thua-dt-nu-viet-nam-truyen-thong-myanmar-chi-ra-nguyen-nhan-chi-mang', 1, 1),
(7, 'Truyền thông Thái Lan thán phục, hết lời khen ngợi thành tích của ĐT nữ Việt Nam', '2023-05-16', 'Truyền thông Thái Lan đã ngả mũ thán phục với chiến tích mà thầy trò HLV Mai Đức Chung tạo nên tại các kỳ SEA Games.', 'Xuất sắc đánh bại đội tuyển nữ Myanmar 2-0 trong trận chung kết, đội tuyển nữ Việt Nam đã giành được tấm HCV quý giá tại SEA Games 32.\r\n\r\nHai pha lập công của Huỳnh Như và Thanh Nhã đã giúp đội tuyển nữ Việt Nam vô địch SEA Games lần thứ 4 liên tiếp và cũng là lần thứ 8 vô địch trong lịch sử giải đấu..\r\n\r\nVới thành tích đặc biệt ấn tượng nói trên, thầy trò HLV Mai Đức Chung đã nhận được nhiều lời tán dương và thán phục từ truyền thông Thái Lan cũng như các nước khác trong khu vực.\r\n\r\nSau khi trận chung kết khép lại, tờ Siam Sport của Thái Lan đăng tải bài viết: \"Giải cứu cho đội nam, đội nữ Việt Nam lần thứ 4 giành HCV SEA Games.\"\r\n\r\nTờ này bình luận: \"Myanmar không thể khiến Việt Nam gặp khó khăn. Thầy trò HLV Mai Đức Chung đã thi đấu kỷ luật, hiệu quả để giành được tấm HCV SEA Games 32 một cách rất xứng đáng\".\r\n\r\nMột tờ báo khác của xứ Chùa vàng là SMM Sport thì nhấn mạnh về kỷ lục của đội tuyển nữ Việt Nam: \"Đội tuyển nữ Việt Nam đều lên ngôi vô địch trong 4 kỳ SEA Games gần nhất. Có thể nói, họ đã thực sự thống trị đấu trường SEA Games\".\r\n\r\nHãng AFP khu vực Đông Nam Á mô tả: \"Cú lốp bóng đỉnh cao của Thanh Nhã đã đặt dấu chấm hết cho mọi nỗ lực của đội tuyển nữ Myanmar. Trước đó, đội tuyển nữ Việt Nam đã sớm giành lợi thế nhờ bàn thắng của đội trưởng Huỳnh Như. Chiến thắng 2-0 là món quà không thể tuyệt vời hơn mà các cô gái của tuyển Việt Nam dành tặng cho 8.000 cổ động viên đến sân Olympic cũng như các khán giả tại quê nhà.”\r\n\r\nTấm HCV SEA Games cũng sẽ tiếp thêm sự tự tin để đội tuyển nữ Việt Nam hướng tới lần đầu tham dự kỳ World Cup vào tháng 7 tới tại Australia và New Zealand.\r\n\r\nKhu vực Đông Nam Á có hai đại diện lọt vào VCK World Cup là Việt Nam và Philippines. Trong đó, các \'nữ chiến binh sao vàng\' nằm chung bảng với Mỹ, Hà Lan và Bồ Đào Nha.', 'https://onsports.vn/post/truyen-thong-thai-lan-than-phuc-het-loi-khen-ngoi-thanh-tich-cua-dt-nu-viet-nam', 'https://stvinaprod.vtvcab.vn/e475a2dd-2ad1-49e2-bffb-c73f9cacfc7d.jpeg', 1, 1, 1, 'truyen-thong-thai-lan-than-phuc-het-loi-khen-ngoi-thanh-tich-cua-dt-nu-viet-nam', 1, 1),
(8, 'Đánh bại Myanmar, ĐT nữ Việt Nam lần thứ 4 liên tiếp giành HCV SEA Games', '2023-05-15', 'Trong trận chung kết môn bóng đá nữ SEA Games 32, ĐT nữ Việt Nam đã xuất sắc đánh bại Myanmar và lần thứ 4 liên tiếp giành HCV SEA Games.', 'Trận đấu hấp dẫn, kịch tính ngay sau tiếng còi khai cuộc. Hai đội tranh chấp liên tục và bóng được luân chuyển rất nhanh. Cơ hội đầu tiên thuộc về tuyển nữ Việt Nam khi Tuyết Dung đá phạt vào vòng cấm, Huỳnh Như ập vào dứt điểm đưa bóng rơi trên nóc lưới khung thành Myanmar.\r\n\r\nVà các CĐV Việt Nam trên sân cũng không phải chờ đợi quá lâu để ăn mừng bàn thắng. Phút thứ 12, từ đường chuyền vượt tuyến của đồng đội, Tuyết Dung thoát xuống đẩy bóng cho Huỳnh Như dễ dàng dứt điểm vào lưới trống khi thủ môn Mya Mya Nyein băng lên không kịp. Đây cũng là bàn thắng thứ 6 của Huỳnh Như ghi bàn vào lưới của Myanmar.\r\n\r\nSau khi bị vươn lên dẫn trước, các cầu thủ Myanmar dâng cao đội hình tấn công và tạo ra nhiều sóng gió trước khung thành của Kim Thanh. Trong khi đó, tuyển nữ Việt Nam thi đấu chắc chắn và có những tình huống phản công sắc sảo. Hiệp 1 kết thúc với tỉ số 1-0 nghiêng về tuyển nữ Việt Nam.\r\n\r\nBước sang hiệp 2, tuyển nữ Myanmar nỗ lực tấn công để tìm bàn gỡ. Tốc độ trận đấu khá cao, bóng luân chuyển nhanh giữa hai đội nhưng vẫn chưa có những cơ hội thực sự rõ ràng.Tuy nhiên, trong khi Myanmar vẫn chưa thể có bàn thắng thì các cô gái Việt Nam tiếp tục có bàn thắng nhân đôi cách biệt.\r\n\r\nPhút thứ 76 của trận đấu, trong một tình huống tấn công nhanh bên cánh phải, Thanh Nhã khống chế bóng trong tầm chân cầu thủ áo trắng, thoát xuống vòng cấm rồi lốp bóng rất tinh tế ghi siêu phẩm.\r\n\r\nTrận đấu kết thúc với tỉ số 2-0 nghiêng về tuyển nữ Việt Nam. Với chiến thắng này, thầy trò HLV Mai Đức Chung đã lần thứ 4 liên tiếp giành HCV SEA Games.', 'https://onsports.vn/post/danh-bai-myanmar-dt-nu-viet-nam-lan-thu-4-lien-tiep-gianh-hcv-sea-games', 'https://stvinaprod.vtvcab.vn/9c72d0b3-0519-4557-bc12-f223a1c708c7.jpg', 1, 1, 1, 'danh-bai-myanmar-dt-nu-viet-nam-lan-thu-4-lien-tiep-gianh-hcv-sea-games', 1, 1),
(9, 'Báo Trung Quốc nhắc đến thầy Park, \'mổ xẻ\' thất bại của U22 Việt Nam', '2023-05-15', 'Truyền thông Trung Quốc đã có những phân tích về thất bại của U22 Việt Nam trước Indonesia tại bán kết SEA Games 32.', 'Trang Dongqiudi của Trung Quốc cho rằng HLV Troussier chính là nguyên nhân khiến U22 Việt Nam thất bại. So với HLV tiền nhiệm Park Hang Sao, vị chiến lược gia được mệnh danh là \"Phù thủy trắng\" lại không tạo được bản sắc trong lối đá của các cầu thủ Việt Nam.\r\n\r\n\"Dù chơi hơn người, U22 Việt Nam của HLV Troussier vẫn bị U22 Indonesia đánh bại vào phút cuối. Kể từ khi bóng đá Việt Nam trỗi dậy mạnh mẽ, đây cũng là lần đầu tiên họ không thể vào đến chung kết một giải đấu trong khu vực.\r\n\r\nNói về thất bại, HLV Troussier rằng đội bóng của ông đã thiếu may mắn khi phải nhận bàn thua, bên cạnh đó việc các cầu thủ Indonesia phòng ngự một cách thô bạo đã làm ảnh hưởng đến nhịp độ trong lối chơi của U22 Việt Nam. Tuy nhiên, thực tế là HLV trưởng của U22 Việt Nam phải chịu trách nhiệm cho trận thua với những chỉ đạo chiến thuật của mình.\r\n\r\nHLV Troussier vẫn chưa có những điều chỉnh hiệu quả khi đối mặt với lối chơi quyết liệt của đối thủ. Nghĩa là, khả năng phản ứng với thế trận là chưa tốt.\r\n\r\nNgoài ra, so với một U22 Việt Nam kỷ luật, phòng ngự chặt chẽ và phản công sắc bén dưới thời HLV Park Hang Seo, đội tuyển hiện tại gần như đã mất đi các bản sắc đó.\r\n\r\nMay mắn là hiện tại các đội tuyển của Trung Quốc sẽ không phải đối mặt với Việt Nam được dẫn dắt bởi HLV Park Hang Seo nữa\" – Dongqiudi bình luận.\r\n\r\nNhững thất bại AFF Cup với ĐTQG và mới đây là U22 tại SEA Games cũng giúp người hâm mộ thừa nhận thực tế rằng: Bóng đá Việt Nam không mạnh đến mức có thể dễ dàng thâu tóm các danh hiệu vô địch trong khu vực Đông Nam Á.\r\n\r\nĐể vươn tầm châu lục, nền bóng đá của chúng ta còn quá nhiều thứ phải làm. Chẳng nói đâu xa, trong khi SEA Games đang diễn ra, Thái Lan đã có thêm 3 tuyển thủ gồm Weerathep, Jaroenask và Ekanit gia nhập J-League. Trước đó, Chanathip và Supachok cũng đã đầu quân cho các CLB J-League và hiện vẫn đang thi đấu ấn tượng tại Nhật Bản, môi trường bóng đá hàng đầu châu lục.\r\n\r\nCòn với Việt Nam, chưa có một cầu thủ bóng đá nam nào thành công khi xuất ngoại. Hiện tại, những ngôi sao như Công Phượng, Quang Hải còn đang chật vật chỉ để có được một cơ hội ra sân.', 'https://onsports.vn/post/bao-trung-quoc-nhac-den-thay-park-mo-xe-that-bai-cua-u22-viet-nam', 'https://stvinaprod.vtvcab.vn/b04d8289-e1f4-40bf-9b9f-283a202c3577.jpeg', 1, 1, 1, 'bao-trung-quoc-nhac-den-thay-park-mo-xe-that-bai-cua-u22-viet-nam', 1, 1),
(10, 'Tiền đạo Tuyết Dung muốn đánh bại Myanmar để làm động lực cho U22 Việt Nam', '2023-05-14', 'Gặp lại \"bại tướng\" Myanmar trong trận chung kết, đội tuyển nữ Việt Nam đang tràn đầy sự tự tin sẽ có lần thứ 4 liên tiếp giành tấm huy chương vàng môn bóng đá nữ SEA Games.', 'Trận thua của U22 Việt Nam trước Indonesia ở trận bán kết vẫn khiến nhiều người, trong đó có cả các thành viên của đội tuyển nữ Việt Nam cảm thấy tiếc nuối. Chia sẻ về những người đàn em, tiền đạo Tuyết Dung cho biết: \"Cả đội cũng có theo dõi trận đấu của U22 Việt Nam ngày hôm qua và cảm thấy đáng tiếc khi các bạn ấy đã không thể giành chiến thắng. Tuy nhiên, các bạn cũng cần nhanh chóng sốc lại tinh thần để chuẩn bị cho trận tranh huy chương đồng. \r\nĐồng thời, đó cũng là động lực để Tuyết Dung cùng đồng đội thi đấu thật tốt trận đấu ngày mai để từ đó sốc lại tinh thần cho các em ở đội U22 vì chỉ một ngày sau trận chung kết giữa nữ Việt Nam và nữ Myanmar là trận tranh huy chương đồng của U22 cũng với đối thủ Myanmar\".\r\n\r\nỞ vòng bảng, đội tuyển nữ Việt Nam đã giành chiến thắng với tỷ số 3-1 trước Myanmar bằng các pha lập công của Huỳnh Như, Thanh Nhã và Thùy Trang. Đó cũng là trận đấu mang tính bản lề để thầy trò HLV Mai Đức Chung tiến vào bán kết SEA Games năm nay.\r\n\r\nNhưng nhìn nhận một cách khách quan, đó là trận đấu mà Myanmar đã gây rất nhiều khó khăn cho đội tuyển nữ Việt Nam, đặc biệt là trong hiệp 1 trận đấu. Nhận xét về màn tái đấu sắp tới, tiền đạo Tuyết Dung chia sẻ: \"Trước giờ Myanmar luôn là đối thủ khó chịu với đội tuyển nữ Việt Nam. Bởi vậy, toàn đội không được phép chủ quan khi gặp lại họ. Điểm mạnh của Myanmar là thể lực, khả năng đeo bám tốt suốt 90 phút. Đó là điều tuyển Việt Nam sẽ phải hóa giải khi chạm chán Myanmar vào ngày mai\".\r\n\r\nKể từ đầu giải, Tuyết Dung luôn là sự lựa chọn hàng đầu bên hành lang cánh trái của đội tuyển nữ Việt Nam. Cầu thủ của Phong phú Hà Nam đóng vai trò quan trọng trong cách vận hành lối chơi mà HLV Mai Đức Chung xây dựng, bên cạnh đội trưởng Huỳnh Như. Dù vậy, cầu thủ sinh năm 1993 vẫn chưa có cho mình bàn thắng ở giải đấu năm nay. Chia sẻ trước trận chung kết, Tuyết Dung cho biết: \"Hiện tại, tinh thần của toàn đội đang rất thoải mái để hướng tới việc bảo vệ thành công tấm huy chương vàng lần thứ 4 liên tiếp. Với cá nhân mình, tôi cũng sẽ cố gắng để ghi bàn trong trận chung kết gặp Myanmar sắp tới\".\r\n\r\nTrận chung kết môn bóng đá nữ SEA Games 32 giữa đội tuyển nữ Việt Nam và Myanmar sẽ diễn ra vào lúc 19h ngày 15/04 trên SVĐ Olympic (Phnom Penh, Campuchia).', 'https://onsports.vn/post/tien-dao-tuyet-dung-muon-danh-bai-myanmar-de-lam-dong-luc-cho-u22-viet-nam', 'https://stvinaprod.vtvcab.vn/59144795-1473-4864-bbd7-06b18ca35719.jpg', 1, 1, 1, 'tien-dao-tuyet-dung-muon-danh-bai-myanmar-de-lam-dong-luc-cho-u22-viet-nam', 1, 1),
(11, 'Truyền thông Đông Nam Á bất ngờ trước trận thua của U22 Việt Nam', '2023-05-14', 'Các tờ báo Đông Nam Á tỏ ra bất ngờ khi chứng kiến U22 Việt Nam để thua U22 Indonesia trong thế chơi hơn người suốt 30 phút.', 'U22 Việt Nam đã vỡ mộng bảo vệ HCV SEA Games sau khi để thua 2-3 trước U22 Indonesia ở trận bán kết. Nhiều tờ báo trong khu vực đã tỏ ra bất ngờ với kết quả trên.\r\n\r\nTờ Makan Bola của Malaysia giật tít: “U22 Việt Nam bất ngờ thua trước U22 Indonesia dù có lợi thế hơn người.”\r\n\r\nTờ Daily News của Thái Lan viết: “Bàn thắng muộn của Indonesia khiến các chiến binh sao vàng gục ngã.”\r\n\r\nTrong khi đó Siam Sport đăng bài viết với tiêu đề: “U22 Việt Nam đổ lệ khi trở thành cựu vương. U22 Indonesia chiến đấu quả cảm chỉ với 10 người và giành quyền vào chung kết.”\r\n\r\nMatichon, một tờ báo khác của xứ Chùa vàng, có bài viết: “U22 Việt Nam đau đớn nhận bàn thua ở phút bù giờ, chính thức trở thành nhà cựu vô địch.”\r\n\r\nVề phía cộng đồng mạng, rất nhiều ý kiến chỉ trích của chính các cổ động viên Việt Nam đã xuất hiện sau khi chứng kiến đội nhà thua trong thế chơi hơn người.\r\n\r\nMột cổ động viên bày tỏ \'nỗi nhớ\' HLV Park Hang-seo: \"Khi ông Park còn dẫn dắt, chúng ta chơi phòng ngự tốt hơn thế này rất nhiều lần. Chắc là chỉ có Thái Lan mới đủ sức xuyên phá hàng thủ của Việt Nam.\"\r\n\r\nMột cổ động viên khác bức xúc: \"Nhìn chung các cầu thủ U22 Việt Nam còn quá non nớt nên mới để thua theo cách này.\"\r\n\r\nTuy nhiên cũng có người tỏ ra lạc quan: \"Màn trình diễn hiện tại cũng được coi là khởi đầu ổn với Troussier, dù sao ông cũng mới chỉ dẫn dắt đội thời gian gắn, hy vọng thành công sẽ đến trong tương lai.\"\r\n\r\nTrên thực tế, lứa U22 Việt Nam hiện tại còn rất trẻ và nhiều thời gian để phát triển. Thất bại vừa rồi cũng là một bài học đắt giá nhưng cần thiết để các cầu thủ trưởng thành hơn ở các giải đấu về sau.\r\n\r\nTrước khi nói lời chia tay với đất nước Campuchia, U22 Việt Nam sẽ còn một trận đấu để tranh HCĐ với U22 Myanmar vào lúc 16h30 ngày 16/5.', 'https://onsports.vn/post/truyen-thong-dong-nam-a-bat-ngo-truoc-tran-thua-cua-u22-viet-nam', 'https://stvinaprod.vtvcab.vn/8519cb1d-e14f-4fa6-9c76-debd8a4e70c6.jpg', 1, 1, 1, 'truyen-thong-dong-nam-a-bat-ngo-truoc-tran-thua-cua-u22-viet-nam', 1, 1),
(12, 'Báo Indonesia chê thủ môn Quan Văn Chuẩn của U22 Việt Nam', '2023-05-14', 'Bên cạnh những lời khen dành cho đội nhà, truyền thông Indonesia cũng chỉ ra những điểm yếu của U22 Việt Nam giúp đội bóng xứ Vạn đảo giành thắng lợi.', 'Trận bán kết SEA Games 32 giữa U22 Indonesia và U22 Việt Nam diễn ra với một kịch bản ít ai ngờ tới. Đội bóng xứ Vạn đảo phải nhận 1 thẻ đỏ ở phút 60, sau đó bị U22 Việt Nam gỡ hòa 2-2 nhưng vẫn ghi được bàn thắng ở phút bù giờ giành chiến thắng 3-2 chung cuộc.\r\n\r\nChiến thắng giúp U22 Indonesia lọt vào trận chung kết và đứng trước cơ hội giành HCV bóng đá nam SEA Games sau hơn 30 năm. Truyền thông xứ Vạn đảo không ngớt lời khen ngợi đội nhà sau màn thể hiện ở trận bán kết.\r\n\r\nTờ Bola giật tít: “Kiên nhẫn là chìa khóa giúp U22 Indonesia đánh bại U22 Việt Nam”.\r\n\r\nTrong bài viết trích dẫn lại lời thủ môn Ernando Ari sau trận: “Thật may mắn vì chúng tôi có những cầu thủ kinh nghiệm đang thi đấu cho ĐTQG. Sau khi phải chơi thiếu người, chúng tôi đã cố gắng phòng thủ và tuân theo chỉ đạo của HLV trưởng. Quả nhiên đối thủ đã lộ ra điểm yếu khi quá mải mê tấn công.\"\r\n\r\nBên cạnh đó, Bola cũng dành sự nể phục cho tài năng của HLV Indra Sjafri: “Đây là lần thứ hai HLV Sjafri đưa chúng ta đến trận tranh HCV SEA Games. Ông ấy thực sự rất giỏi nhìn người. Điều đó đã được chứng minh từ lứa cầu thủ của Evan Dimas. Còn Taufany, cầu thủ ghi bàn kết liễu U22 Việt Nam cũng mới chỉ có 8 lần ra sân ở giải VĐQG Indonesia mùa trước”.\r\n\r\nTrong khi đó, tờ CNN Indonesia chỉ ra điểm yếu của U22 Việt Nam giúp đội bóng xứ vạn đảo khai thác: \"Thủ môn U22 Việt Nam chơi tệ giúp U22 Indonesia vào chung kết\".\r\n\r\nBài viết nhận định: \"Trong 3 bàn thắng mà U22 Indonesia ghi được, thủ môn Quan Văn Chuẩn ghi dấu ấn đến 2 bàn. Những pha bóng tệ, chọn sai vị trí của Quan Văn Chuẩn khiến đội bóng của anh thất bại trước U22 Indonesia\".\r\n\r\nTờ Suara thì dẫn lại lời phát biểu đầy tự hào của Chủ tịch Liên đoàn bóng đá Indonesia, Erick Thohir. Tỷ phú này chia sẻ: \"Tôi luôn nói rằng bóng đá Indonesia đi tới ngày hôm nay là nhờ sự can đảm. Dù phải chơi ít người, chúng ta vẫn chiến đấu hết mình để giành chiến thắng thuyết phục. Đó là bản lĩnh của bóng đá Indonesia\".\r\n\r\nTờ Bola tiếp tục có bài viết với tiêu đề: \"Sau chiến tích thần kỳ, HLV Indra Sjafri sẽ giúp Indonesia làm nên lịch sử?\". Tờ báo này viết: \"U22 Indonesia đã trải qua một trận bán kết nhọc nhằn, nhưng nhờ vậy mà đội bóng chứng tỏ được bản lĩnh tuyệt vời.\"\r\n\r\nU22 Indonesia sẽ đụng độ với U22 Thái Lan trong trận tranh HCV SEA Games 32 vào lúc 19h30 ngày 16/5 .', 'https://onsports.vn/post/bao-indonesia-che-thu-mon-quan-van-chuan-cua-u22-viet-nam', 'https://stvinaprod.vtvcab.vn/da5b1bb9-2a4e-420e-97b6-78d2ffe59bf8.jpg', 1, 1, 1, 'bao-indonesia-che-thu-mon-quan-van-chuan-cua-u22-viet-nam', 1, 1),
(13, 'Thua cay đắng U22 Indonesia, U22 Việt Nam vỡ mộng bảo vệ HCV tại SEA Games 32', '2023-05-13', 'Được chơi hơn người trong khoảng 40 phút cuối trận, U22 Việt Nam vẫn để thua cay đắng trước U22 Indonesia, qua đó vỡ mộng bảo vệ HCV bóng đá nam SEA Games.', 'Lúc 16h00 chiều nay (13/5), trên SVĐ Olympic đã diễn ra trận bán kết 1 môn bóng đá nam SEA Games 32 giữa U22 Indonesia và U22 Việt Nam. Với một trận đấu có tính chất vô cùng quan trọng như trận bán kết, cả hai đội bóng đều nhập cuộc với tâm thế thận trọng. Tuy nhiên, U22 Indonesia bất ngờ có bàn thắng sớm nhờ tận dụng tốt tình huống cố định.\r\n\r\nPhút thứ 10, Pratama Arhan thực hiện pha ném biên cực mạnh để đưa bóng vào thẳng vòng cấm của U22 Việt Nam. Trung vệ Komang Trisnanda bật cao đánh đầu cận thành làm tung lưới thủ môn Quan Văn Chuẩn, qua đó mở tỷ số trận đấu. \r\n\r\nCó được bàn thắng dẫn trước từ sớm, đội bóng \'xứ Vạn đảo\' chơi chắc chắn và rình rập chờ cơ hội. Chiến thuật hợp lý của đối thủ tạo ra rất nhiều khó khăn cho U22 Việt Nam. Mặc dù vậy, các học trò của HLV Troussier vẫn thi đấu vô cùng nỗ lực, miệt mài tấn công. Cuối cùng những nỗ lực ấy đã được đền đáp ngay trong hiệp 1.\r\n\r\nPhút 36, đón đường treo bóng của Đức Tú, Văn Tùng thực hiện một pha đánh đầu ngược hiểm hóc, đánh bại thủ thành của Indonesia để gỡ hòa 1-1 cho U22 Việt Nam.\r\n\r\nTrước khi hiệp 1 khép lại, diễn biến trên sân trở nên cực kỳ căng thẳng khi cầu thủ hai đội xảy ra xô xát. Trận đấu bị cắt vụn và không có thêm cơ hội đáng kể nào được tạo ra. Hai bên bước vào đường hầm nghỉ giữa hiệp với tỷ số 1-1.\r\n\r\nBước sang hiệp 2, U22 Indonesia ngay lập tức đẩy cao tốc độ trận đấu nhằm tìm kiếm bàn thắng. Phút 55, Pratama Arhan tiếp tục thực hiện một pha ném biên cực mạnh, buộc thủ môn Quan Văn Chuẩn phải đấm bóng ra ngoài. Bóng đến chân của một cầu thủ U22 Indonesia ngoài vòng cấm, cầu thủ này dứt điểm bóng chìm, bóng trúng chân một cầu thủ áo đỏ khác đổi hướng, khiến thủ môn của U22 Việt Nam bó tay. Tỷ số được nâng lên thành 2-1 cho U22 Indonesia.\r\n\r\nNhững phút sau đó, nỗ lực tấn công của U22 Việt Nam khiến đối thủ mắc sai lầm. Phút 60, U22 Việt Nam có một pha lên bóng tốc độ cao khiến Pratama buộc phải phạm lỗi với Đức Phú ngay sát vòng cấm. Số 12 của Indonesia phải nhận thẻ vàng thứ 2 rời sân.\r\n\r\nVới lợi thế chơi hơn người, các chiến binh sao vàng tấn công dồn dập và điều gì đến cũng phải đến. Phút 78, U22 Việt Nam gỡ hòa 2-2 sau một tình huống lóng ngóng phá bóng về lưới nhà của hậu vệ U22 Indonesia.\r\n\r\nU22 Việt Nam có lợi thế cực lớn và tiếp tục ép sân để sớm giải quyết trận đấu ngay trong thời gian thi đấu chính thức. Tuy nhiên, bất ngờ đã xảy ra ở phút bù giờ thứ 6, tiền vệ Taufany chớp thời cơ ghi bàn nâng tỷ số lên 3-2 cho Indonesia.\r\n\r\nThời gian còn lại quá ít ỏi để U22 Việt Nam tìm kiếm bàn gỡ. Cuối cùng, thầy trò HLV Troussier chấp nhận thất bại 2-3, qua đó chính thức vỡ mộng bảo vệ HCV bóng đá nam tại SEA Games 32.\r\n\r\nTỷ số trận đấu:\r\n\r\nU22 Indonesia 3-2 U22 Việt Nam.', 'https://onsports.vn/post/thua-cay-dang-u22-indonesia-u22-viet-nam-vo-mong-bao-ve-hcv-tai-sea-games-32', 'https://stvinaprod.vtvcab.vn/bf7126ab-894e-418c-b125-755ebcb51025.jpg', 1, 1, 1, 'thua-cay-dang-u22-indonesia-u22-viet-nam-vo-mong-bao-ve-hcv-tai-sea-games-32', 1, 1),
(14, 'Giành quyền vào chung kết SEA Games, ĐT nữ Việt Nam nhận món quà cực lớn', '2023-05-13', 'ĐT nữ Việt Nam đã được Liên đoàn Bóng đá Việt Nam thưởng nóng 500 triệu đồng ngay sau khi đánh bại chủ nhà Campuchia để giành vé vào chung kết bóng đá nữ SEA Games 32.', 'ĐT nữ Việt Nam đụng độ với ĐT nữ của chủ nhà Campuchia trong trận bán kết bóng đá nữ SEA Games 32 vào tối 12/5. Trong khoảng 10 phút đầu, đội tuyển chủ nhà thi đấu rất tự tin dưới sự cổ vũ của đông đảo khán giả trên sân. Tuy nhiên, các cô gái Việt Nam sau đó vẫn cho thấy được sự bình tĩnh và đẳng cấp vượt trội để hạ gục ĐT nữ Campuchia với tỷ số đậm 4-0.\r\n\r\nGiành chiến thắng trong trận bán kết, thầy trò HLV Mai Đức Chung giành vé vào chung kết và sẽ tái đấu với ĐT nữ Myanmar trong trận tranh huy chương vàng.\r\n\r\nSau khi trọng tài nổi hồi còi mãn cuộc trận bán kết giữa ĐT nữ Việt Nam và ĐT nữ Campuchia, ông Trần Quốc Tuấn, Chủ tịch Liên đoàn Bóng đá Việt Nam, đã xuống sân chúc mừng các cô gái áo đỏ. Tại đây, ông cũng thông báo rằng, Thường trực BCH LĐBĐVN thưởng 500 triệu đồng cho toàn đội với thành tích lọt vào trận chung kết bóng đá nữ SEA Games 32.\r\n\r\nBên cạnh đó, Chủ tịch Trần Quốc Tuấn cũng động viên thầy trò HLV Mai Đức Chung và căn dặn toàn đội giữ sức khỏe, chuẩn bị thật kỹ cho trận chung kết.\r\n\r\nTrước đó, ĐT nữ Việt Nam và ĐT nữ Myanmar đã từng chạm trán nhau tại vòng bảng. Ở trận đấu đó, các \'nữ chiến binh sao vàng\' không mấy khó khăn để đánh bại đối thủ 3-1. Dù vậy, thầy trò HLV Mai Đức Chung không được phép chủ quan. Bởi chắc chắn trong trận chung kết, các đội bóng sẽ nhập cuộc với tâm thế rất khác. Ngoài ra, ĐT nữ Myanmar cũng đang có sự tự tin bởi trong trận bán kết 2, họ đã lội ngược dòng 4-2 trước ĐT nữ Thái Lan sau khi bị đối thủ dẫn trước 2-0.\r\n\r\nTheo lịch, trận chung kết môn bóng đá nữa SEA Games 32 giữa Việt Nam và Myanmar sẽ diễn ra vào 19 giờ ngày 15/5/2023.', 'https://onsports.vn/post/gianh-quyen-vao-chung-ket-sea-games-dt-nu-viet-nam-nhan-mon-qua-cuc-lon', 'https://stvinaprod.vtvcab.vn/9e2c885d-c90c-4b8f-9966-d7d6366334ca.jpg', 1, 1, 1, 'gianh-quyen-vao-chung-ket-sea-games-dt-nu-viet-nam-nhan-mon-qua-cuc-lon', 1, 1),
(15, 'Truyền thông Indonesia lo ngại khi phải gặp Việt Nam ở bán kết SEA Games 32', '2023-05-12', 'Trước thềm bán kết bóng đá nam SEA Games 32, truyền thông Indonesia đã đưa ra thống kê và thừa nhận tuyển Việt Nam là \'khắc tinh\' của đội nhà tại các kỳ Đại hội thể thao Đông Nam Á.', 'Vòng bảng môn bóng đá nam SEA Games 32 đã chính thức khép lại sau tối qua (11/5). Trận hòa 1-1 giúp U22 Thái Lan vào bán kết với tư cách nhất bảng, trong khi đó U22 Việt Nam nhì bảng. Điều đó có nghĩa là U22 Thái Lan sẽ đối đầu với U22 Myanmar tại bán kết, trong khi đó U22 Việt Nam sẽ chạm trán U22 Indonesia. Hai trận bán kết sẽ diễn ra vào ngày 13/5.\r\n\r\nTrước thềm bán kết 1 giữa U22 Việt Nam và U22 Indonesia, trang tin Bola (Indonesia) đã thừa nhận đội tuyển Việt Nam là khắc tinh của Indonesia sau khi thống kê lại thành tích hai đội tại các kỳ SEA Games gần đây.', 'https://onsports.vn/post/truyen-thong-indonesia-lo-ngai-khi-phai-gap-viet-nam-o-ban-ket-sea-games-32', 'https://stvinaprod.vtvcab.vn/085422e8-72ad-47ad-952f-8cd69db87e27.png', 1, 1, 1, 'truyen-thong-indonesia-lo-ngai-khi-phai-gap-viet-nam-o-ban-ket-sea-games-32', 1, 1),
(16, 'HLV tuyển Nhật Bản lo ngại khi chung bảng với Việt Nam tại Asian Cup', '2023-05-12', 'Sau khi có kết quả bốc thăm Asian Cup 2023, HLV của tuyển Nhật Bản đặc biệt nhắc tới ông Philippe Troussier, người đang dẫn dắt tuyển Việt Nam.', 'Chiều 11/5 tại Qatar đã diễn ra buổi lễ bốc thăm chia bảng Asian Cup 2023. Tuyển Việt Nam cùng với Indonesia, Iraq và Nhật Bản nằm chung bảng D. Sau lễ bốc thăm, HLV của tuyển Nhật Bản, Hajime Moriyasu có phát biểu đáng chú ý về ĐT Việt Nam: “Tôi nghĩ rằng tuyển Nhật Bản sẽ gặp nhiều khó khăn tại bảng D. HLV trưởng của tuyển Việt Nam, ông Troussier là một người rất am hiểu bóng đá Nhật Bản”.\r\n\r\n“Để đánh bại được HLV Troussier cũng như các đội bóng khác tại Asian Cup, chúng tôi sẽ phải có sự chuẩn bị thật kỹ”, HLV của tuyển Nhật Bản nhấn mạnh.\r\n\r\nHLV Troussier từng là HLV trưởng của ĐT Nhật Bản trong giai đoạn 1998-2002. Nhà cầm quân người Pháp đã giúp các \"samurai xanh\" vô địch Asian Cup 2000 và vào tới vòng 1/8 World Cup 2002.\r\n\r\nMặc dù mốc thời gian đó cách hiện tại khá xa, tuy nhiên HLV người Pháp vẫn luôn dành sự quan tâm cho tuyển Nhật Bản. Thậm chí tại World Cup 2022 mới đây, ông Troussier vẫn bình luận trên truyền thông của Nhật Bản về màn trình diễn của thầy trò HLV Hajime Moriyasu.\r\n\r\nTrở lại với Asian Cup 2023, HLV Hajime Moriyasu thẳng thắn bày tỏ mục tiêu của tuyển Nhật Bản: “Chúng tôi đặt mục tiêu vô địch giải đấu. Tôi rất vui khi được cùng tuyển Nhật Bản trở lại Qatar để tham dự giải đấu hàng đầu châu lục”.\r\n\r\nHLV trưởng của ĐT Nhật Bản cũng cho biết những khó khăn mà đội bóng của ông sẽ gặp phải trong quá trình chuẩn bị cho Asian Cup. Cụ thể, giải đấu sẽ diễn ra vào tháng 1/2024, khi đó các cầu thủ đá giải quốc nội đang nghỉ ngơi, còn các cầu thủ đang thi đấu tại châu Âu lại đang ở giữa mùa giải. Chính vì vậy, nhiều khả năng tuyển Nhật Bản sẽ bị ảnh hưởng về mặt lực lượng.\r\n\r\nĐây là kỳ Asian Cup thứ hai mà ông Hajime Moriyasu dẫn dắt tuyển Nhật Bản. Trước đó, nhà cầm quân 54 tuổi cùng với tuyển Nhật Bản đã để thua ở chung kết với tỉ số 1-3 trước Qatar tại Asian Cup 2019.', 'https://onsports.vn/post/hlv-tuyen-nhat-ban-lo-ngai-khi-chung-bang-voi-viet-nam-tai-asian-cup', 'https://imgvlive.vtvcab.vn/416x234/8d08121d-df97-4da0-b4ae-49563bd4d4f8.jpg?auto=format&fit=max&w=384', 1, 1, 1, 'hlv-tuyen-nhat-ban-lo-ngai-khi-chung-bang-voi-viet-nam-tai-asian-cup', 1, 1),
(17, 'Báo Thái Lan nhận định U22 Việt Nam chơi tốt hơn đội nhà', '2023-05-12', 'Dù không thể đánh bại U22 Thái Lan, thầy trò HLV Troussier vẫn được báo chí Đông Nam Á đánh giá rất cao về màn trình diễn trong trận đấu vừa rồi.', 'Truyền thông Thái Lan tỏ ra tiếc nuối khi U22 Thái Lan không giành được trọn vẹn điểm số ở SEA Games 32. Sau trận hòa 1-1 trước U22 Việt Nam, tờ Siam Sport giật tít: “Bị U22 Việt Nam cầm hòa, U22 Thái Lan vẫn đứng nhất bảng B và gặp U22 Myanmar tại bán kết”.\r\n\r\nTờ Siam Sport nhận định, dù U22 Việt Nam bị thủng lưới từ sớm nhưng tổng thể đã chơi tốt hơn so với thầy trò HLV Issara Sritaro trong hiệp 2. Những pha hãm thành nguy hiểm của đội bóng áo đỏ được cụ thể hóa bằng bàn gỡ 1-1 của Nhật Nam. Siam Sport cũng không quên đề cập đến pha sút phạt nguy hiểm Văn Khang ở phút 74 khiến thủ môn Thirawooth phải bay người hết cỡ để cứu thua cho U22 Thái Lan.\r\n\r\nBên cạnh đó, tờ Daily News cũng tỏ ra thất vọng khi đăng tải bài viết: “U22 Thái Lan không vượt trội hơn U22 Việt Nam, bị cầm hòa 1-1 và sẽ gặp U22 Myanmar ở bán kết”.\r\n\r\nTờ Sport Detik của Indonesia thì bình luận: “U22 Việt Nam sẽ gặp U22 Indonesia ở bán kết. Đây sẽ là cơ hội để chúng ta trả món nợ thua 0-3 tại chung kết SEA Games 2019 cũng như trận thua 0-3 tại vòng bảng SEA Games 2022”.\r\n\r\nMột tờ báo khác của quốc gia Xứ vạn đảo là Bola Okezone đánh giá: “Sau khi ghi bàn gỡ hòa 1-1, U22 Việt Nam đã tạo ra sức ép cực lớn trước U22 Thái Lan”.\r\n\r\nBáo chí Malaysia cũng dành sự chú ý cho trận đấu của U22 Việt Nam. Makan Bola mô tả về trận đấu: “Bị dẫn trước từ rất sớm nhưng các học trò của HLV Troussier vẫn thi đấu cực hay, đặc biệt là trong hiệp 2. Thành quả đến với họ là bàn gỡ hòa ở phút 54. Sau đó, đội bóng áo đỏ tiếp tục tấn công dồn dập vì họ cần một chiến thắng để giành ngôi nhất bảng. Tuy vậy, U22 Việt Nam vẫn không thể chọc thủng lưới U22 Thái Lan thêm một lần nữa và đành chấp nhận hòa 1-1”.\r\n\r\nTheo lịch thi đấu, 2 trận bán kết SEA Games sẽ diễn ra trên sân Olympic vào ngày 13/5. Sau đó 3 ngày sẽ diễn ra trận chung kết.', 'https://onsports.vn/post/bao-thai-lan-nhan-dinh-u22-viet-nam-choi-tot-hon-doi-nha', 'https://imgvlive.vtvcab.vn/416x234/e5a7ec41-18e6-422d-a33c-8af960005dc3.png?auto=format&fit=max&w=384', 1, 1, 1, 'bao-thai-lan-nhan-dinh-u22-viet-nam-choi-tot-hon-doi-nha', 1, 1),
(18, 'Hòa Thái Lan, U22 Việt Nam chạm trán Indonesia tại bán kết SEA Games 32', '2023-05-11', 'Hòa 1-1 U22 Thái Lan, U22 Việt Nam chấp nhận vị trí nhì bảng B và sẽ đối đầu U22 Indonesia tại bán kết SEA Games 32.', 'Các cổ động viên Việt Nam nhuộm đỏ khán đài sân Prince Stadium và tạo ra một bầu không khí thực sự sôi động. Điều đó đã tiếp thêm tinh thần và sự tự tin cho các cầu thủ U22 Việt Nam. Tuy nhiên, đội bóng áo đỏ bất ngờ phải nhận bàn thua từ rất sớm.\r\n\r\nNgay ở phút thứ 3, một pha bóng tưởng chừng như không có gì đặc biệt, trung vệ đội trưởng Lương Duy Cương bất ngờ bị trượt chân. Tiền đạo Achitpol của U22 Thái Lan nhanh chóng chớp thời cơ và ghi bàn mở tỷ số.\r\n\r\nSau khi bị thủng lưới, U22 Việt Nam nhanh chóng triển khai tấn công và tạo ra nhiều cơ hội, tuy nhiên lại không thể cụ thể hóa thành bàn thắng. Cơ hội ngon ăn nhất đến ở phút 24, Khuất Văn Khang đón đường tạt bóng từ đồng đội nhưng cú đánh đầu của anh lại đưa bóng đi không trúng đích.\r\n\r\nỞ nửa sau của hiệp 1, Thái Lan với lợi thế dẫn bàn đã không mạo hiểm dâng cao đội hình. Họ chơi phòng ngự chặt chẽ khiến cho U22 Việt Nam gặp nhiều khó khăn trong việc tiếp cận khung thành của Thirawoot Sraunson. Tốc độ trận đấu cũng bị giảm xuống đáng kể và không có nhiều tình huống đáng chú ý được tạo ra. Hiệp 1 kết thúc với lợi thế dẫn 1-0 dành cho U22 Thái Lan.\r\n\r\nBước sang hiệp 2, huấn luyện viên Troussier ngay lập tức có những điều chỉnh nhân sự. U22 Việt Nam không giấu ý đồ tìm kiếm bàn gỡ. Tốc độ trận đấu lại được đẩy lên cao với liên tiếp các tình huống uy hiếp khung thành U22 Thái Lan. Đến phút 54, xuất phát từ một pha đá phạt, Nhật Nam có mặt đúng lúc đúng chỗ trong vòng cấm để sút tung lưới đối phương, đem về bàn thắng gỡ hòa 1-1.\r\n\r\nSau bàn gỡ hòa, tinh thần của U22 Việt Nam lên cao. Các cầu thủ áo đỏ tiếp tục thực hiện nhiều đường lên bóng nguy hiểm. Phút 74, Khuất Văn Khang thực hiện một pha sút phạt đẳng cấp, nhưng thủ thành bên phía U22 Thái Lan cũng chơi xuất sắc. Trận đấu sau đó tiếp tục diễn ra với tốc độ cao và nhiều pha bóng quyết liệt. U22 Việt Nam vẫn chơi kiên trì và miệt mài tìm bàn thắng nhưng vẫn không thể lần thứ 2 làm rung mảnh lưới của đối phương.\r\n\r\nCuối cùng, trận đấu đã khép lại với kết quả hòa 1-1. Với trận hòa này, U22 Việt Nam và U22 Thái Lan có cùng điểm số. Tuy nhiên \'Voi chiến\' vẫn giữ được vị trí đầu bảng nhờ hơn hiệu số bàn thắng bại.\r\n\r\nCán đích bảng B với tư cách nhì bảng, U22 Việt Nam sẽ đụng độ với U22 Indonesia tại bán kết. Trong khi đó, Thái Lan sẽ chạm trán U22 Myanmar ở trận bán kết còn lại. Cả hai trận bán kết sẽ diễn ra vào ngày 13/5 tới.', 'https://onsports.vn/post/hoa-thai-lan-u22-viet-nam-cham-tran-indonesia-tai-ban-ket-sea-games-32', 'https://stvinaprod.vtvcab.vn/88a9616b-2d49-4bb7-b1d4-f41a1c33aad6.jpg', 1, 1, 1, 'hoa-thai-lan-u22-viet-nam-cham-tran-indonesia-tai-ban-ket-sea-games-32', 1, 1),
(20, 'HLV Mai Đức Chung: \"Lúc này chúng tôi đánh giá Campuchia như Thái Lan, Philippines\"', '2023-05-11', 'Việc đụng độ chủ nhà Campuchia ở trận bán kết được xem là tương đối dễ thở giành cho đội tuyển nữ Việt Nam. Tuy nhiên, trong buổi họp báo trước trận đấu, HLV trưởng Mai Đức Chung không cho rằng như vậy.', 'Thầy trò HLV Mai Đức Chung vẫn đang duy trì phong độ ấn tượng tại SEA Games 32 khi giành quyền vào bán kết với ngôi nhất bảng A. Ở trận bán kết, các cô gái Việt Nam sẽ đụng độ đội chủ nhà Campuchia. Dù từng thắng đậm đối thủ này cách đây 4 năm song HLV Mai Đức Chung vẫn không hề chủ quan.\r\n\r\nThầy trò HLV Mai Đức Chung vẫn đang duy trì phong độ ấn tượng tại SEA Games 32 khi giành quyền vào bán kết với ngôi nhất bảng A. Ở trận bán kết, các cô gái Việt Nam sẽ đụng độ đội chủ nhà Campuchia. Dù từng thắng đậm đối thủ này cách đây 4 năm song HLV Mai Đức Chung vẫn không hề chủ quan.\r\n\r\nỞ vòng bảng, đội tuyển nữ Campuchia đã thi đấu ấn tượng khi giành 2 chiến thắng trước Singapore và Lào và chỉ chịu thua trước ứng cử viên cho tấm huy chương vàng là Thái Lan. Điều đó cho thấy phần nào sự tiến bộ của đội tuyển này dưới sự dẫn dắt của HLV Gao Fulin. Song, khi đối đầu với đội tuyển Việt Nam đã quá giàu kinh nghiệm sẽ là thử thách thực sự với đội chủ nhà.\r\n\r\nChia sẻ về cuộc đọ sức tới, HLV Gao Fulin cho biết ông cùng toàn đội sẽ cố gắng 100% để cống hiến một trận đấu tốt và hy vọng có thể tạo ra bất ngờ trước những nhà đương kim vô địch. Trận đấu giữa đội tuyển nữ Campuchia và đội tuyển nữ Việt Nam sẽ diễn ra vào lúc 16h ngày 12/05 tới đây.', 'https://onsports.vn/post/hlv-mai-duc-chung-luc-nay-chung-toi-danh-gia-campuchia-nhu-thai-lan-philippines', 'https://stvinaprod.vtvcab.vn/30449301-7d7f-4677-9181-195eeba906a5.jpg', 1, 1, 1, 'hlv-mai-duc-chung-luc-nay-chung-toi-danh-gia-campuchia-nhu-thai-lan-philippines', 1, 1),
(21, 'Báo Malaysia công kích tiền đạo U22 Việt Nam sau khi đội nhà bị loại', '2023-05-10', 'Hai tấm thẻ đỏ mà U22 Malaysia phải nhận trong trận thua 1-2 trước U22 Việt Nam khiến truyền thông của Malaysia phẫn nộ.', 'U22 Malaysia đã chính thức bị loại từ vòng bảng sau khi để thua 1-2 trước U22 Việt Nam. Sau trận đấu, truyền thông Malaysia tỏ ra phẫn nộ khi chứng kiến nỗ lực của đội nhà bị dập tắt bởi 2 tấm thẻ đỏ chỉ trong vòng 3 phút cuối trận.\r\n\r\nCụ thể, tấm thẻ đỏ đầu tiên của Malaysia đến ở phút 80 khi Safwan Mazlan dùng tay đánh vào ngực Thanh Nhàn. Đến phút 83, Najmudin phạm lỗi với tiền vệ U22 Việt Nam, anh phải nhận tấm thẻ vàng thứ 2 và rời sân.\r\n\r\nTờ Harian Metro, nhật báo thể thao hàng đầu của Malaysia, đã công kích Thanh Nhàn và trọng tài chính Jumpei Iida.\r\n\r\n\"Thanh Nhàn cần phải được trao giải Oscar. Anh ta có một pha diễn xuất sắc và đã lừa được trọng tài rút thẻ đỏ khiến U22 Malaysia bị loại”, tờ báo của Malay viết.\r\n\r\n“Chúng ta đang có thế trận tốt để tìm kiếm bàn gỡ, nhưng vì những pha ăn vạ của Thanh Nhàn mà đội bóng bị đuổi 2 người. Thất bại 1-2 khiến U22 Malaysia phải dừng bước ở vòng bảng dù còn 1 lượt trận nữa.\"\r\n\r\nChưa dừng lại ở đó, tờ Harian Metro còn chỉ trích trọng tài người Nhật với bài viết có tiêu đề: \"Trọng tài giúp U22 Việt Nam vào bán kết\".\r\n\r\nCây bút của tờ báo này nhận định: \"Những quyết định gây tranh cãi và có lợi cho U22 Việt Nam được đưa ra bởi trọng tài Jumpei Iida vốn không phải là điều gì lạ lùng. Trong quá khứ, vị trọng tài này cũng từng có nhiều tai tiếng.\r\n\r\nVí dụ điển hình là năm 2009, ông Jumpei Iida chỉ mất 9 giây để rút thẻ đỏ cho Tomo Sugawara của Tokyo Verdy, dù đó là tình huống Sugawara chỉ đáng nhận thẻ vàng\".\r\n\r\nVới việc chính thức bị loại, trận đấu giữa U22 Malaysia và U22 Singapore vào lúc 16h00 ngày 11/5 chỉ mang tính chất thủ tục. Trong khi đó, U22 Việt Nam và U22 Thái Lan sẽ quyết chiến để giành ngôi đầu bảng vào lúc 19h00 cùng ngày.', 'https://onsports.vn/post/bao-malaysia-cong-kich-tien-dao-u22-viet-nam-sau-khi-doi-nha-bi-loai', 'https://stvinaprod.vtvcab.vn/1954907a-80f6-4abe-b0f4-b2b56bf57a06.jpg', 1, 1, 1, 'bao-malaysia-cong-kich-tien-dao-u22-viet-nam-sau-khi-doi-nha-bi-loai', 1, 1),
(22, 'Chị em song sinh Việt kiều nén nỗi đau mất người thân, giành HCV SEA Games', '2023-05-08', 'Chị em song sinh Trương Thảo My và Trương Thảo Vy nén nỗi đau mất người thân để giúp bóng rổ Việt Nam giành HCV lịch sử ở nội dung 3x3 SEA Games 32.', 'Trong ngày thi đấu 7/5, đội tuyển bóng rổ nữ 3x3 Việt Nam đã xuất sắc Philippines với tỷ số 21-16 ở chung kết, đem về tấm HCV SEA Games lịch sử. Cặp sinh đôi Việt kiều, Thảo My và Thảo Vy, vẫn là điểm sáng với những pha đi bóng mạnh mẽ và nhiều cú ném hai điểm chuẩn xác, góp công lớn vào chiến thắng của tuyển bóng rổ nữ 3x3.\r\n\r\nĐược biết, tại SEA Games 32 trên đất Campuchia, đồng hành cùng chị em Trương Thảo My và Trương Thảo Vy là bố, ông Trương Quốc Mẫn. Khác với SEA Games 31 có cả bố mẹ theo cùng hỗ trợ và động viên, lần này mẹ của Trương Twins vắng mặt.\r\n\r\nTheo tiết lộ, lý do bà Katherine Nguyễn không thể có mặt để chúc mừng hai con trong ngày giành tấm HCV lịch sử cho bóng rổ Việt Nam là bởi bà ngoại của Trương Twins đã đột ngột qua đời. Bà Katherine Nguyễn phải về Mỹ để lo hậu sự và chỉ có thể cập nhật hành trình của các con tại SEA Games 32 thông qua các cuộc gọi điện thoại.\r\n\r\nBiết được thông tin bà qua đời, nhưng Thảo My và Thảo Vy đã nén nỗi đau để tập trung thi đấu vì mục tiêu giành vàng ở SEA Games làm quà tặng cho bà ngoại. Đáng tiếc, hai chị em không còn có thể trao tận tay món quà đặc biệt này cho bà. Nghị lực của Thảo My và Thảo Vy khiến người hâm mộ vô cùng khâm phục.', 'https://onsports.vn/post/chi-em-song-sinh-viet-kieu-nen-noi-dau-mat-nguoi-than-gianh-hcv-sea-games', 'https://stvinaprod.vtvcab.vn/dcdcc687-d974-4930-bb7e-1fcec1527be8.jpg', 1, 1, 2, 'chi-em-song-sinh-viet-kieu-nen-noi-dau-mat-nguoi-than-gianh-hcv-sea-games', 1, 1),
(23, 'Soi dàn \"ngoại binh\" siêu khủng của bóng rổ Campuchia tại SEA Games 32', '2023-05-07', 'Nhờ dàn ngoại binh chất lượng, bóng rổ Campuchia đã khiến \"gió đảo chiều\" tại SEA Games 32 khi đánh bại tất cả các đối thủ mạnh trong khu vực.', 'Bóng rổ Campuchia không để lại bất kỳ ấn tượng nào tại SEA Games 31 diễn ra vào năm ngoái. Trong tất cả các nội dung tham dự, họ thậm chí không có nổi một chiến thắng nào. Tuy nhiên chỉ sau 12 tháng, cục diện bỗng nhiên thay đổi hoàn toàn.\r\n\r\nNội dung 3x3 nam, ĐT nước chủ nhà SEA Games 32 đã gây sốc khi đánh bại ĐKVĐ Thái Lan. Trong khi đó, đội nữ Campuchia cũng xuất sắc vượt qua đối thủ mạnh Indonesia.\r\n\r\nSở dĩ bóng rổ Campuchia có sự lột xác ngoạn mục như vậy là bởi sự xuất hiện của những cầu thủ nhập tịch.\r\n\r\nTrước đây, đội hình bóng rổ Campuchia thường là những Cam Kiều (tức là cầu thủ vẫn có gốc Khmer) thì ở SEA Games năm nay, họ đã xây dựng một đội hình hầu như toàn ngoại binh.\r\n\r\nỞ nội dung 3x3, ĐT Campuchia sử dụng đến 3 cầu thủ \"mới toanh\".\r\n\r\nDarrin Dorsey (36 tuổi) có một sự nghiệp rất đáng chú ý. Anh sinh ra ở Phoenix, từng chơi cho 3 trường đại học ở Mỹ trước khi thi đấu cho một loạt đội bóng tại Bắc Mỹ và châu Âu. Trước khi nhập tịch Campuchia, cầu thủ này chơi cho Orthodox Amman ở Tây Á.\r\n\r\nTiếp theo là Brandon Peterson, cầu thủ 32 tuổi và cao 2m03 cũng có \"lai lịch\" khá ấn tượng. Anh cũng từng thi đấu cho CLB đại học rồi kinh qua nhiều đội bóng Âu, Trung Mỹ và Nam Mỹ. Peterson cũng cập bến Orthodox Amman và trở thành đồng đội của Dorsey trước khi cả hai khoác áo ĐT Campuchia.\r\n\r\nCầu thủ cuối cùng là Sayeed Pridgett, cũng là người trẻ nhất khi mới chuẩn bị bước sang tuổi 25. Thế nhưng sự nghiệp của cầu thủ này cũng không hề đơn giản khi anh từng thi đấu cho Birmingham Squadron, bên cạnh đó là các giải ở Hy Lạp và Đảo Síp.\r\n\r\nVới đội tuyển bóng rổ nữ Campuchia, các cầu thủ nhập tịch cũng rất chất lượng.\r\n\r\nBrittanny Dinkins từng thi đấu tại nhiều quốc gia từ châu Mỹ, châu Phi cho đến châu Âu. Trong đó, đỉnh cao sự nghiệp cô là giai đoạn thi đấu tại Iceland. Năm 2019, cô từng lên ngôi vô địch tại đây đồng thời nhận danh hiệu ngoại binh xuất sắc nhất.\r\n\r\nMeghan Simmons, 31 tuổi, cũng từng khoác áo nhiều CLB như New York Liberty, Atlanta Dream, Besiktas,... Cô cũng từng thi đấu tại Romania và Italia.\r\n\r\nNgoài ra, đội hình của tuyển bóng rổ nữ Campuchia còn có các ngoại binh là Mariah Cooks và Kim Hanlon, dù không có sự nghiệp ấn tượng nhưng cũng là đủ để giúp chủ nhà SEA Games 32 có một lực lượng hùng mạnh khiến phần còn lại phải ngán ngẩm.\r\n\r\nChắc chắn với đội hình gồm những ngoại binh chất lượng, bóng rổ Campuchia sẽ hướng đến mục tiêu giành HCV và họ đang ở rất gần với mục tiêu đó. Tuy nhiên, liệu cách làm này có giúp bóng rổ Campuchia được nâng tầm hay không?', 'https://onsports.vn/post/soi-dan-ngoai-binh-sieu-khung-cua-bong-ro-campuchia-tai-sea-games-32', 'https://stvinaprod.vtvcab.vn/e21cba0b-4874-4036-b4c1-60b331e1d351.jpg', 6, 2, 2, 'soi-dan-ngoai-binh-sieu-khung-cua-bong-ro-campuchia-tai-sea-games-32', 1, 1);
INSERT INTO `posts` (`id`, `title`, `date`, `description`, `content`, `link_post`, `image`, `country_id`, `category_id`, `sport_id`, `slug`, `hot_news`, `status`) VALUES
(24, 'Bóng rổ nữ Việt Nam lần đầu tiên trong lịch sử giành Huy chương Vàng SEA Games', '2023-05-07', 'Đội tuyển bóng rổ nữ Việt Nam đã xuất sắc vượt qua tuyển Philippines trong trận chung kết, qua đó giành được tấm Huy chương Vàng SEA Games 32.', 'Trưa 7/5, Đội tuyển bóng rổ nữ 3x3 Việt Nam (bao gồm Huỳnh Thị Ngoan, Nguyễn Thị Tiểu Duy và cặp sinh đôi Việt kiều Trương Thảo My, Trương Thảo Vy) đã xuất sắc đánh bại Đội tuyển Philippines với tỷ số 21-16 để giành tấm huy chương Vàng.\r\n\r\nTrước đó tại vòng bảng, tuyển bóng rổ nữ Việt Nam đã chơi xuất sắc khi toàn thắng cả 3 trận trước Thái Lan, Philippines và Lào để giành vé vào bán kết. Đến trận bán kết, dù bị Indonesia dẫn trước 4-10 nhưng các cô gái của tuyển bóng rổ Việt Nam đã lội ngược dòng ngoạn mục 21-18.\r\n\r\nTrong trận chung kết, đối thủ Philippines nhập cuộc tự tin và có lợi thế dẫn trước 5-3 ở đầu trận. Nhưng thêm một lần nữa, các cô gái Việt Nam cho thấy tinh thần quyết tâm và sự tập trung cao độ để ghi điểm dẫn lại đối thủ. Trong đó, song sinh Việt kiều Thảo My và Thảo Vy với những pha đi bóng mạnh mẽ và nhiều cú ném 2 điểm chuẩn xác chính là điểm sáng lớn nhất, giúp cho Việt Nam luôn giữ được thế trận trước đối thủ.\r\n\r\nKhhi trận đấu chỉ còn chưa đầy 2 phút, tuyển Việt Nam đã dẫn trước đối thủ đến 4 điểm. Philippines nỗ lực làm mọi cách để rút ngắn khoảng cách nhưng Trương Thảo My chấm dứt mọi hy vọng của đội bạn khi thực hiện 2 cú ném hoàn hảo, qua đó mang về tấm Huy chương Vàng cho ĐT bóng rổ nữ Việt Nam.\r\n\r\nTấm huy chương Vàng này cũng là tấm HCV đầu tiên của bóng rổ Việt Nam ở một kỳ SEA Games, nên có thể nói rằng đó là một tấm HCV mang tính lịch sử của môn bóng rổ nước nhà.', 'https://onsports.vn/post/bong-ro-nu-viet-nam-lan-dau-tien-trong-lich-su-gianh-huy-chuong-vang-sea-games', 'https://stvinaprod.vtvcab.vn/847dae52-e302-4bf7-a678-030b5489403e.jpg', 1, 1, 2, 'bong-ro-nu-viet-nam-lan-dau-tien-trong-lich-su-gianh-huy-chuong-vang-sea-games', 1, 1),
(25, 'Tuyển nữ bóng rổ 3x3 \"đòi nợ\" thành công trước người Thái', '2023-05-06', 'Gần một năm sau ngày thất bại trước người Thái trong trận chung kết SEA Games 31, Huỳnh Thị Ngoan và các đồng đội đã có màn phục hận thành công trên đất Campuchia.', 'So với cách đây 1 năm, đội tuyển bóng rổ 3x3 Việt Nam tới Campuchia với lực lượng gần như nguyên vẹn với cặp sinh đôi Thảo My - Thảo Vy cùng đội trưởng Huỳnh Thị Ngoan là những cái tên nòng cột.\r\n\r\nTrước khi bước vào trận đấu gặp Thái Lan, các cô gái Việt Nam đã đánh bại đối thủ khó chịu Philippines với tỷ số 21-19. Đây được xem là bước đà tâm lý cực tốt để các cô gái Việt Nam sẵn sàng cho màn phục hận.\r\n\r\nNgay từ đầu trận đấu, Việt Nam là đội nắm thế chủ động khi liên tục dẫn trước với những cú ném 2 điểm chính xác của Huỳnh Ngoan. Cặp chị em sinh đôi Thảo My - Thảo Vy cũng làm tốt trong việc xâm nhập và thực hiện những pha lên rổ chính xác. Khả năng phòng thủ của đội tuyển 3x3 Việt Nam cũng được thể hiện tốt ở trận đấu này.\r\n\r\nDù vậy, Thái Lan với bản lĩnh của nhà vô địch SEA Games cũng tỏ ra không hề kém cạnh khi liên tục ghi điểm, tạo thế bám đuổi trong toàn bộ trận đấu. \r\n\r\nSự phấn khích của các CĐV Việt Nam có mặt trên khán đài của nhà thi đấu bóng rổ nằm trong khuôn viên SVĐ Murodok Techo (Phnom Penh, Campuchia).\r\n\r\nTuy nhiên, cuối cùng sự tỏa sáng của Huỳnh Ngoan đã giúp các cô gái Việt Nam kết thúc trận đấu với chiến thắng 16-13 đầy kịch tính. Đồng thời, đội tuyển bóng rổ nữ 3x3 Việt Nam cũng đòi thành công \"món nợ\" từ người Thái sau thất bại cay đắng trên sân nhà cách đây 1 năm.\r\n\r\nChiến thắng này cũng giúp bóng rổ 3x3 Việt Nam chính thức giành vé vào vòng bán kết. Đối thủ của Thảo My và các đồng đội sẽ được xác định sau khi lượt trận còn lại của buổi chiều ngày 06/05 kết thúc.', 'https://onsports.vn/post/tuyen-nu-bong-ro-3x3-doi-no-thanh-cong-truoc-nguoi-thai', 'https://stvinaprod.vtvcab.vn/adf9c6a0-8b8c-4434-be95-819c988c5722.jpg', 1, 1, 2, 'tuyen-nu-bong-ro-3x3-doi-no-thanh-cong-truoc-nguoi-thai', 1, 1),
(26, 'Bóng rổ Hà Nội toàn thắng tại chặng cuối VBA 3x3 Prime 2023', '2023-04-03', 'Tại chặng cuối VBA 3x3 Prime 2023, 2 đội bóng đến từ Thủ đô Hà Nội là Thang Long Warriors (Men Pro) và Hanoi Buffaloes (U20) xuất sắc nâng cao chức vô địch sau những trận đấu căng thẳng và kịch tính.', 'Trận chung kết nội dung Men Pro là màn so tài giữa Thang Long Warriors và Saigon Heat. 2 đội bóng này đã gặp nhau ở chung kết của 2 chặng thi đấu trước đó và đội bóng Thủ đô đều giành chiến thắng.\r\n\r\nTrận chung kết nội dung Men Pro là màn so tài giữa Thang Long Warriors và Saigon Heat. 2 đội bóng này đã gặp nhau ở chung kết của 2 chặng thi đấu trước đó và đội bóng Thủ đô đều giành chiến thắng.\r\n\r\nCòn tại nội dung U20 Nam, trận chung kết là màn so tài giữa U20 Cantho Catfish và U20 Hanoi Buffaloes. 2 đội đã cống hiến cho người hâm mộ 1 màn so tài hấp dẫn và kịch tính như kỳ vọng.\r\n\r\nThế trận bám đuổi gay cấn khi tỉ số lên tới 13-13, tuy nhiên, Hanoi Buffaloes ghi liền 3 điểm để vươn lên dẫn 16-13. Đây là bước ngoặt của trận đấu khi Cantho Catfish không thể gỡ hòa lần nữa và phải nhìn Hanoi Buffaloes lên ngôi vô địch.', 'https://onsports.vn/post/bong-ro-ha-noi-toan-thang-tai-chang-cuoi-vba-3x3-prime-2023', 'https://stvinaprod.vtvcab.vn/9a1edbe4-c31e-4a4a-b409-46209fcdeba0.jpg', 1, 1, 2, 'bong-ro-ha-noi-toan-thang-tai-chang-cuoi-vba-3x3-prime-2023', 1, 1),
(27, 'ĐT nữ Việt Nam tiếp tục nhận thưởng lớn sau HCV SEA Games 32', '2023-05-23', 'Chiều 23/05, VFF đã tổ chức lễ trao thưởng cho ĐT nữ Việt Nam và U22 Việt Nam sau thành tích ấn tượng tại SEA Games 32. Tại đây, thầy trò HLV Mai Đức Chung tiếp tục nhận được những món quà tinh thần để hướng tới VCK World Cup 2023 diễn ra vào tháng 7 sắp tới.', 'Chiến thắng trước Myanmar trong trận chung kết giúp đội tuyển nữ Việt Nam có lần thứ 4 liên tiếp thầy trò HLV Mai Đức Chung giành được tấm huy chương vàng SEA Games. Chia sẻ trong lễ trao thưởng, Chủ tịch VFF - ông Trần Quốc Tuấn cho biết: \"Tấm huy chương vàng tại kỳ đại hội này có ý nghĩa lịch sử khi đây là lần thứ 8 và lần thứ 4 liên tiếp – thành tích lịch sử mà chỉ có ĐT nữ Việt Nam làm được qua các kỳ SEA Games. Đội tuyển nữ Việt Nam đã phải đối diện với nhiều khó khăn khi một số trụ cột chấn thương, sự tiến bộ của các nước trong khu vực như Philippines, Myanmar, Thái Lan. Đặc biệt là Philippines khi họ là đội đầu tiên tham dự WC và đang là đương kim vô địch AFF Cup\".\r\n\r\nĐặc biệt, cũng trong buổi lễ, ĐT nữ Việt Nam tiếp tục nhận được những khoản \"thưởng khủng\" từ những nhà mạnh thường quân. Trong đó, Tập đoàn Thaco thưởng cho ĐT nữ Việt Nam 1 tỷ đồng, Tổng Công ty Cổ phần Phong Phú thưởng thêm 1 tỷ đồng. Như vậy, cùng với số tiền thưởng từ VFF, thầy trò HLV Mai Đức Chung đã nhận được tổng cộng 5,6 tỷ đồng sau chức vô địch tại SEA Games 32. Bên cạnh đội tuyển nữ Việt Nam, U22 Việt Nam cũng được VFF thưởng số tiền là 2,4 tỷ đồng cho thành tích giành huy chương đồng SEA Games 32.\r\n\r\nHLV Mai Đức Chung cho biết: \"Tôi thay mặt đội tuyển nữ Việt Nam cảm ơn tình cảm của mình với tất cả mọi người đã hỗ trợ, giúp đỡ và động viên chúng tôi rất nhiều trong những năm vừa qua. Tôi xin hứa trước người hâm mộ, đội tuyển nữ Việt Nam sẽ đoàn kết, phấn đầu quyết tâm và giành thêm những thành tích cao hơn nữa ở những giải đấu trong thời gian tới\".\r\n\r\nTrong thời gian tới, ĐT nữ Việt Nam sẽ tiếp tục tập luyện để hướng tới VCK World Cup 2023 tại Astralia và New Zealand vào tháng 7 tới đây. Thầy trò HLV Mai Đức Chung sẽ có quãng tập huấn chất lượng tại Đức và có một trận đấu giao hữu cùng ĐT nữ Đức. Đây được xem là kỳ tập huấn chất lượng để ĐT nữ Việt Nam sẵn sàng cho mục tiêu World Cup.\r\n\r\nVới U22 Việt Nam, các cầu thủ đã trở lại CLB để thi đấu tại V.League và giải Hạng Nhất QG. Vào tháng 6 tới, toàn đội sẽ tập trung trở lại để tham dự giải giao hữu Panda Cup 2023 tại Thành Đô (Trung Quốc). Đây là giải đấu nhằm chuẩn bị cho VL U23 Châu Á 2024 sẽ diễn ra tại Việt Nam vào tháng 9 tới.', 'https://onsports.vn/post/dt-nu-viet-nam-tiep-tuc-nhan-thuong-lon-sau-hcv-sea-games-32', 'https://stvinaprod.vtvcab.vn/3fb24f16-a627-4043-b794-e28360a00948.jpg', 1, 1, 1, 'dt-nu-viet-nam-tiep-tuc-nhan-thuong-lon-sau-hcv-sea-games-32', 1, 1),
(28, 'Xác định trận đấu đầu tiên của ĐT Việt Nam dưới thời HLV Troussier', '2023-05-19', 'Theo kế hoạch, đội tuyển Việt Nam sẽ có trận đấu đầu tiên dưới thời HLV Philippe Troussier vào đợt FIFA Days trong tháng 6 sắp tới.', 'Cụ thể, đội tuyển Việt Nam sẽ đối đầu với Hồng Kông vào ngày 15/06 tới đây. Địa điểm tổ chức trận đấu có thể là SVĐ Lạch Tray - nơi có điều kiện cơ sở vật chất thuộc diện hàng đầu V.League thời điểm hiện tại. Để chuẩn bị cho trận đấu này, đội tuyển Việt Nam sẽ bắt đầu tập trung từ ngày 07/06, ngay sau khi vòng 11 V.League khép lại.\r\n\r\nCụ thể, đội tuyển Việt Nam sẽ đối đầu với Hồng Kông vào ngày 15/06 tới đây. Địa điểm tổ chức trận đấu có thể là SVĐ Lạch Tray - nơi có điều kiện cơ sở vật chất thuộc diện hàng đầu V.League thời điểm hiện tại. Để chuẩn bị cho trận đấu này, đội tuyển Việt Nam sẽ bắt đầu tập trung từ ngày 07/06, ngay sau khi vòng 11 V.League khép lại.\r\n\r\nCòn với U23 Việt Nam, sau khi giành tấm HCĐ tại SEA Games 32, các cầu thủ cũng sẽ tập trung trở lại vào tháng 6 tới để tham dự giải giao hữu U23 Panda Cup tại Thành Đô (Trung Quốc). Tại đây, các cầu thủ trẻ sẽ tiếp tục được cọ sát với những đối thủ hàng đầu châu lục như Uzbekistan, Bahrain và chủ nhà Trung Quốc. Đó sẽ lại là dịp để U23 Việt Nam cải thiện lối chơi, khắc phục những sai sót trước khi bước vào VL U23 Châu Á diễn ra trên sân nhà vào tháng 9 tới đây.', 'https://onsports.vn/post/xac-dinh-tran-dau-dau-tien-cua-dt-viet-nam-duoi-thoi-hlv-troussier', 'https://stvinaprod.vtvcab.vn/d6a15349-99cc-4af9-90f0-4d69aac2b5b6.jpg', 1, 1, 1, 'xac-dinh-tran-dau-dau-tien-cua-dt-viet-nam-duoi-thoi-hlv-troussier', 1, 1),
(29, 'Huỳnh Như chuẩn bị tái ký với Lank FC, tiếp tục chinh phục châu Âu', '2023-05-23', 'Với những gì đã thể hiện được ở mùa giải 2022, tiền đạo Huỳnh Như đã gây được ấn tượng mạnh mẽ với BLĐ Lank FC. Đội bóng Bồ Đào Nha được cho đang sẵn sàng gia hạn hợp đồng với đội trưởng ĐT nữ Việt Nam thêm ít nhất 1 năm nữa.', 'Dù chưa có một suất đá chính chắc chắn song Huỳnh Như là một trong những quân bài dự bị chiến lược quan trọng của Lank FC ở mùa giải vừa qua. Trong 10 tháng thi đấu tại Bồ Đào Nha, tiền đạo gốc Trà Vinh đã đóng góp 7 bàn cho Lank FC, đồng thời có 1 lần được bình chọn vào đội hình tiêu biểu của giải VĐQG nữ Bồ Đào Nha.\r\n\r\nKhông chỉ đóng góp tốt về mặt chuyên môn, Huỳnh Như cũng cho thấy mình hòa nhập rất nhanh với môi trường mới. Đội trưởng ĐT nữ Việt Nam rất được lòng các đồng đội cũng như BLĐ đội bóng. Bởi vậy, ngay sau khi mùa giải 2022/2023 khép lại, nhiều thông tin cho rằng Lank FC sẵn sàng kích hoạt điều khoản gia hạn hợp đồng thêm 1 năm với Huỳnh Như.\r\n\r\nĐây là phần thưởng xứng đáng dành cho những nỗ lực và cố gắng của chân sút người Việt Nam trong lần đầu xuất ngoại chơi bóng. Nếu thương vụ này thành công, tiền đạo sinh năm 1991 sẽ tiếp tục gắn bó với đội bóng Bồ Đào Nha thêm ít nhất 1 năm nữa.\r\n\r\nTại SEA Games 32, Huỳnh Như vẫn cho thấy đẳng cấp của mình khi là đầu tàu đưa đội tuyển nữ Việt Nam bảo vệ thành công tấm huy chương vàng, qua đó có lần thứ 4 liên tiếp lên ngôi hậu ở giải đấu này. Do mùa giải ở Bồ Đào Nha đã kết thúc nên Huỳnh Như sẽ lập tức bước vào tập luyện cùng ĐT nữ Việt Nam để hướng tới VCK World Cup diễn ra vào tháng 7 tới đây.\r\n\r\nSau giải đấu trên đất Australia và New Zealand, Huỳnh Như sẽ cùng BLĐ của Lank FC ngồi lại và đàm phán về việc gia hạn hợp đồng để tiền đạo quê Trà Vinh có thể tiếp tục ở lại châu Âu chơi bóng.', 'https://onsports.vn/post/huynh-nhu-chuan-bi-tai-ky-voi-lank-fc-tiep-tuc-chinh-phuc-chau-au', 'https://stvinaprod.vtvcab.vn/d29a1228-a7ce-497d-be17-77a18eda8fc2.jpg', 1, 1, 1, 'huynh-nhu-chuan-bi-tai-ky-voi-lank-fc-tiep-tuc-chinh-phuc-chau-au', 1, 1),
(30, 'HLV Troussier muốn U22 Việt Nam chơi bóng đá đẹp, giành HCV SEA Games', '2023-04-26', 'HLV Troussier cùng đội tuyển U22 Việt Nam đã đáp chuyến bay sang Campuchia. Chiến lược gia người Pháp cho biết toàn đội đã sẵn sàng để bảo vệ tấm HCV SEA Games.', 'Đội tuyển U22 Việt Nam đáp chuyến bay đến thủ đô Phnom Pênh vào sáng 25/4. Tại sân bay, các cầu thủ và ban huấn luyện U22 Việt Nam được chào đón hết sức nồng nhiệt bởi nước chủ nhà và cả đại diện cộng đồng doanh nghiệp Việt Nam tại Campuchia.\r\n\r\nTrước khi di chuyển về khách sạn, HLV Philippe Troussier đã có buổi trao đổi ngắn với các phóng viên. Dù kết quả của U22 Việt Nam thời gian gần đây chưa tốt, HLV người Pháp vẫn khẳng định các học trò sẽ nỗ lực hết sức để bảo vệ tấm HCV môn bóng đá nam tại SEA Games 32. Ông nói:\r\n\r\n“Tôi rất vui khi có mặt tại đây. Đây cũng là lần đầu tiên tôi đặt chân đến Campuchia và tham vọng của chúng tôi lần này là được chơi thứ bóng đá tốt nhất của mình, qua đó hoàn thành mục tiêu vô địch.\r\n\r\nĐể hoàn thành mục tiêu là không hề đơn giản, có thể nói là chúng tôi sẽ gặp phải nhiều thử thách khó khăn. Nhưng tôi nghĩ toàn đội đã sẵn sàng để đương đầu với nó. Các học trò của tôi đang luyện tập với sự nghiêm túc và tập trung cao độ. U22 Việt Nam đến đây để giành lấy kết quả tốt nhất”\r\n\r\nBên cạnh đó, HLV Troussier cũng thừa nhận U22 Việt Nam đang gặp áp lực lớn sau những kết quả chưa tốt trong thời gian vừa qua. Dù vậy, vị chiến lược gia người Pháp khẳng định sẽ đưa ra những đấu pháp hợp lý để đội bóng của mình đạt được kết quả tốt nhất có thể.\r\n\r\n“Chúng tôi có áp lực về mục tiêu ở SEA Games, áp lực phải chơi thứ bóng đã xuất sắc, thứ bóng đá đẹp. Tuy nhiên, tôi hy vọng rằng những áp lực này sẽ tạo nên quyết tâm cho các học trò của mình”, HLV Troussier nói.\r\n\r\nHiện tại, U22 Việt Nam đã về nghỉ ngơi tại khách sạn do BTC sắp xếp. Chiều nay, đội sẽ có buổi tập trên sân NIPES, cách vị trí khách sạn đang đóng quân khoảng 10km. Đây cũng là buổi tập chính thức đầu tiên của U22 Việt Nam trên đất Campuchia để chuẩn bị cho SEA Games 32.', 'https://onsports.vn/post/hlv-troussier-muon-u22-viet-nam-choi-bong-da-dep-gianh-hcv-sea-games', 'https://stvinaprod.vtvcab.vn/0716bc00-645c-461f-8385-6882043140f2.jpg', 1, 1, 1, 'hlv-troussier-muon-u22-viet-nam-choi-bong-da-dep-gianh-hcv-sea-games', 1, 1),
(31, 'Những sao trẻ U20 hứa hẹn gây sốt tại VBA 3x3 Prime 2023', '2023-03-30', 'Nhiều cầu thủ trẻ lứa U20 đã sở hữu chiều cao ấn tượng, hứa hẹn có những màn trình diễn ấn tượng tại VBA 3x3 Prime 2023.', 'Nội dung U20 VBA 3x3 2023 sẽ có 16 đội bóng tham dự gồm:\r\n\r\n- 05 CLB VBA: Hanoi Buffaloes - Thang Long Warriors - Saigon Heat - Ho Chi Minh City Wings - Cantho Catfish.\r\n\r\n- 04 đội bóng khách mời: Bumblebees, PLAY NUTRITION, Phú Thọ HCM, ARES BKK (Thái Lan)\r\n\r\n- 07 CLB từ các học viện, trường Quốc tế, trường Đại học: Danang City (powered by Danang Dragons & Danang Basketball Development Centre), The Perfomance Lab, ĐH Tôn Đức Thắng, ĐH Kiến Trúc, Triform - AIS (Australian International School), YourLife, Next Level Basketball.\r\n\r\nTrong đó, Danang Dragons, đại diện bóng rổ miền Trung, đã tiết lộ danh tính các tay ném U20. Đáng chú ý trong đội hình của \"Rồng sông Hàn\" có ngôi sao giải U18 quốc gia Huỳnh Andre Minh Triết với chiều cao 1,91m.\r\n\r\nMột đại diện khác đến từ Hà Nội, Thang Long Warriors cũng sở hữu tay ném Đào Nguyễn Quang Anh cao đến 1,88m dù chỉ ở độ tuổi 18.\r\n\r\nTrong khi đó, Đinh Toàn Quốc của Hanoi Buffaloes cũng là cầu thủ có chiều cao vượt trội nhất với 1,95m.\r\n\r\nVương Quốc Trung, tay ném sinh năm 2003 của Bumblebees - CLB khách mời lần đầu tham dự giải đấu của VBA, còn đặc biệt khiến người hâm mộ tò mò khi sở hữu chiều cao lên đến 2m.\r\n\r\nSau 2 chặng đầu tiên diễn ra tại Hà Nội (từ ngày 18 đến 19/3) và Nha Trang (từ ngày 24 đến 26/3), chặng thứ 3 và cũng là chặng chung kết (Grand Finals) của Giải bóng rổ VBA 3x3 Prime 2023 sẽ được tổ chức tại phố đi bộ Nguyễn Huệ (TP.HCM) từ ngày 31/3 đến 2/4.', 'https://onsports.vn/post/nhung-sao-tre-u20-hua-hen-gay-sot-tai-vba-3x3-prime-2023', 'https://stvinaprod.vtvcab.vn/b8138285-60cd-4657-9121-b9df18ca73a4.png', 1, 1, 2, 'nhung-sao-tre-u20-hua-hen-gay-sot-tai-vba-3x3-prime-2023', 1, 1),
(32, 'Lịch thi đấu giải VBA 3x3 Prime 2023 chặng 3 tại TP.HCM', '2023-03-30', 'Giải bóng rổ VBA 3x3 Prime 2023 chặng 3 sẽ diễn ra từ ngày 31/3 - 2/4 tại phố đi bộ Nguyễn Huệ (TP.HCM).', 'Sau 2 chặng đầu tiên diễn ra tại Hà Nội (từ ngày 18 đến 19/3) và Nha Trang (từ ngày 24 đến 26/3), chặng thứ 3 và cũng là chặng chung kết (Grand Finals) của Giải bóng rổ VBA 3x3 Prime 2023 sẽ được tổ chức tại phố đi bộ Nguyễn Huệ (TP.HCM) từ ngày 31/3 đến 2/4. Tại chặng cuối sẽ diễn ra 2 nội dung chung kết Men Pro và U20 Men.\r\n\r\nỞ nội dung U20 sẽ có 16 đội bóng tham dự gồm:\r\n\r\n- 05 CLB VBA: Hanoi Buffaloes - Thang Long Warriors - Saigon Heat - Ho Chi Minh City Wings - Cantho Catfish.\r\n\r\n- 04 đội bóng khách mời: Bumblebees, PLAY NUTRITION, Phú Thọ HCM, ARES BKK (Thái Lan)\r\n\r\n- 07 CLB từ các học viện, trường Quốc tế, trường Đại học: Danang City (powered by Danang Dragons & Danang Basketball Development Centre), The Perfomance Lab, ĐH Tôn Đức Thắng, ĐH Kiến Trúc, Triform - AIS (Australian International School), YourLife, Next Level Basketball.\r\n\r\nTrong khi đó, MERON AKO chính thức là đội bóng thứ 12, đội bóng khách mời đặc biệt tranh tài tại nội dung Men Pro ở chặng thi đấu cuối cùng, Grand Final. Đội bóng đến từ Philippines mang đến VBA 3x3 dàn cầu thủ chất lượng. Sự xuất hiện của đội bóng thứ 12 hứa hẹn sẽ tạo nên những cặp đấu đáng xem với thể thức Double Elimination (Loại kép).', 'https://onsports.vn/post/lich-thi-dau-giai-vba-3x3-prime-2023-chang-3-tai-tphcm', 'https://stvinaprod.vtvcab.vn/9cb44beb-6055-4aa7-bb43-6b4466218820.jpg', 1, 1, 2, 'lich-thi-dau-giai-vba-3x3-prime-2023-chang-3-tai-tphcm', 1, 1),
(33, 'Thang Long Warriors vô địch chặng 1 giải VBA 3x3 Prime 2023', '2023-03-20', 'Tối 19/3, Thang Long Warriors đã xuất sắc đánh bại Sai Gon Heat trong trận chung kết và lên ngôi vô địch chặng 1 giải bóng rổ VBA 3x3 Prime 2023.', 'Trong trận chung kết diễn ra vào tối 19/3 tại Phố đi bộ Hồ Gươm (Hà Nội), Thang Long Warriors thi đấu bùng nổ và giành chiến thắng ấn tượng 21-19 trước CLB Sai Gon Heat với sự tỏa sáng của ngoại binh Blazquez Lopez José Antonio, qua đó lên ngôi vô địch chặng 1 giải VBA 3x3 2023.\r\n\r\nTrên hành trình tiến tới chức vô địch, Thang Long Warriors giữ vững thành tích toàn thắng khi lần lượt hạ nhiều đối thủ mạnh như: Rising Star, New Sport, Vietnam Select... Tại giải đấu năm nay, đội bóng thủ đô cũng sở hữu nhiều ngôi sao chất lượng như: Tô Quang Trung, Thái Hưng, Carlos Martinez, Josse Antonio Blazquez,...\r\n\r\nNội dung thi đấu VBA 3x3 Men Pro tại chặng 1 khép lại với xếp hạng sau:\r\n\r\n· Hạng nhất: Thang Long Warriors\r\n\r\n· Hạng nhì: Saigon Heat\r\n\r\n· Hạng 3: VN Select\r\n\r\n· Hạng 4: BumbleBees\r\n\r\nSau khi kết thúc chặng 1 tại Hà Nội, giải VBA 3x3 sẽ tiếp tục thi đấu chặng 2 tại Nha Trang từ 25/3 đến 26/3. Chặng cuối cùng sẽ diễn ra tại TP.HCM từ 31/3 đến 2/4.', 'https://onsports.vn/post/thang-long-warriors-vo-dich-chang-1-giai-vba-3x3-prime-2023', 'https://stvinaprod.vtvcab.vn/4eebba12-4006-4649-a3f5-8bfcaf087879.jpg', 1, 1, 2, 'thang-long-warriors-vo-dich-chang-1-giai-vba-3x3-prime-2023', 1, 1),
(34, 'Giải bóng rổ VBA 3x3 Prime 2023 khởi tranh từ ngày 18/3 tại Hà Nội', '2023-03-26', 'Giải Bóng rổ 3x3 (VBA 3x3 Prime) 2023 sẽ chính thức khởi tranh tại 3 thành phố: Hà Nội, Nha Trang và Hồ Chí Minh với 3 nội dung thi đấu gồm: VBA 3x3 Men Pro, VBA 3x3 Women Open và VBA 3x3 Men U20.', 'Ở mùa giải VBA 2023, BTC có nhiều thay đổi ở nội dung dành cho bóng rổ 3x3. Lần đầu tiên, các đội bóng nữ được tham dự nội dung này.\r\n\r\nGiải bóng rổ VBA 3x3 Prime 2023 dự kiến tổ chức tại 3 thành phố: Hà Nội, Nha Trang và Hồ Chí Minh với 3 nội dung thi đấu gồm:\r\n\r\n- Giải Bóng rổ Chuyên nghiệp Nam VBA 3x3 (Men Pro)\r\n\r\n- Giải Bóng rổ Nữ VBA 3x3 (VBA 3x3 Women)\r\n\r\n- Giải Bóng rổ trẻ U20 VBA 3x3 (VBA 3x3 Men U20)\r\n\r\nTại Hà Nội, VBA 3x3 Prime Stop 1 tranh tài từ ngày 18 tới 19/3 tại Phố đi bộ Hồ Gươm ở nội dung VBA 3x3 Men Pro. Có 11 đội bóng - CLB chuyên nghiệp tham gia tranh tài. Trong đó, 4 đội VBA gồm: Thang Long Warriors, Saigon Heat, Ho Chi Minh City Wings, Cantho Catfish. 7 đội khách mời gồm: Hoop Dreams, Aress AKK (Thái Lan), New Sports, Rising Stars (Malaysia), Bumblebees, The performance lab và VietNam Select.\r\n\r\nBên cạnh các trận cầu kịch tính đến nghẹt thở và những màn trình diễn mãn nhãn từ các VĐV chuyên nghiệp ở cuộc thi Dunk contest (úp rổ), người hâm mộ còn được trở thành một phần của “ngày hội bóng rổ” VBA qua các hoạt động bên lề hấp dẫn như: Giải bóng rổ 1x1 dành cho lứa tuổi U15 hay hết mình trong không khí sôi động của những bài rap từ nhóm 1337 Battle tại chặng Hà Nội.\r\n\r\nSau khi kết thúc chặng đầu tiên tại Hà Nội, giải VBA 3x3 Prime 2023 sẽ diễn ra chặng thứ hai ở thành phố biển Nha Trang (ngày 24-26/3) trước khi di chuyển vào Tp.Hồ Chí Minh cho chặng chung kết (từ ngày 31/3 đến ngày 2/4).', 'https://onsports.vn/post/giai-bong-ro-vba-3x3-prime-2023-khoi-tranh-tu-ngay-183-tai-ha-noi', 'https://stvinaprod.vtvcab.vn/48692fbd-533f-4d93-b4c7-0f2a774939aa.jpeg', 1, 1, 2, 'giai-bong-ro-vba-3x3-prime-2023-khoi-tranh-tu-ngay-183-tai-ha-noi', 1, 1),
(35, 'Chung kết giải bóng rổ HBC 2022: 3F Galaxy và Fudo giành chức vô địch đầy cảm xúc', '2022-10-02', 'Trong mùa giải đầu tiên của HBC, 3F Galaxy và Fudo đã giành chức vô địch đầy cảm xúc sau màn trình diễn xuất sắc trong trận chung kết của nam và nữ.', 'Tại nội dung nữ, Fudo đã có một trận chung kết đầy kịch tính trước Whales. Ở vòng bảng, Whales đã có chiến thắng trước Fudo (1-0). Trong lần tái đấu này, Fudo kiên trì bám trụ trong suốt 4 hiệp đấu chính thức để rồi bùng nổ vào những phút giây cuối cùng và trở thành nhà vô địch đầu tiên của mùa giải HBC.\r\n\r\nCòn ở nội dung nam, 3F Galaxy đã thể hiện sức mạnh áp đảo trong suốt hành trình từ vòng bảng tới trận chung kết. Đối mặt với Hà Nội ở trận đấu cuối cùng, 3F Galaxy tiếp tục thể hiện có màn trình diễn ấn tượng để lên ngôi vô địch đầy thuyết phục.\r\n\r\nNhư vậy, tại mùa giải đầu tiên của HBC, Fudo và 3F Galaxy đã lên ngôi vô địch rất ấn tượng và thuyết phục. Giải bóng rổ vô địch Hà Nội (Hanoi Basketball Championship) 2022 là giải đấu dành cho các cầu thủ cấp độ bán chuyên và tiệm cận chuyên nghiệp trên địa bàn Hà Nội. Đây cũng là giải bóng rổ bán chuyên nghiệp lần đầu tiên được tổ chức tại Việt Nam. Giải đấu đã góp phần lan tỏa văn hóa bóng rổ hiện đại, năng động, truyền cảm hứng lối sống tích cực đến thế hệ trẻ và nâng cao sức khỏe cộng đồng.', 'https://onsports.vn/post/chung-ket-giai-bong-ro-hbc-2022-3f-galaxy-va-fudo-gianh-chuc-vo-dich-day-cam-xuc', 'https://stvinaprod.vtvcab.vn/0be73fa0-86e8-44e0-bd50-1d4d0988c95a.jpg', 1, 1, 2, 'chung-ket-giai-bong-ro-hbc-2022-3f-galaxy-va-fudo-gianh-chuc-vo-dich-day-cam-xuc', 1, 1),
(36, 'Khai mạc giải bóng rổ vô địch Hà Nội 2022: Dàn sao VBA hội tụ', '2022-09-22', 'Lễ khai mạc giải bóng rổ vô địch Hà Nội (HBC) 2022 đã diễn ra vào tối 21/9 tại Nhà thi đấu huyện Thanh Trì (Hà Nội).', 'Giải bóng rổ vô địch Hà Nội (Hanoi Basketball Championship) 2022 là giải đấu dành cho các cầu thủ cấp độ bán chuyên và tiệm cận chuyên nghiệp trên địa bàn Hà Nội. Đây là giải bóng rổ bán chuyên nghiệp lần đầu tiên được tổ chức tại Việt Nam.\r\n\r\nSân chơi này quy tụ 12 đội bóng đến từ Thủ đô gồm 8 đội nam và 4 đội nữ, chia làm 2 bảng A-B. Các đội sẽ thi đấu vòng tròn tính điểm tại mỗi bảng.\r\n\r\nTrưởng ban Tổ chức Giải HBC 2022 Tô Duy cho biết: “Sự góp mặt của 12 đội bóng giàu thành tích trên địa bàn Hà Nội, cùng format tổ chức hoàn toàn mới, giải đấu hứa hẹn sẽ mang đến các trận đấu hấp dẫn, kịch tính, quyết liệt. 28 trận đấu sẽ là cơ hội giao lưu, cọ sát của các cầu thủ không chuyên, chuyên nghiệp và ngoại quốc”.\r\n\r\nĐặc biệt, một trong những điểm thu hút nhất của HBC chính là sự góp mặt của dàn sao VBA (Giải bóng rổ chuyên nghiệp Việt Nam) như Tâm Đinh, Đặng Thái Hưng, Nguyễn Văn Hùng, Tô Quang Trung, Huỳnh Thanh Tâm và các ngoại binh như DeAngelo Hamilton, Dominique Grant,...\r\n\r\nTrong ngày khai mạc đã diễn ra các trận đấu: CLB Ba Đình đã giành chiến thắng 80-63 trước CLB Basket Sags (nam); còn CLB Whales thắng 60-44 trước CLB Fudo (nữ). Trận đấu tâm điểm của ngày thi đấu đầu tiên, CLB Phòng không không quân đã thua 47-67 trước CLB Hà Nội (nam); còn ở trận đấu muộn trong ngày, CLB Hidden Dragons thua 65-67 trước CLB Scorpious (nam).', 'https://onsports.vn/post/khai-mac-giai-bong-ro-vo-dich-ha-noi-2022-dan-sao-vba-hoi-tu', 'https://stvinaprod.vtvcab.vn/8ee66612-e3df-4a02-9374-f3e1d8b6c3d1.JPG', 1, 1, 2, 'khai-mac-giai-bong-ro-vo-dich-ha-noi-2022-dan-sao-vba-hoi-tu', 1, 1),
(37, 'Giải bóng rổ vô địch Hà Nội khởi tranh từ ngày 21/9 trên VTVcab', '2022-09-15', 'Giải bóng rổ vô địch Hà Nội 2022 sẽ diễn ra từ ngày 21/09 đến ngày 02/10/2022 tại Nhà thi đấu huyện Thanh Trì (673 Ngọc Hồi, Văn Điển).', 'Giải bóng rổ vô địch Hà Nội 2022 - Hanoi Basketball Championship 2022 (HBC 2022) được Liên đoàn Bóng rổ Hà Nội, Truyền hình Cáp Việt Nam (VTVcab) và Công ty Cổ phần New Sports phối hợp tổ chức với mục tiêu thúc đẩy phong trào cộng đồng bóng rổ Thủ đô nói riêng và bóng rổ Việt Nam nói chung, đẩy mạnh phong trào tập luyện và thi đấu bóng rổ dành cho các cầu thủ cấp độ bán chuyên và tiệm cận chuyên nghiệp trên địa bàn Hà Nội.\r\n\r\nGiải đấu sẽ diễn ra từ ngày 21/09/2022 - 02/10/2022 tại Nhà thi đấu huyện Thanh Trì (673 Ngọc Hồi, Văn Điển, Thanh Trì, Hà Nội) và được trực tiếp trên hệ sinh thái thể thao của VTVcab (Kênh ON Sports; App: ON, ON Sports TV; Facebook: ON Sports; Youtube: ON Bóng rổ).\r\n\r\nGiải đấu quy tụ 12 đội bóng đến từ Thủ đô gồm 08 đội nam và 04 đội nữ, chia làm 2 bảng A-B với tổng giá trị giải thưởng lên tới hơn 150 triệu đồng ngay mùa giải đầu tiên và hứa hẹn nâng cao các mức thưởng trong các mùa giải tiếp theo. Để tăng chất lượng của giải, Ban tổ chức cho phép mỗi đội được đăng ký không giới hạn các vận động viên là người gốc Việt Nam (Việt kiều) và 02 vận động viên người nước ngoài đang học tập, làm việc và sinh sống tại Việt Nam. Các đội được sử dụng không giới hạn các cầu thủ người gốc Việt và chỉ sử dụng 01 vận động viên nước ngoài thi đấu trên sân.\r\n\r\nGiải đấu áp dụng thể thức thi đấu vòng tròn tính điểm tại mỗi bảng. Trường hợp nếu có 02 đội cùng số điểm xếp hạng giống nhau sẽ áp dụng tính chất đối đầu trực tiếp để xếp hạng. Mỗi ngày tối thiểu diễn ra 02 trận đấu, bắt đầu từ: 18h00 tới 22h00.\r\n\r\nĐặc biệt, lần đầu tiên trong các trận đấu của một giải bóng rổ tại Việt Nam sẽ có các màn vũ đạo náo nhiệt, chuyên nghiệp đến từ các dancer tên tuổi của chương trình Street Dance Việt Nam – Đây chính là nhảy đường phố, hứa hẹn sẽ góp thêm tính hấp dẫn của từng trận đấu.\r\n\r\nGiải bóng rổ vô địch Hà Nội 2022 là giải bóng rổ bán chuyên nghiệp có quy mô lớn nhất và lần đầu tiên tổ chức tại Việt Nam theo fomat mới với chủ đề “Beyond the game - Hơn cả một trận đấu” nhằm lan tỏa văn hóa bóng rổ hiện đại, năng động, truyền cảm hứng lối sống tích cực đến thế hệ trẻ và nâng cao sức khỏe cộng đồng. Các cầu thủ không chuyên sẽ có cơ hội cọ xát với các cầu thủ chuyên nghiệp để nâng cao chất lượng chuyên môn, rèn luyện thể chất ngày một tiến bộ và mở ra nhiều cơ hội trong tương lai.', 'https://onsports.vn/post/giai-bong-ro-vo-dich-ha-noi-khoi-tranh-tu-ngay-219-tren-vtvcab', 'https://stvinaprod.vtvcab.vn/c05ed948-4e22-47f3-98d6-19ee78d44f24.jpg', 1, 1, 2, 'giai-bong-ro-vo-dich-ha-noi-khoi-tranh-tu-ngay-219-tren-vtvcab', 1, 1),
(38, 'MU đồng ý trả lương và lót tay cực khủng cho Harry Kane', '2023-04-25', 'Man Utd sẵn sàng biến Harry Kane trở thành cầu thủ có mức thù lao cao nhất tại Old Trafford nếu như anh đồng ý khoác áo \"Quỷ đỏ\".', 'MU đang thực sự rất cần một trung phong chất lượng trong đội hình, và đội bóng chủ sân Old Trafford không giấu giếm mục tiêu có được sự phục vụ của Harry Kane, tiền đạo đang khoác áo Tottenham.\r\n\r\nTruyền thông Anh cho biết, để nhanh chóng có được cái gật đầu của Kane, MU sẵn sàng tăng lương gấp rưỡi cho tiền đạo này, từ mức 200.000 bảng/tuần lên 300.000 bảng/tuần.\r\n\r\nVới việc De Gea bắt buộc phải giảm lương để được tiếp tục khoác áo \"Quỷ đỏ\", Harry Kane sẽ trở thành cầu thủ lương cao nhất nếu gia nhập MU.\r\n\r\nHiện ban lãnh đạo của MU đang rất tự tin vào khả năng thành công của thương vụ này, đặc biệt là khi Tottenham đang trải qua một mùa giải tồi tệ tiếp theo. Họ thậm chí còn đứng trước nguy cơ mất vé dự Champions League mùa tới.\r\n\r\nBản hợp đồng hiện tại của Harry Kane và Spurs sẽ hết hạn vào tháng 6/2024. Lúc này, hai bên đang không tìm được tiếng nói chung cho một bản giao kèo mới.\r\n\r\nTottenham cũng đang cân nhắc việc bán Kane, vì họ có thể thu về một khoản tiền để tái đầu tư. Tuy nhiên, Chủ tịch Daniel Levy của CLB cho biết ông sẽ bán giá cao hơn đối với các đối thủ cạnh tranh tại Premier League.\r\n\r\nCụ thể, nếu Real Madrid hay Bayern Munich muốn chiêu mộ Kane thì chỉ cần bỏ ra 80 triệu bảng. Trong khi MU sẽ phải móc hầu bao 100 triệu bảng nếu muốn sở hữu được anh.\r\n\r\nMặc dù mức phí chuyển nhượng cũng như tiền lương cho một cầu thủ sắp bước sang tuổi 30 như Kane được coi là rất lớn, Quỷ đỏ vẫn quyết tâm theo thương vụ này đến cùng. Bởi lẽ, Harry Kane là một cây săn bàn đã khẳng định được đẳng cấp và cũng rất giàu kinh nghiệm tại Ngoại hạng Anh.', 'https://onsports.vn/post/mu-dong-y-tra-luong-va-lot-tay-cuc-khung-cho-harry-kane', 'https://stvinaprod.vtvcab.vn/d5cd6bf5-fd5d-4f32-8827-4dd187ee0c77.jpg', 2, 2, 1, 'mu-dong-y-tra-luong-va-lot-tay-cuc-khung-cho-harry-kane', 1, 1),
(39, 'Chủ tịch Chelsea nổi cơn thịnh nộ trong phòng thay đồ', '2023-04-18', 'Tờ Sun Sport cho biết, tỷ phú Mỹ Todd Boehly đã vào thẳng phòng thay đồ và nổi cơn thịnh nộ với dàn sao Chelsea sau trận thua Brighton trên sân nhà cuối tuần vừa rồi.', 'Trong cái nhìn của giới truyền thông cũng như các cổ động viên, Todd Boehly - ông chủ hiện tại của Chelsea - là một người đàn ông xởi lởi, vui vẻ và dễ gần. Tuy nhiên, phong độ bế bát của The Blues thời gian gần đây đã khiến cho tỷ phú nước Mỹ trở thành một người cộc cằn và khó gần hơn.\r\n\r\nSau khi HLV Potter bị sa thải vì thành tích không tốt, Frank Lampard trở thành HLV tạm quyền của Chelsea. Tuy nhiên đội bóng thành London vẫn chưa khởi sắc hơn khi để thua 3 trận gần nhất. Cuối tuần qua, Chelsea thua ngược Brighton ngay trên sân nhà. Trước đó, đội bóng áo xanh đã để thua khi làm khách trước Wolves và Real Madrid.\r\n\r\nTheo Sun Sport, kết quả trên khiến cho ông chủ Todd Boehly vô cùng tức giận. Sau khi lời qua tiếng lại với một số CĐV trên khán đài sân Stamford Bridge, tỷ phú Mỹ cùng các đồng sở hữu đã đi thẳng vào phòng thay đồ của đội bóng để chỉ trích các cầu thủ.\r\n\r\nCuộc nói chuyện đã diễn ra khá căng thẳng khi tỷ phú nước Mỹ liên tục dùng cụm từ \"đáng xấu hổ\" để nhấn mạnh kết quả bết bát của toàn đội trong thời gian qua, dù BLĐ đã bỏ ra số tiền khổng lồ để cải thiện lực lượng. Tuy nhiên tờ báo nước Anh cũng tiết lộ, sau cuộc nói chuyện, Todd Boehly vẫn dành những lời động viên dàn sao Chelsea trước trận tứ kết lượt về Champions League với Real Madrid vào rạng sáng 19/4.\r\n\r\nỞ lượt đi, Chelsea đã thua 0-2 tại Bernabeu. Về mặt tỷ số, mọi chuyện dường như vẫn chưa quá tồi tệ với The Blues, nhưng với chênh lệch về phong độ hiện tại, đội bóng áo xanh được dự báo là \"lành ít dữ nhiều\" trong trận lượt về.', 'https://onsports.vn/post/chu-tich-chelsea-noi-con-thinh-no-trong-phong-thay-do', 'https://stvinaprod.vtvcab.vn/4d11a99f-0217-44b3-923f-503caee915b8.jpg', 2, 2, 1, 'chu-tich-chelsea-noi-con-thinh-no-trong-phong-thay-do', 1, 1),
(40, 'BTC Ngoại hạng Anh tổ chức thêm giải đấu vào mùa Hè', '2023-04-11', 'Nhằm tăng sức hấp dẫn của bóng đá trên toàn cầu, BTC Ngoại hạng Anh sẽ tổ chức một giải đấu trên đất Mỹ vào cuối tháng 7, với sự góp mặt của Chelsea, Newcastle, Brentford...', 'Theo Daily Mail, giải đấu mùa hè \"Summer Series\" sẽ có sự tham dự của các CLB gồm Chelsea, Newcastle, Brighton, Brentford và Aston Villa. Ngoài ra, nếu như Leeds có thể trụ lại Premier League mùa tới thì CLB này cũng sẽ góp mặt.\r\n\r\nÝ tưởng tổ chức một \"Ngoại hạng Anh thu nhỏ\" bên ngoài nước Anh được lấy cảm hứng từ các giải đấu như NBA, NFL với các trận đấu được diễn ra khắp châu Âu như Paris, London, Munich,... Premier League muốn lan toả hình ảnh giải đấu hấp dẫn nhất nước Anh với khán giả toàn cầu.\r\n\r\nTrong những năm qua, các đội bóng Anh thường xuyên đến Mỹ để du đấu trong mùa hè. Xứ sở cờ hoa cũng là thị trường tiềm năng mà BTC Premier League muốn khai thác mạnh mẽ hơn trong tương lai.\r\n\r\nỞ mùa này, Aston Villa, Brighton, Brentford đều có phong độ cao và đang ở nửa trên bảng xếp hạng, thậm chí còn nằm trong nhóm cạnh tranh suất tham dự cúp châu Âu 2023/24. Trong khi đó, Chelsea đang gặp rất nhiều vấn đề và chật vật ở Ngoại hạng Anh, nhưng họ vẫn là một CLB lớn với lượng người hâm mộ đông đảo trên toàn thế giới.\r\n\r\nVề phía Leeds, đội bóng chủ sân Elland Road đang đứng thứ 16 trên BXH và chỉ hơn 2 điểm so với nhóm \"cầm đèn đỏ\". Phong độ của CLB này cũng đang ở mức đáng báo động, khi họ vừa bị Crystal Palace vùi dập với tỷ số 1-5 ngay trên sân nhà trong trận đấu cuối tuần qua.\r\n\r\nMan United sẽ không tham gia giải đấu hè của Premier League tổ chức vì thầy trò HLV Ten Hag đã có lịch thi đấu riêng, cũng trên đất Mỹ. Ngày 30/7, MU sẽ đối đầu với Dortmund tại Las Vegas. Trước đó 5 ngày, tại San Diego, \"Quỷ đỏ\" sẽ chạm trán Wrexham, CLB đang thuộc quyền sở hữu của tài tử Ryan Reynolds.', 'https://onsports.vn/post/btc-ngoai-hang-anh-to-chuc-them-giai-dau-vao-mua-he', 'https://stvinaprod.vtvcab.vn/083aaf3e-3775-49e8-bbde-dd9259a00570.jpg', 2, 2, 1, 'btc-ngoai-hang-anh-to-chuc-them-giai-dau-vao-mua-he', 1, 1),
(41, 'Trọng tài đánh cùi chỏ vào mặt hậu vệ Liverpool đối diện án phạt nặng', '2023-04-10', 'Trợ lý trọng tài Hatzidakis có hành vi gây tranh cãi khi đánh cùi chỏ vào mặt hậu vệ Andy Robertson của Liverpool trong trận cầu tâm điểm Ngoại hạng Anh đêm 9/4.', 'Ngoài những pha bóng hấp dẫn, trận Liverpool hòa Arsenal còn có một khoảnh khắc diễn ra cuối hiệp 1 khiến cho giới hâm mộ túc cầu phải xôn xao bàn tán. Cụ thể, đó là tình huống trọng tài biên Constantine Hatzidakis vung cùi chỏ vào mặt Andy Robertson của Liverpool khi cầu thủ này áp sát để phàn nàn điều gì đó. Sau đó, hậu vệ của Liverpool bị phạt thẻ vàng vì lỗi phản ứng.\r\n\r\nTình huống này bị ống kính máy quay ghi lại trọn vẹn. Tất cả các khán giả đều chứng kiến được. Cơ quan quản lý trọng tài Anh (PGMOL) ngay sau đó đã lên tiếng: “Chúng tôi đã biết về tình huống diễn ra giữa trợ lý trọng tài Constantine Hatzidakis và Andy Robertson. Toàn bộ vấn đề sẽ được xem xét cẩn thận khi trận đấu kết thúc”.\r\n\r\nBình luận viên Gary Neville nhận xét: “Tôi chưa bao giờ thấy trọng tài có hành vi như vậy với một cầu thủ. Tôi nghĩ ông ấy sẽ gặp rắc rối lớn sau trận này”.\r\n\r\nTờ Daily Mail viết: “Rõ ràng là Robertson không nên tranh luận với các trọng tài. Anh ấy đã vượt qua giới hạn về quyền của một cầu thủ với người điều hành trận đấu, đặc biệt là việc tiếp xúc cơ thể. Tuy nhiên, Hatzidakis cũng cần phải kiềm chế bởi đó là một phần công việc của ông ấy”.\r\n\r\nCũng theo Daily Mail, nhiều khả năng trọng tài Hatzidakis sẽ phải nhận án phạt rất nặng. Bình luận viên Dominic King cho rằng vị trợ lý trọng tài này có thể bị đình chỉ hết mùa giải.\r\n\r\nTrận đấu giữa Liverpool và Arsenal sau đó đã kết thúc với tỉ số hòa 2-2. Trên thánh địa Anfield của đối thủ, Arsenal nhập cuộc với tinh thần rất cao và dẫn trước 2 bàn do công của Martinelli và Jesus. Tuy nhiên sau đó Liverpool gỡ hòa với các bàn thắng của Salah và Firmino, đồng thời khiến các cổ động viên của Arsenal phải thót tim những phút cuối trận. 1 điểm có được giúp Liverpool vươn lên thứ tám với 44 điểm. Trong khi đó, Arsenal giờ đây chỉ còn hơn 6 điểm so với đội xếp thứ 2 là Man City, mà còn thi đấu nhiều hơn một trận.', 'https://onsports.vn/post/trong-tai-danh-cui-cho-vao-mat-hau-ve-liverpool-doi-dien-an-phat-nang', 'https://stvinaprod.vtvcab.vn/4ca2831e-7953-4435-92a9-51b4ba6a06d8.jpg', 2, 2, 1, 'trong-tai-danh-cui-cho-vao-mat-hau-ve-liverpool-doi-dien-an-phat-nang', 1, 1),
(42, 'HLV Erik Ten Hag bị chỉ trích vì thiên vị trò cưng', '2023-04-04', 'Bình luận viên nổi tiếng Richard Keys mới đây đã lên tiếng chỉ trích HLV Ten Hag vì HLV này đang quá ưu ái Wout Weghorst, dù cầu thủ này chẳng đóng góp được gì cho Quỷ đỏ.', 'Sau khi nói lời chia tay với siêu sao Ronaldo, MU đã tìm một sự thay thế trên hàng tiền đạo và họ đã mượn Weghorst từ Burnley. Tuy nhiên, kể từ khi chuyển tới Old Trafford hồi tháng Giêng, tiền đạo người Hà Lan vẫn chưa ghi nổi bàn nào, dù anh đã có 9 lần ra sân ở Premier League. Đỉnh điểm ở trận MU để thua Newcastle 0-2 cuối tuần qua, Weghorst đã có màn trình diễn khá tệ. Anh bị các cổ động viên của MU chê \"chân gỗ\" muốn đẩy cầu thủ này trở lại đội bóng cũ khi mùa giải kết thúc.\r\n\r\nRichard Keys - người từng dẫn chương trình cho nhiều kênh truyền hình nổi tiếng như BBC, Fox Sports, Sky, ITV, Channel 4, Talksport, Al Jazeera, ESPN Star Sports và BeIN Sports, cũng có quan điểm tương tự. Keys viết trên blogs cá nhân: \"Tôi không phủ nhận rằng MU đã có sự tiến bộ, nhưng họ vẫn chưa đạt đến đẳng cấp của một nhà vô địch\".\r\n\r\n\"Với Nout Workhorse (Ám chỉ Weghorst như một chú ngựa vô dụng) trong đội hình, MU sẽ chẳng làm nên trò trống gì cả. Nghiêm túc mà nói, tôi chẳng hiểu tại sao anh ta lại được sử dụng. Có người bảo rằng, Weghorst phải rất hữu ích cho chiến thuật thì HLV mới thường xuyên cho đá chính? Nhưng hữu ích ở chỗ nào vậy?\".\r\n\r\n\"Đừng nói với tôi rằng Weghorst là mẫu tiền đạo có khả năng tạo sức ép khủng khiếp cho đối phương hay hỗ trợ phòng ngự tuyệt vời. Tôi chỉ thấy HLV Ten Hag đang cố gắng tâng bốc cầu thủ đồng hương để biện minh cho kế hoạch mua đứt anh ta mà thôi.\"\r\n\r\nRichard Keys tiếp tục mỉa mai: \"HLV Ten Hag nói rằng, Weghorst đã tạo ra khoảng trống cho các đồng đội. Nhưng theo tôi, nếu như anh ta không ra sân có khi còn tạo ra nhiều khoảng trống hơn. Rõ ràng, Weghorst là một trung phong, nhưng lại không ghi bàn và cũng chẳng đủ đẳng cấp để đá cho MU\".', 'https://onsports.vn/post/hlv-erik-ten-hag-bi-chi-trich-vi-thien-vi-tro-cung', 'https://stvinaprod.vtvcab.vn/bf381207-fb0d-49e0-8015-60ca819f80ac.jpeg', 2, 2, 1, 'hlv-erik-ten-hag-bi-chi-trich-vi-thien-vi-tro-cung', 1, 1),
(43, 'Everton nguy cơ bị trừ điểm, xuống hạng vì vi phạm Luật công bằng tài chính', '2023-04-01', 'Everton đối mặt với việc bị trừ điểm nặng sau khi bị cáo buộc vi phạm các quy tắc của Luật công bằng tài chính (FFP). BTC Ngoại hạng Anh đã vào cuộc và tuyên bố sẽ giải quyết vụ việc này trước khi mùa giải 2022/23 kết thúc', 'Với cục diện hiện tại, bất kỳ quyết định trừ điểm nào của BTC Ngoại hạng Anh cũng sẽ đẩy Everton xuống hạng nhanh hơn. Sau vòng 28, Everton mới chỉ có 26 điểm. Dù đứng thứ 15 nhưng họ cũng chỉ hơn nhóm ‘cầm đèn đỏ’ 2 điểm, trong khi đó những CLB kia vẫn còn vài trận chưa đấu. Mặc dù thành tích của Everton đã có sự cải thiện sau khi sa thải Frank Lampard và bổ nhiệm Sean Dyche, tuy nhiên họ cũng chưa thể ngay lập tức leo lên vị trí cao hơn.\r\n\r\nEverton hiện đang đứng trước nguy cơ bị trừ điểm rất nặng vì họ bị Premier League cáo buộc vi phạm Luật công bằng tài chính (FFP). Đội bóng này bị đặt vào tầm ngắm vì có dấu hiệu không minh bạch khi mang về tới 11 cầu thủ mới trong 10 tháng qua. Ngoài ra, trong khoảng 3 năm qua họ ghi nhận khoản lỗ 371 triệu bảng, vượt quá giới hạn cho phép là 105 triệu bảng.\r\n\r\nĐã có 6 bức thư được các CLB gửi cho ban tổ chức, yêu cầu vụ việc của Everton được xử lý minh bạch hơn và phải được kết thúc trong mùa giải này. Premier League đã thành lập một Ủy ban độc lập để điều tra và phân tích tình hình tài chính của Everton. Nếu uỷ ban này kết tội Everton, đội bóng này sẽ bị trừ điểm và gần như chắc chắn phải xuống hạng, bởi mùa giải hiện tại chỉ còn lại 10 vòng đấu nữa.\r\n\r\nPhía BTC Premier League tin tưởng vụ việc có thể được giải quyết trước ngày 28/5, thời điểm giải đấu hạ màn. Cuối tuần này, Everton sẽ mang một tâm trạng nặng nề trong cuộc đụng độ Tottenham.', 'https://onsports.vn/post/everton-nguy-co-bi-tru-diem-xuong-hang-vi-vi-pham-luat-cong-bang-tai-chinh', 'https://stvinaprod.vtvcab.vn/3bdcceba-2336-4b94-aaac-05f3da411ae0.jpeg', 2, 2, 1, 'everton-nguy-co-bi-tru-diem-xuong-hang-vi-vi-pham-luat-cong-bang-tai-chinh', 1, 1),
(44, 'Rashford ra điều kiện ký hợp đồng mới với MU', '2023-03-25', 'Tiền đạo Marcus Rashford sẽ đợi cho đến khi câu chuyện tiếp quản MU kết thúc, trước khi đưa ra bất kỳ quyết định nào về hợp đồng mới.', 'Rashford hiện đang có một mùa giải tuyệt vời dưới triều đại của HLV Erik ten Hag. Chân sút 25 tuổi đã có 27 pha lập công sau 44 trận ở mùa giải này, và hướng đến mùa giải ghi bàn tốt nhất từ trước đến nay.\r\n\r\nTuy nhiên, hợp đồng của Rashford với Man Utd chỉ còn 12 tháng cuối mùa giải hiện tại. Vì thế nên ban lãnh đạo đội bóng chủ sân Old Trafford đang cố gắng giữ chân tiền đạo người Anh với các điều khoản mới. Nhưng Rashford cho biết anh chỉ sẵn sàng ngồi vào bàn đám phán khi nào tương lai lâu dài của CLB rõ ràng.\r\n\r\nTờ The Sun đưa tin, trước khi Rashford quyết định về tương lai, anh và các cố vấn của anh muốn biết mức lương mà chủ mới của MU sẵn sàng trả cho mình.\r\n\r\nTrong cuộc đua giành MU từ tay gia đình Glazer, hiện tỷ phú giàu nhất nước Anh Sir Jim Ratcliffe và hoàng thân Qatar Sheikh Jassim bin Hamad Al Thani là 2 ứng viên hàng đầu.\r\n\r\nMặc dù Rashford đang bật tín hiệu cho việc ở lại MU, nhưng vẫn còn phải chờ xem liệu CLB có sẵn sàng trả mức lương 300.000 bảng/tuần theo như dự đoán không. Trên thực tế, tiền đạo người Anh đang nhận mức 200.000 bảng/tuần. Con số 300.000 bảng là một mức cao hơn đáng kể.\r\n\r\nTheo một số nguồn tin, hồi đầu năm nay một số thành viên trong ban lãnh đạo \"Quỷ đỏ\" gồm HLV Ten Hag, giám đốc bóng đá John Murtough và giám đốc điều hành Richard Arnold đã đặt ra quy tắc \"Cristiano Ronaldo\" sau khi CR7 ra đi.\r\n\r\nĐó là quy tắc nhằm giới hạn tiền lương, qua đó hạn chế tình trạng bất ổn trong phòng thay đồ khi đề cập đến vấn đề tiền lương. Trước khi Ronaldo bị chấm dứt hợp đồng, anh được cho là đã bỏ túi khoảng 480.000 bảng mỗi tuần tại Old Trafford.\r\n\r\nNếu như Man Utd và Rashford không đạt được những thoả thuận về tiền lương, rất có thể đội bóng chủ sân Old Trafford sẽ đánh mất ngôi sao số 1 của mình. Hiện tại, hàng loạt ông lớn tại châu Âu đang muốn chiêu mộ Rashford.', 'https://onsports.vn/post/rashford-ra-dieu-kien-ky-hop-dong-moi-voi-mu', 'https://stvinaprod.vtvcab.vn/09ca9217-85e6-46ff-a530-c7647b0273cd.jpeg', 2, 2, 1, 'rashford-ra-dieu-kien-ky-hop-dong-moi-voi-mu', 1, 1),
(45, 'Lộ diện thế lực mới muốn mua MU', '2023-03-24', 'Bên cạnh hai nhà đầu tư khác là tỷ phú Sir Jim Ratcliffe và hoàng thân Sheikh Jassim bin Hamad al-Thani, doanh nhân Thomas Zilliacus là thế lực tiếp theo gửi đề nghị mua lại MU.', 'Cuộc đua kịch tính để mua lại MU đã có bước ngoặt mới khi ông Thomas Zilliacus, doanh nhân người Phần Lan và Chủ tịch của Tập đoàn novaM, đưa ra kế hoạch kêu gọi CĐV MU góp tiền mua lại đội bóng. Thomas Zilliacus đưa ra đề nghị: \"Giá trị thị trường của MU chỉ dưới 3,9 tỷ USD (3,18 tỷ bảng). Có nghĩa là nếu tất cả các CĐV của câu lạc bộ muốn góp sức mua lại đội, mỗi người sẽ bỏ ra dưới 6 USD (4,8 bảng). Nhóm của tôi sẽ tài trợ một nửa số tiền cần thiết để tiếp quản CLB, đồng thời kêu gọi các fan đóng góp nửa còn lại. Nếu tất cả cùng tham gia thì khi đó, mỗi người chỉ phải đóng góp 3 USD (2,44 bảng). Điều đó là sự công bằng với chính các CĐV\".\r\n\r\nÔng Zilliacus hứa hẹn rằng nếu như thành công mua được MU, ban lãnh đạo \"Quỷ đỏ\" sẽ thông qua ứng dụng riêng của CLB để lắng nghe đóng góp của CĐV. Tất cả người hâm mộ đều có thể lên tiếng để giúp phát triển CLB\r\n\r\nTrước khi được được lên giữ chức Giám đốc Kinh doanh khu vực châu Á - Thái Bình Dương, doanh nhân Zilliacus từng làm Giám đốc Truyền thông của Nokia ở Singapore. Ở khía cạnh thể thao, ông Zilliacus cũng là người có kinh nghiệm khi từng là chủ tịch đội bóng quê nhà giai đoạn 1982-1986. Ông cũng sở hữu một CLB hockey hàng đầu Phần Lan trước khi đội bị bán vào năm 2012 cho một nhà đầu tư Nga.\r\n\r\nBầu Trời Indonesia đỏ Ngầu\r\nMặc dù khối tài sản mà ông Zilliacus đang sở hữu không được tiết lộ trước truyền thông, nhưng Mirror cho rằng nguồn lực của doanh nhân người Phần Lan không mạnh hơn hai đối thủ cạnh tranh Sir Jim Ratcliffe và Sheikh Jassim bin Hamad al-Thani.\r\n\r\nHôm 23/3, hai nhà đầu tư nói trên đều trì hoãn việc ra giá lần thứ hai để mua lại MU. Hạn chót để các bên ra giá mua lại MU chưa được ấn định lại, song tỷ phú Ratcliffe dự kiến đưa ra giá mới trước cuối tuần này.\r\n\r\nỞ lần đấu thầu đầu tiên, Times cho biết mức giá cao nhất được đưa ra là 4,5 tỷ bảng. Mặc dù ban đầu nhà Glazer muốn bán CLB với giá khoảng 6 tỷ bảng. Tuy nhiên các nguồn tin nội bộ cho biết, khó có chuyện Ratcliffe hay phía Qatar đáp ứng mức giá trên trời mà giới chủ của \"Quỷ đỏ\" mong muốn.', 'https://onsports.vn/post/lo-dien-the-luc-moi-muon-mua-mu', 'https://stvinaprod.vtvcab.vn/6e1203e9-17e7-4f82-bb90-db139913a646.jpg', 2, 2, 1, 'lo-dien-the-luc-moi-muon-mua-mu', 1, 1),
(46, 'Tottenham mở đường cho Harry Kane gia nhập MU', '2023-03-21', 'Tottenham sẽ chấp nhận bán Harry Kane cho MU vào mùa hè tới nếu nhận được 100 triệu bảng trả trước, ngoài ra còn kèm theo phụ phí.', 'Harry Kane sắp bước sang tuổi 30 mà chưa có một danh hiệu lớn nào. Chính vì thế, tiền đạo người Anh không muốn tiếp tục gia hạn với Tottenham mà muốn tìm một bến đỗ mới.\r\n\r\nTrong suốt 9 năm qua, Kane giữ phong độ tốt, có thành tích ghi bàn đáng nể, nhưng số danh hiệu trong sự nghiệp của anh vẫn là con số 0 tròn chĩnh. Đó cũng là lý do khiến anh nhất quyết rời Tottenham. Trước đó, Kane đã cố gắng gia nhập Man City vào mùa hè 2021 nhưng không thành công. Đến mùa hè năm ngoái, nhà đương kim vô địch Anh lại chọn mua Erling Haaland, khiến cho hy vọng cập bến Etihad của Kane cũng bị dập tắt.\r\n\r\nVới tình hình hiện tại, các chuyên gia cho rằng, nếu muốn có danh hiệu tại Anh, Kane chỉ còn đường gia nhập MU, bằng không anh sẽ phải ra nước ngoài thi đấu. Bởi lẽ Tottenham chắc chắn sẽ không bán tiền đạo số 1 của họ cho các kình địch ở London là Arsenal và Chelsea. Trong khi đó, Man City đã có Haaland, còn Liverpool thì cũng đã sở hữu tiền đạo đắt giá Darwin Nunez.\r\n\r\nTheo The Times, nếu Kane kiên quyết từ chối gia hạn hợp đồng, Tottenham sẽ xem xét nghiêm túc việc rao bán anh cho MU. Hợp đồng hiện tại của Kane với Tottenham chỉ còn thời hạn đến tháng 6/2024, tức hơn 1 năm. Tuy nhiên CLB chủ quản cũng sẽ không để Kane ra đi với giá rẻ. Đội bóng áo trắng đã đưa ra con số 100 triệu bảng chưa tính phụ phí.\r\n\r\nĐiều này có nghĩa là nếu muốn sở hữu Kane, MU phải trả đầy đủ 100 triệu bảng, thậm chí sẽ phải trả thêm các phụ phí tùy theo thành tích của Kane tại Old Trafford.\r\n\r\nĐược biết, HLV Erik ten Hag đang nhắm Kane cho vị trí trung phong ở MU. Ngoài ra, HLV người Hà Lan cũng có mục tiêu khác là Victor Osimhen của Napoli, nhưng khả năng MU mua được tiền đạo này là rất thấp.', 'https://onsports.vn/post/tottenham-mo-duong-cho-harry-kane-gia-nhap-mu', 'https://stvinaprod.vtvcab.vn/4b52a973-1a60-4d3f-ba31-a64a1c0209fc.jpg', 2, 2, 1, 'tottenham-mo-duong-cho-harry-kane-gia-nhap-mu', 1, 1),
(47, 'Arsenal giành cú ăn 4 giải thưởng khi mùa giải chưa kết thúc', '2023-03-14', 'HLV Mikel Arteta của Arsenal đã vượt qua những đồng nghiệp như Richie Wellen, Emma Hayes, Thomas Frank hay Marco Silva, để vinh dự giành giải nhà cầm quân hay nhất năm ở London.', 'Trong buổi gala trao giải, HLV Arteta đã dành lời phát biểu để cảm ơn các cộng sự và cầu thủ Arsenal, những người đã đồng hành cùng ông thời gian qua. Theo HLV Arteta, thành công mà ông đang có được nhờ vào công sức rất lớn của họ.\r\n\r\nNgoài HLV Arteta, còn có ba thành viên khác của Arsenal cũng được vinh danh vào tối qua. Ở hạng mục dành cho cầu thủ hay nhất năm ở Premier League đang chơi bóng tại London, tiền vệ Martin Odegaard đã giành chiến thắng với số phiếu bầu cao hơn các ứng cử viên khác như Harry Kane, Bukayo Saka, Alexsandar Mitrovic và Ivan Toney.\r\n\r\nĐối với hạng mục cầu thủ trẻ xuất sắc nhất năm, Bukayo Saka là người thắng cuộc. Còn danh hiệu \"người gác đền số 1 ở London\" cũng được trao cho thủ môn Aaron Ramsdale của Arsenal.\r\n\r\nTrở lại với HLV Arteta, mới cách đây ít ngày ông đã được ban tổ chức Ngoại hạng Anh trao tặng danh hiệu HLV xuất sắc nhất trong tháng 1. Kể từ đầu mùa giải, thuyền trưởng của Arsenal đã vinh dự được nhận danh hiệu này 3 lần. Qua đó, ông cũng lập kỷ lục khi trở thành HLV đầu tiên trong lịch sử Arsenal được 3 lần bầu là HLV hay nhất tháng tại Premier League trong một mùa.\r\n\r\nMùa giải 2022/2023 cũng là mùa giải thứ tư mà Arteta dẫn dắt \"Pháo thủ thành London\". Ở mùa giải năm nay, nhà cầm quân 40 tuổi đang cùng với Arsenal nắm giữ nhiều lợi thế trên hành trình vô địch Premier League. Sau 27 vòng, Arsenal đã giành được 66 điểm, nhiều hơn 5 điểm so với đội xếp thứ 2 là Man City, nhà đương kim vô địch giải đấu.', 'https://onsports.vn/post/arsenal-gianh-cu-an-4-giai-thuong-khi-mua-giai-chua-ket-thuc', 'https://stvinaprod.vtvcab.vn/2aad31a9-c691-4284-b809-b4ada8adae0d.jpg', 2, 2, 1, 'arsenal-gianh-cu-an-4-giai-thuong-khi-mua-giai-chua-ket-thuc', 1, 1);
INSERT INTO `posts` (`id`, `title`, `date`, `description`, `content`, `link_post`, `image`, `country_id`, `category_id`, `sport_id`, `slug`, `hot_news`, `status`) VALUES
(48, 'MU chuẩn bị nổ \"bom tấn\" mua tiền đạo trị giá 100 triệu Euro', '2023-03-14', 'Tạp chí FourFourTwo đưa tin, MU đang tiến hành đàm phán để chiêu mộ tiền đạo Goncalo Ramos của Benfica trong kỳ chuyển nhượng Hè 2023.', 'Việc chấm dứt hợp đồng với Cristiano Ronaldo hồi tháng 11/2022 khiến MU phải tìm một chân sút thay thế. Sau đó, đội chủ sân Old Trafford đã quyết định mượn Wout Weghorst từ Burnley. Tuy nhiên, tính đến nay, sau 16 lần ra sân trên mọi đấu trường Weghorst mới chỉ có vỏn vẹn 2 bàn thắng và chưa thể đáp ứng được sự kỳ vọng.\r\n\r\nGần như chắc chắn, MU sẽ không giữ lại Weghorst sau khi mùa giải 2022/23 kết thúc. Bởi vậy, HLV Ten Hag đã bắt đầu lên kế hoạch chiêu mộ một tiền đạo đẳng cấp để cùng sát cánh và san sẻ nhiệm vụ ghi bàn với Marcus Rashford ở mùa giải năm sau.\r\n\r\nGần như chắc chắn, MU sẽ không giữ lại Weghorst sau khi mùa giải 2022/23 kết thúc. Bởi vậy, HLV Ten Hag đã bắt đầu lên kế hoạch chiêu mộ một tiền đạo đẳng cấp để cùng sát cánh và san sẻ nhiệm vụ ghi bàn với Marcus Rashford ở mùa giải năm sau.', 'https://onsports.vn/post/mu-chuan-bi-no-bom-tan-mua-tien-dao-tri-gia-100-trieu-euro', 'https://stvinaprod.vtvcab.vn/87ffc393-d3f4-4c4b-8582-44c80160d645.jpg', 2, 2, 1, 'mu-chuan-bi-no-bom-tan-mua-tien-dao-tri-gia-100-trieu-euro', 1, 1),
(49, 'Marcus Rashford nhận chỉ trích dữ dội vì pha ăn vạ lộ liễu', '2023-03-13', 'Tiền đạo Marcus Rashford bị chỉ trích nặng nề vì \"ngã vờ thô thiển\" trong trận Man United hoà không bàn thắng với đội bét bảng Southampton.', 'Rashford đáng có một mùa giải 2022/2023 đầy thuận lợi. Chân sút của MU thậm chí còn được nhiều người đánh giá là có phong độ ghi bàn ấn tượng nhất thế giới. Ở trong trận đại thắng Betis ở Europa League gần đây, Rashford cũng đã lập công và nối dài chuỗi thành tích ghi bàn với 26 bàn thắng trên mọi đấu trường cho Man United.\r\n\r\nTuy nhiên, hôm Chủ nhật (12/3) vừa qua, cầu thủ 25 tuổi đã hoàn toàn im hơi lặng tiếng tại Old Trafford, khiến đội nhà bị Southampton cầm hòa 0-0.\r\n\r\nĐó là một ngày tồi tệ với Man Utd khi mắt xích quan trọng hàng tiền vệ là Casemiro phải nhận thẻ đỏ trực tiếp, đồng thời nhận án treo giò 4 trận tiếp theo. Còn Alejandro Garnacho sau trận đấu cũng phải chống nạng.\r\n\r\nThế nhưng, có lẽ Rashford mới là người phải chịu sự hổ thẹn lớn nhất. Từ chỗ là một ứng cử viên tiềm năng của danh hiệu Quả bóng Vàng, cầu thủ này bỗng chốc \"hot\" trên MXH cho đề cử Fallon d\'Floor, giải thưởng dành cho cầu thủ ăn vạ thô thiển nhất. Mặc dù dường như không có bất kỳ va chạm nào với thủ môn của đối thủ, tiền đạo của MU đã đo sàn trong vòng cấm.\r\n\r\nMột người hâm mộ bình luận: \"Hãy nhìn cái cách mà Rashford cố gắng kiếm phạt đền đi, thật đáng hổ thẹn. Đáng lẽ ra trọng tài phải cho cậu ta một tấm thẻ vàng\".\r\n\r\n“Tôi sẽ xếp nó vào một trong những pha ăn vạ thô thiển nhất”, người thứ hai ngán ngẩm.\r\n\r\nNgười thứ ba có lẽ là fan Liverpool hình dung: \" Nếu đây là Mo Salah, chắc giới truyền thông sẽ không để yên\".\r\n\r\nMột ý kiến khác: \"Chẳng có ai lên án điều này ư? Rashford đúng là con cưng của truyền thông\".\r\n\r\n\"Thật không ngờ những cầu thủ ở đẳng cấp thế giới lại làm điều vô nghĩa và đáng hổ thẹn như vậy. Mà chắc là tiến sĩ Rashford sẽ không làm bài luận về điều này đâu\", người thứ năm than vãn.\r\n\r\nMột người khác lên tiếng: \"Cậu ta nghĩ rằng mình sẽ lừa được một người mù chắc? Thành thật mà nói, Rashford đã làm một hành động đáng xấu hổ\".\r\n\r\nTrong khi đó, nhiều CĐV MU lại khẳng định đó là một quả phạt đền: \"Tôi đã thấy đầu gối thủ môn có chạm vào Rashford... dù chỉ chạm nhẹ thì vẫn là penalty. VAR ở đâu?\".\r\n\r\nVà một người khác nói thêm: \"Đáng lẽ Rashford phải được hưởng quả phạt đền. Tất cả chúng tôi đều thấy chân anh ấy bị kẹp\".', 'https://onsports.vn/post/marcus-rashford-nhan-chi-trich-du-doi-vi-pha-an-va-lo-lieu', 'https://stvinaprod.vtvcab.vn/15dbfcd2-1869-459b-85ef-5168f4129fa6.jpg', 2, 2, 1, 'marcus-rashford-nhan-chi-trich-du-doi-vi-pha-an-va-lo-lieu', 1, 1),
(50, 'Andrey Rublev lên ngôi vô địch Monte Carlo Masters', '2023-04-17', 'Ở trận chung kết Monte Carlo Masters tối ngày 16/4, Andrey Rublev xuất sắc đánh bại Holger Rune với tỷ số 2-1 để giành chức vô địch mùa giải 2023.', 'Andrey Rublev đã có một ngày thi đấu lội ngược dòng xuất sắc trên sân Rainier III với 33 điểm winner. Từ set 3 bị dẫn trước 1-4, anh đã thể hiện đẳng cấp của mình khi giành danh hiệu lớn nhất trong sự nghiệp của mình.\r\n\r\nSau chức vô địch Masters 1000 đầu tiên trong sự nghiệp, anh xúc động chia sẻ : \"Tôi rơi nước mắt và không biết nói gì hơn. Tôi cảm thấy rất hạnh phúc. Tôi nỗ lực không ngừng để vô địch một giải Masters 1000. Bị dẫn 1-4, 0/30, rồi cứu break point, tưởng chừng như không còn cơ hội vô địch nhưng bằng cách nào đó tôi đã làm được\".\r\n\r\nSau chức vô địch Masters 1000 đầu tiên trong sự nghiệp, anh xúc động chia sẻ : \"Tôi rơi nước mắt và không biết nói gì hơn. Tôi cảm thấy rất hạnh phúc. Tôi nỗ lực không ngừng để vô địch một giải Masters 1000. Bị dẫn 1-4, 0/30, rồi cứu break point, tưởng chừng như không còn cơ hội vô địch nhưng bằng cách nào đó tôi đã làm được\".', 'https://onsports.vn/post/andrey-rublev-len-ngoi-vo-dich-monte-carlo-masters', 'https://stvinaprod.vtvcab.vn/137606dd-1d21-4fc0-a9e5-aa503c49e1ad.jpg', 3, 2, 3, 'andrey-rublev-len-ngoi-vo-dich-monte-carlo-masters', 1, 1),
(51, 'Giải Quần vợt Vô địch Đồng đội Quốc gia 2023 trực tiếp trên VTVcab', '2023-04-17', 'Giải Quần vợt Vô địch Đồng đội Quốc gia 2023 quy tụ các vận động viên nổi bật, cũng sẽ là các vận động viên tiêu biểu của tuyển quốc gia tham dự SEA Games 32.', 'Giải Quần vợt Vô địch Đồng đội Quốc gia 2023 quy tụ các vận động viên nổi bật, cũng sẽ là các vận động viên tiêu biểu của tuyển quốc gia tham dự SEA Games 32.\r\n\r\nBên cạnh đó, giải còn có sự góp mặt của các câu lạc bộ mới: Nước sạch Hải Dương và chủ nhà Hoài Ân – Thành Công Đắk Lắk hứa hẹn sẽ đến những trận đấu thú vị, hấp dẫn.\r\n\r\nĐây là các vận động viên hứa hẹn đem về các thành tích tốt cho các câu lạc bộ, với kì vọng đoàn Quân đội sẽ thi đấu thành công ở nội dung đồng đội nữ và đoàn TP Hồ Chí Minh sẽ thành công ở nội dung đồng đội nam.\r\n\r\nNăm nay, tổng giải thưởng lên đến 150 triệu cho 8 hạng mục thi đấu: Vô địch Đồng đội Nam là 40.000.000 đồng, Vô địch đồng đội Nữ là 30.000.000 đồng, Giải nhì đồng đội Nam là 25.000.000 đồng; Giải nhì đồng đội Nữ là 15.000.000 đồng; Đồng hạng 3 cho cả đồng đội Nam và đồng đội nữ là 10.000.000 đồng.\r\n\r\nCác giải thưởng là nguồn khích lệ quan trọng giúp cho các vận động viên có thêm động lực thi đấu hết mình để nắm lấy cơ hội khoác áo đội tuyển quốc gia tại kỳ SEA Games 32 sắp tới và hướng tới kỳ Á vận hội năm 2023.\r\n\r\nTheo thể thức thi đấu, các đội thi đấu theo thể thức chuyên nghiệp 3 ván thắng 2, căn cứ vào thứ hạng của vận động viên trong đội sẽ thi đấu theo trình tự: Trận đơn thứ nhất: Vận động viên số 2 gặp số 2; Trận đơn thứ hai: Vận động viên số 1 gặp số 1; Trận đôi: được xếp bất kỳ vận động viên nào trong danh sách thi đấu của đội.\r\n\r\nĐối tượng tham dự: Mỗi đơn vị được cử tối đa hai đội nam và hai đội nữ ở mỗi nội dung thi đấu. Mỗi đội được đăng ký tối đa 4 vận động viên và 01 đội trưởng (các vận động viên sinh năm từ 2010 trở về trước và đội trưởng sinh năm 1999 trở về trước). Trong trường hợp đội trưởng thi đấu thì mỗi đội chỉ được đăng ký 3 vận động viên. Vận động viên tham gia thi đấu giải phải có thẻ vận động viên do Liên đoàn Quần Vợt cấp.', 'https://onsports.vn/post/giai-quan-vot-vo-dich-dong-doi-quoc-gia-2023-truc-tiep-tren-vtvcab', 'https://stvinaprod.vtvcab.vn/33313a8f-bf48-4332-908b-d963e1983e66.jpg', 1, 1, 3, 'giai-quan-vot-vo-dich-dong-doi-quoc-gia-2023-truc-tiep-tren-vtvcab', 1, 1),
(52, 'Lý Hoàng Nam quyết tâm bảo vệ HCV ở SEA Games 32', '2023-04-15', 'Sau khoảng thời gian thi đấu quần vợt tại nước ngoài vừa qua, Lý Hoàng Nam đã quay về và luyện tập để chuẩn bị tốt nhất cho SEA Games 32. Tại đây, tay vợt số 1 Việt Nam quyết tâm bảo vệ tấm HCV ở nội dung đơn nam.', 'Lý Hoàng Nam hiện tại đang giữ vị trí 260 trên BXH thế giới. Dường như tại nội dung đơn nam, tay vợt số 1 Việt Nam có thể dễ dàng bảo vệ tấm HCV bởi 2 đối thủ người Thái Lan cũng có thứ hạng cao ở Đông Nam Á là Kasidit Samrej và Palaphoom Kovapitukted lần lượt có thứ hạng 621 ATP và 734 ATP.\r\n\r\nTrong năm 2022, Lý Hoàng Nam gặt hái được nhiều thành tích và được xem là năm đại thành công đối với anh. Với tấm HCV tại SEA Games 31, vô địch M25, \"hat-trick\" M15 ở Tây Ninh, chuỗi 24 trận thắng liên tiếp ở đấu trường quốc tế trong hệ thống chuyên nghiệp ITF World Tennis, tay vợt số 1 Việt Nam thành công đứng hạng 231 ATP. Tuy nhiên, tay vợt người Tây Ninh lại không có được khởi đầu thuận lợi trong năm 2023.\r\n\r\nQuay trở lại SEA Games 32, Lý Hoàng Nam còn được kỳ vọng sẽ kết hợp với Trịnh Linh Giang hay Phạm Minh Tuấn, Nguyễn Văn Phương để giành huy chương. Tuy nhiên với nội dung đôi nam, Đông Nam Á quy tụ rất nhiều tay vợt có tiếng như 2 tay vợt người Philippines: Ruben Gonzales (hạng 119 ATP), Treat Huey (hạng 148 ATP) hay Rungkat người Indonesia (hạng 230 ATP).\r\n\r\nSEA Games 32 sẽ bắt đầu khởi tranh từ ngày 5/5 - 17/5 tại Campuchia với các quốc gia tham dự là Campuchia,Việt Nam, Thái Lan, Malaysia, Indonesia, Philippines, Singapore, Lào, Campuchia, Brunei, Đông Timor.', 'https://onsports.vn/post/ly-hoang-nam-quyet-tam-bao-ve-hcv-o-sea-games-32', 'https://stvinaprod.vtvcab.vn/720c307a-0ce6-48f7-873a-8e3064b3e274.jpg', 1, 1, 3, 'ly-hoang-nam-quyet-tam-bao-ve-hcv-o-sea-games-32', 1, 1),
(53, 'Scandal dàn xếp tỷ số quần vợt khiến thế giới chấn động', '2023-03-21', 'Phiên tòa xét xử dàn xếp tỷ số trong làng tennis ở Bỉ hôm thứ Sáu vừa rồi đã bị tạm hoãn, do có quá tình tiết phức tạp cần phải được mở rộng điều tra và củng cố hồ sơ. Dự kiến phiên tòa sẽ được lùi thời gian xét xử sang ngày 24/3 tới.', 'Đây là một vụ việc gây rúng động làng quần vợt tại Bỉ. Theo hồ sơ lên tới hàng nghìn trang giấy được cảnh sát thu thập từ năm 2014 đến 2018, đã có tới 376 trận tennis ở mọi cấp độ bị dàn xếp tỷ số, với sự tham gia của 182 tay vợt.\r\n\r\nĐáng chú ý, các tay vợt này ngoài công dân Bỉ còn đến từ nhiều quốc gia khác trên thế giới như Pháp, Đức, Mỹ,… Trong đó có những người đã gác vợt, có những người vẫn còn đang thi đấu chuyên nghiệp.\r\n\r\nNhân vật chính của vụ án đang kinh thiên động địa nói trên là Grigor Sargsyan. Người đàn ông 32 tuổi này có biệt danh là “Maestro” - kẻ đóng vai trò kết nối các tay vợt với đường dây tổ chức cá độ tại Bỉ.\r\n\r\nCảnh sát đã bắt đối tượng này hồi tháng 6/2018 ngay tại nhà riêng ở Saint-Gilles, Nam Brussels. Suốt 8 tháng vừa qua, nghi phạm chịu sự quản thúc, đồng thời trong 2 tháng gần gã phải đeo thêm vòng định vị để cảnh sát tiện giám sát.\r\n\r\nHôm thứ Sáu vừa rồi, Sargsyan xuất hiện tại tòa án với dáng vẻ khép nép, sợ sệt. Trước đó, với mong muốn được hưởng sự khoan hồng từ pháp luật, nghi phạm đã phá vỡ \"luật im lặng\" để đưa ra lời khai. “Maestro” khai rằng, tuy vào tính chất của set đấu hay giải đấu, mỗi tay vợt tham gia dàn xếp tỷ số sẽ được trả 400 euro đến 3.000 euro.\r\n\r\nCác vụ dàn xếp tỷ số nói trên chủ yếu diễn ra ở cấp cơ sở - nơi phát hện tại năng tại Challenger và Futures. Nhưng cũng có ngôi sao \"nhúng chàm\". Trong hồ sơ của vụ án, có một tay vợt từng xếp hạng 113 của ATP. Hiện ngôi sao này đã bị cảnh sát triệu tập để điều ta, nhưng danh tính vẫn chưa được tiết lộ.\r\n\r\nDữ liệu thu thập được từ cảnh sát cho biết, đã có 1.671 tài khoản đã tham gia đường dây cá độ trong các trận đấu dàn xếp tỷ số nói trên, với số tiền giao dịch từ 6/2016 đến tháng 3/2018 là 8,2 triệu euro. Tuy nhiên, con số đó mới chỉ là phần nổi của tảng băng chìm. Với thông tin gây sốc này, không chỉ uy tín của quần vợt Bỉ bị ảnh hưởng nghiêm trọng, mà làng banh nỉ Thế giới cũng bị đặt trước tình trạng báo động.\r\n\r\nVấn nạn cá độ và dàn xếp tỷ số ở môn tennis không phải mới. Năm 2018, tay vợt ngưới Áo từng xếp hạng 385 thế giới, Daniel Kollerer đã bị phát hiện tham gia dàn xếp tỷ số. Hay vào 8/2015, 2 tay vợt người Italia Daniele Bracciali và Potito Starace cũng bị phát một đường dây cá độ hồi tháng. Những ngôi sao quần vợt trên đều nhận chung cái kết là bị cấm thi đấu vĩnh viễn. Tuy nhiên, những vụ án nói trên vẫn chưa là gì so với đường dây tại Bỉ.\r\n\r\nNhững năm gần đây, những bê bối về dàn xếp tỷ số ngày càng lan rộng trong thế giới thể thao. Các nhóm thế lực ngầm xuyên quốc gia đã vươn vòi bạch tuộc từ bóng đá đến bóng rổ và gần đây là môn thể thao quý tộc tennis. Theo cơ quan Liêm chính quần vợt (TIU), có cả những tay vợt nổi tiếng từng vô địch Grand Slam cũng đang nằm trong tầm ngắm bị điều tra.\r\n\r\nTruyền thông Bỉ cho biết, vụ dàn xếp tỷ số môn quần vợt này được dư luận đặc biệt quan tâm, nên sẽ được xét xử hết sức nghiêm túc, không để lọt người, lọt tội. Điều đó khiến phiên tòa hôm 24/3 tới đang được hết sức chờ đợi. Về phía nghi phạm “Maestro”, gã đang được đặt trong chế độ bảo vệ đặc biệt, sau khi phiên tòa hôm thứ Sáu vừa rồi phải tạm hoãn. Cảnh sát lo ngại các “thế lực đen” sẽ có những hành động để bưng bít thông tin.', 'https://onsports.vn/post/scandal-dan-xep-ty-so-quan-vot-khien-the-gioi-chan-dong', 'https://stvinaprod.vtvcab.vn/54b4d578-ecc2-458e-8c14-e3fe24712239.jpg', 6, 2, 3, 'scandal-dan-xep-ty-so-quan-vot-khien-the-gioi-chan-dong', 1, 1),
(54, 'Djokovic tiếp tục bỏ lỡ Miami Open, bị cấm nhập cảnh vào Mỹ', '2023-03-19', 'Theo thông tin mới nhất, Novak Djokovic sẽ bỏ lỡ giải Masters 1000 tiếp theo tại Mỹ là Miami do các vấn đề liên quan đến vaccine COVID-19.', 'Novak Djokovic đã có khởi đầu hoàn hảo trong năm 2023 khi đăng quang ngôi vô địch một cách thuyết phục tại Úc Mở rộng. Với danh hiệu Grand Slam thứ 22 này, anh cũng chính thức đòi lại vị trí số 1 thế giới từ tay Carlos Alcaraz. \r\n\r\nSau giải đấu trên, Djokovic tiếp tục tham gia Dubai Championships - giải ATP 500 tại Dubai, UAE và vào đến trận chung kết. Tuy nhiên ở trận đấu cuối cùng, tay vợt người Serbia đã phải nhận thất bại trước tay vợt đang có phong độ rất cao là Daniil Medvedev. Ở ATP Tour, ngôi sao người Nga hiện đã có chuỗi 19 trận thắng liên tiếp. \r\n\r\nNovak Djokovic đã có khởi đầu hoàn hảo trong năm 2023 khi đăng quang ngôi vô địch một cách thuyết phục tại Úc Mở rộng. Với danh hiệu Grand Slam thứ 22 này, anh cũng chính thức đòi lại vị trí số 1 thế giới từ tay Carlos Alcaraz. \r\n\r\nSau giải đấu trên, Djokovic tiếp tục tham gia Dubai Championships - giải ATP 500 tại Dubai, UAE và vào đến trận chung kết. Tuy nhiên ở trận đấu cuối cùng, tay vợt người Serbia đã phải nhận thất bại trước tay vợt đang có phong độ rất cao là Daniil Medvedev. Ở ATP Tour, ngôi sao người Nga hiện đã có chuỗi 19 trận thắng liên tiếp.', 'https://onsports.vn/post/djokovic-tiep-tuc-bo-lo-miami-open-bi-cam-nhap-canh-vao-my', 'https://stvinaprod.vtvcab.vn/dbc98595-8789-4120-a81d-c652218d40cd.jpg', 3, 2, 3, 'djokovic-tiep-tuc-bo-lo-miami-open-bi-cam-nhap-canh-vao-my', 1, 1),
(55, 'Nadal thua sốc ngay vòng 2, trở thành cựu vương Australian Open 2023', '2023-01-18', 'Ngay tại vòng 2 Australian Open 2023, Rafael Nadal bất ngờ nhận thất bại trước tay vợt Mackenzie McDonald sau 3 set đấu.', 'Tại vòng 2 Australian Open, Nadal đối đầu với tay vợt hạng 90 thế giới, Mackenzie McDonald. Tay vợt người Tây Ban Nha để thua 2 break-point trong ván đấu đầu. Tại ván đấu thứ 2, Nadal tiếp tục thua break-point ngay ở game đầu tiên cầm giao bóng, sau đó dù bẻ lại game giao bóng của đối thủ ở game 4, anh lại để thua break-point ở game 7. Ở ván đấu thư 3, anh tiếp tục thua break-point ở game 11 và thua match-point ở game 12.\r\n\r\nTại vòng 2 Australian Open, Nadal đối đầu với tay vợt hạng 90 thế giới, Mackenzie McDonald. Tay vợt người Tây Ban Nha để thua 2 break-point trong ván đấu đầu. Tại ván đấu thứ 2, Nadal tiếp tục thua break-point ngay ở game đầu tiên cầm giao bóng, sau đó dù bẻ lại game giao bóng của đối thủ ở game 4, anh lại để thua break-point ở game 7. Ở ván đấu thư 3, anh tiếp tục thua break-point ở game 11 và thua match-point ở game 12.\r\n\r\nTrước khi bước vào Australian Open 2023, Nadal cũng không có được phong độ tốt. Anh để thua 6/8 trận đấu gần đây, và thua 2 trận liên tiếp khi chuẩn bị cho mùa giải năm mới ở United Cup 2023.', 'https://onsports.vn/post/nadal-thua-soc-ngay-vong-2-tro-thanh-cuu-vuong-australian-open-2023', 'https://stvinaprod.vtvcab.vn/e2ef43ba-08d6-4d98-be1b-179538bb9a90.jpg', 6, 2, 3, 'nadal-thua-soc-ngay-vong-2-tro-thanh-cuu-vuong-australian-open-2023', 1, 1),
(56, 'Nadal dừng bước sớm tại ATP Finals, truyền nhân lập kỷ lục vô tiền khoáng hậu', '2022-11-16', 'Với việc Nadal bị loại ngay sau vòng bảng ATP Finals, Alcaraz chắc chắn kết thúc năm 2022 với vị trí số 1 thế giới.', 'Sau thất bại trước tân binh Fritz ở trận ra quân, rất nhiều người hâm mộ cho rằng đây chỉ là tai nạn đối với Rafael Nadal, do đó anh sẽ trở lại ở trận đấu thứ 2. Tuy nhiên, huyền thoại sống của làng quần vợt thế giới tiếp tục gây thất vọng khi chạm trán đàn em Felix Auger-Aliassime.\r\n\r\nỞ trong set 1, Nadal có tới 4 cơ hội giành break, chia đều ở game giao bóng thứ 1 và 4 của Auger-Aliassime. Tuy nhiên, tay vợt 36 tuổi đã không thể tận dụng thành công bất cứ một lần nào. Thậm chí, anh còn để đối thủ bên kia chiến tuyến bẻ game cầm giao bóng bản lề và thua 3-6.\r\n\r\nSang đến set 2 tiếp tục, thế trận giằng co được duy trì xuyên suốt. Tuy nhiên, sai lầm ở game 3 khi để Auger-Aliassime có được break đã khiến mọi nỗ lực sau đó của ngôi sao người Tây Ban Nha trở nên vô ích. Giành chiến thắng chung cuộc sau 2-0 sau gần 2 giờ đồng hồ giúp hạt giống số 5 còn nguyên cơ hội giành vé đi tiếp ở bảng Xanh.\r\n\r\nTrong khi đó, Nadal đã chính thức dừng bước ở ATP Finals 2022 trước khi lượt đấu cuối cùng diễn ra. Bởi lẽ, ở cặp đấu còn lại Casper Ruud đã có được thắng lợi 2-1 trước Taylor Fritz và giành tấm vé sớm vào bán kết. Ở lượt cuối, Auger-Aliassime và Fritz sẽ đối đầu nhau để tìm ra người còn lại tiến đến vòng đấu của 4 tay vợt mạnh nhất ở bảng Xanh.\r\n\r\nViệc Nadal bị loại sớm đã giúp Carlos Alcaraz chắc chắn có được vị trí số 1 thế giới sau năm 2022. Ngoài ra, nó cũng giúp tài năng sinh năm 2003 là tay vợt trẻ nhất lịch sử làm được điều này. Trước đó, Lleyton Hewitt là người giữ kỷ lục này từ năm 2001. Thời điểm đó, Hewitt hơn Alcaraz 16 tháng tuổi.\r\n\r\nVề phía Nadal, nguyên nhân dẫn đến 2 trận thua liên tiếp gần đây xuất phát từ việc anh chưa hoàn toàn có được thể trạng tốt nhất sau chấn thương. Theo Eurosport, “ông vua” sân đất nện để ngỏ khả năng đánh trận thủ tục với Ruud. Nếu tay vợt 22 lần vô địch Grand Slam rút lui, Holger Rune sẽ được chọn thay thế.', 'https://onsports.vn/post/nadal-dung-buoc-som-tai-atp-finals-truyen-nhan-lap-ky-luc-vo-tien-khoang-hau', 'https://stvinaprod.vtvcab.vn/a8f8d1cc-ffed-42a8-919c-a1a28bd9016f.jpg', 6, 2, 3, 'nadal-dung-buoc-som-tai-atp-finals-truyen-nhan-lap-ky-luc-vo-tien-khoang-hau', 1, 1),
(57, 'Djokovic vô địch giải ATP Astana Open', '2022-10-10', 'Novak Djokovic (Nole) giành danh hiệu thứ 90 trong sự nghiệp sau khi vô địch giải ATP Astana Open ngày 9/10.', 'Giành chiến thắng trước Stefanos Tsitsipas với tỷ số 6-3, 6-4 ở chung kết ATP Astana Open, Novak Djokovic vô địch giải ATP Astana Open. Chiến thắng này đã giúp tay vợt người Serbia giành danh hiệu thứ 90 trong sự nghiệp.\r\n\r\nTuần trước Djokovic đã vô địch tại Tel Aviv và có 9 trận thắng để đăng quang tại Astana Open trong lần đầu giải đấu lên cấp độ ATP 500. Tại cấp độ này, Tsitsipas chưa thể vô địch lần nào dù đã vào chín trận chung kết.\r\n\r\nTrong trận chung kết hôm 9/10, Nole giao bóng quá tốt trong khi Tsitsipas không có nổi break - point. Djokovic chỉ mặc 7 lỗi tự đánh hỏng, thắng 33/38 điểm bóng một và dễ dàng đánh bại tay vợt người Hy Lạp chỉ sau 75 phút.\r\n\r\nGiành được danh hiệu thứ 90, Djokovic chia sẻ: “Tôi luôn muốn bản thân sẽ có một sự nghiệp vĩ đại. Không biết tôi sẽ chơi bao nhiêu trận chung kết hay vô địch bao nhiêu giải, nhưng tôi muốn bản thân đạt đến đỉnh cao nhất của môn thể thao này”.\r\n\r\nSau chức vô địch tại Astana, Nole cùng các tay vợt như Rafael Nadal, Carlos Alcaraz, Stefanos Tsitsipas và Casper Ruud đã đủ điểm để tham dự giải danh giá ATP Finals diễn ra vào cuối năm.', 'https://onsports.vn/post/djokovic-vo-dich-giai-atp-astana-open', 'https://stvinaprod.vtvcab.vn/934e8fa7-6663-4ae1-ae2a-2a8940963e80.jpg', 6, 2, 3, 'djokovic-vo-dich-giai-atp-astana-open', 1, 1),
(58, 'Vé xem trận đấu cuối cùng của Fereder được bán với mức giá kỷ lục.', '2022-09-23', 'Giá vé trận đấu cuối cùng của Fereder đánh cặp với Nadal được bán với mức giá kỷ lục khi nhiều người tranh nhau vào sân O2 Arena.', 'Tối 23/9, trận đấu cuối cùng của Roger Fereder sẽ được diễn ra tại sân O2 Arena. Truyền thông Anh tiết lộ, vé xem hai trận đấu hôm đó được người hâm mộ tranh nhau mua. Tại chợ đen, có thời điểm giá vé được đẩy lên 50.000 USD/cặp.\r\n\r\nMức giá 10.000 USD/cặp đã tăng gấp 5 lần mà NHM vẫn không thể mua vì tình trạng cháy vé. Báo AS của Tây Ban Nha cho biết: “Phải có đặc quyền mới vào được sân O2 Arena ngày hôm nay”.\r\n\r\nFederer xác nhận chỉ chơi trận đôi duy nhất trong ngày đầu Laver Cup. Trải qua hơn một năm nghỉ thi đấu do chấn thương đầu gối, anh không thể đảm bảo đủ sức khoẻ để tham gia đánh đơn ở Laver Cup 2022.\r\n\r\nNăm 2013, trận chung kết Wimbledon giữa Andy Murray và Novak Djokovic có giá cao nhất 59.000 USD. Năm 2010, giá vé trận chung kết NBA giữa LA Lakers và Boston Celtics có lúc lên 52.000 USD. Và đến trận đấu của Federer, giá vé đã lên đến 50.000 USD.\r\n\r\nTrước khi huyền thoại người Thuỵ Sĩ thông báo giải nghệ, vé trận đấu cũng chỉ tăng lên 2120 USD đến 2.770 USD. Dường như ai cũng muốn xem trận đấu cuối cùng của Roger Federer khi đánh cặp cùng người bạn thân và cũng là đối thủ lớn Nadal.\r\n\r\nLaver Cup 2022 diễn ra trong 3 ngày (từ 23 - 25/9) ở sân O2 (London) với 12 trận đấu, trong đó có 9 trận đơn và 3 trận đôi.\r\n\r\nMỗi ngày, hai đội đấu 3 trận đơn và 1 trận đôi. Mỗi trận thắng ở ngày đầu được 1 điểm, ngày hai 2 điểm và ngày ba 3 điểm. Đội nào chạm đến điểm số thứ 13 trước là đội thắng cuộc. Trong 4 kỳ Laver Cup trước đây, đội châu u đều giành chiến thắng chung cuộc.\r\n\r\nSự có mặt của “Big Four” Andy Murray, Novak Djokovic, Rafael Nadal và Roger Federer trong mùa giải giúp Laver Cup 2022 càng trở nên hấp dẫn. Đây là lần đầu tiên trong lịch sử Laver Cup, “Big Four” hội quân cho Team Europe. Giải còn quy tụ rất nhiều những tay vợt hàng đầu thế giới hiện nay như Casper Ruud, Stefanos Tsitsipas, Taylor Fritz, Felix Auger-Aliassime, Jack Sock và Tommy Paul...', 'https://onsports.vn/post/ve-xem-tran-dau-cuoi-cung-cua-fereder-duoc-ban-voi-muc-gia-ky-luc', 'https://stvinaprod.vtvcab.vn/7cead3ad-7afd-4adc-9fb2-15f8d1ebb1be.jpg', 4, 2, 3, 've-xem-tran-dau-cuoi-cung-cua-fereder-duoc-ban-voi-muc-gia-ky-luc', 1, 1),
(59, 'Choáng với số tiền Roger Federer kiếm được nhờ quần vợt', '2022-09-16', 'Trong quãng thời gian thi đấu chuyên nghiệp, “Tàu tốc hành” đã đem về tổng cộng số tiền lên tới hơn 1 tỷ USD.', 'Chỉ hai tuần sau khi Serena Williams nói lời chia tay với sự nghiệp quần vợt chuyên nghiệp, một biểu tượng vĩ đại khác là Roger Federer cũng chính thức tuyên bố treo vợt sau khi Laver Cup, sự kiện diễn ra ở London kết thúc.\r\n\r\nSau 24 năm thi đấu quần vợt chuyên nghiệp, Federer đã chơi tổng cộng 1.526 trận, 103 danh hiệu lớn nhỏ trong đó có 20 lần lên ngôi tại các giải Grand Slam, 1.526 trận, kỷ lục 103 danh hiệu. Ngoài ra, huyền thoại người Thụy Sĩ cũng có tới 310 tuần ở vị trí số 1 thế giới, trong đó 237 tuần liên tiếp.\r\n\r\nVề lối chơi, Federer là một trong những tay vợt được hâm mộ nhất khi anh luôn vui vẻ và thi đấu bằng sự tinh tế. Trong mắt các CĐV, huyền thoại 41 tuổi là một người đàn ông lịch lãm nhưng với các đối thủ, họ xem anh là một con quái vật. Một trong những điều người ta ấn tượng nhất với Federer ở trên sân là những cú trái một tay đẹp mắt.\r\n\r\nNgoài những danh hiệu, quần vợt cũng giúp Federer trở thành một trong những người ngôi sao thể thao kiếm tiền khủng nhất. Theo thông tin được tờ Marca đăng tải, tài sản ròng của người đàn ông có 4 người con được cho lên tới 550 triệu USD sau một sự nghiệp chứng kiến anh kiếm được gần 1 tỷ USD từ bản quyền hình ảnh, các hợp đồng quảng cáo cùng hơn 150 triệu USD từ các khoản thưởng.\r\n\r\nĐược biết, Federer chỉ kiếm được 28.000 USD trong năm 1998 nhưng con số này tăng lên gấp 400 lần trong vòng 10 năm. Khi còn ở quãng thời gian thăng hoa nhất trong sự nghiệp, anh được cho kiếm được 94 triệu USD mỗi năm, đến vào mùa giải 2018/19, phần lớn nhờ vào hợp tác với các thương hiệu lớn như Credit Suisse, Rolex và Mercedes Benz.\r\n\r\nNăm 2018, Federer ký hợp đồng 10 năm với thương hiệu thời trang Nhật Bản, Uniqlo với giá trị lên tới 300 triệu USD.\r\n\r\nXét về tổng số tiền thi đấu, Federer kiếm được 115 triệu USD, thấp hơn Nadal (131 triệu USD) và Djokovic (159 triệu USD), nhưng tổng tài sản thu về trong hơn 2 thập kỷ thì hai đối thủ lớn nhất trong sự nghiệp của tay vợt này đều không thể so sánh được với anh.', 'https://onsports.vn/post/choang-voi-so-tien-roger-federer-kiem-duoc-nho-quan-vot', 'https://stvinaprod.vtvcab.vn/8930698c-0558-44f3-a5ab-e7b4a78fa5de.jpg', 6, 2, 3, 'choang-voi-so-tien-roger-federer-kiem-duoc-nho-quan-vot', 1, 1),
(60, '“Tiểu Nadal” đi vào lịch sử làng tennis sau chức vô địch US Open', '2022-09-12', 'Dù mới 19 tuổi nhưng với những gì tay vợt người Tây Ban Nha đã thể hiện, rất nhiều người đã tin tưởng vào việc anh sẽ trở nên vĩ đại như Rafael Nadal.', 'Bước vào trận chung kết US Open 2022 với bất lợi không nhỏ về mặt thể lực sau khi phải trải qua 5 set đấu căng thẳng trước Frances Tiafoe, tuy nhiên Carlos Alcaraz đã có một màn trình diễn bùng nổ.\r\n\r\nỞ set 1, dù Casper Ruud liên tục bám đuổi nhưng Alcaraz đã thể hiện được phẩm chất của mình khi giành được 1 break và thắng 6-4. Tuy nhiên, trong set 2, đối thủ của “Tiểu Nadal” đã đưa trận đấu trở về thế cân bằng với thắng lợi 6-2.\r\n\r\nSang đến set đấu thứ 3 bản lề, thêm một lần hai tay vợt cho khán giả thưởng thức màn bám đuổi kịch tích. Và khi bước vào loạt tie-break, Carlos Alcaraz dù còn trẻ nhưng đã thi đấu đầy lì lợm, trong khi Casper Ruud bị mất tinh thần nên anh đã thắng dễ 7-1 để có được lợi thế.\r\n\r\nBước vào set 4, với sự hưng phấn ngôi sao sinh năm 2003 ép được đàn anh mỗi khi tấn công, và bền bỉ khi phòng ngự. Anh tận dụng tốt break-point ở game 6 để khép lại set đấu với tỷ số 6-3.\r\n\r\nThắng lợi chung cuộc 3-1 đã giúp Carlos Alcaraz có lần đầu tiên trong sự nghiệp lên ngôi tại một giải Grand Slam, vươn lên vị trí số 1 trên bảng xếp hạng ATP và thiết lập những cột mốc lịch sử đáng tự hào.\r\n\r\nVới mốc 19 tuổi 4 tháng 6 ngày, anh trở thành người trẻ nhất trong lịch sử leo lên ngôi vị tay vợt nam số 1 thế giới. “Tiểu Nadal” đã phá kỷ lục đã tồn tại suốt 21 năm qua kể từ thời điểm huyền thoại người Australia Lleyton Hewitt đạt vị trí này khi 20 tuổi 8 tháng 23 ngày vào ngày 19/10/2001.\r\n\r\nAlcaraz là nhà vô địch Grand Slam trẻ nhất sau 17 năm kể từ thời điểm đàn anh đồng hương Rafael Nadal đăng quang Roland Garros năm 2005 khi 19 tuổi. Tài năng trẻ sinh ra ở El Palmar còn là người trẻ nhất vô địch đơn nam US Open kể từ khi huyền thoại Pete Sampras làm được điều này vào năm 1990 khi cũng 19 tuổi.\r\n\r\nNgoài ra, Alcaraz cũng là tay vợt đầu tiên kể từ Novak Djokovic ở Wimbledon 2019 cứu match-point trên đường vô địch Grand Slam. Tân số 1 thế giới cứu match-point khi đấu Jannik Sinner ở tứ kết, một trong ba trận dài năm set của anh tại New York năm nay.', 'https://onsports.vn/post/tieu-nadal-di-vao-lich-su-lang-tennis-sau-chuc-vo-dich-us-open', 'https://stvinaprod.vtvcab.vn/41e5586c-0640-4195-81f3-d0357209a8a5.jpg', 4, 2, 3, '“tieu-nadal”-di-vao-lich-su-lang-tennis-sau-chuc-vo-dich-us-open', 1, 1),
(61, 'Lý Hoàng Nam làm nên cột mốc lịch sử mới cho quần vợt Việt Nam', '2022-08-29', 'Với thành tích ấn tượng tại ATP Challenger 50, Lý Hoàng Nam đã thiết lập nên cột mốc lịch sử mới cho quần vợt Việt Nam trên bảng xếp hạng thế giới.', 'Theo bảng xếp hạng ATP mới được công bố sáng nay (29/8), Lý Hoàng Nam đã nhảy vọt từ vị trí thứ 328 lên xếp hạng thứ 290, thứ hạng cao nhất trong sự nghiệp thi đấu của tay vợt sinh năm 1997. Đây cũng là lần đầu tiên một tay vợt Việt Nam (không tính nhập tịch) lọt vào top 300 bảng xếp hạng nhà nghề thế giới.\r\n\r\nTrong bảng xếp hạng này, Lý Hoàng Nam giữ ngôi vị số một ở Đông Nam Á. Vị trí thứ 2 thuộc về Kasidit Samrej (Thái Lan) với thứ hạng 616.\r\n\r\nViệc Lý Hoàng Nam có bước nhảy vọt trong sự nghiệp là nhờ thành tích giành ngôi á quân Nonthaburi Challenger ATP 50 vừa qua. Tay vợt sinh năm 1997 được cộng thêm 30 điểm thưởng, qua đó tích lỹ tổng điểm 169. Anh trở thành tay vợt Việt Nam đầu tiên vào chung kết một giải Challenger 50.\r\n\r\nLý Hoàng Nam đang có một năm 2022 thi đấu khá thành công. Tay vợt số 1 Việt Nam liên tục giành 2 vị trí á quân ở giải M15 tại Pháp, Thái Lan và Malaysia. Anh còn 3 lần liên tiếp vô địch trên sân nhà vào tháng 6 và tháng 7.\r\n\r\nTheo kế hoạch, Hoàng Nam sẽ tiếp tục ở lại Thái Lan để tham dự giải nhà nghề Nonthaburi Challenger 2 diễn ra từ ngày 29/8 đến 4/9. Tay vợt số 1 Việt Nam sẽ thi đấu trận mở màn vòng chính gặp tay vợt hạt giống số 3 người Nhật Bản Uchiyama Yasutaka (hạng 264 ATP).', 'https://onsports.vn/post/ly-hoang-nam-lam-nen-cot-moc-lich-su-moi-cho-quan-vot-viet-nam', 'https://stvinaprod.vtvcab.vn/6fdd32af-7ff6-40e0-a7f5-0f521f6ec629.jpg', 1, 2, 3, 'ly-hoang-nam-lam-nen-cot-moc-lich-su-moi-cho-quan-vot-viet-nam', 1, 1),
(62, 'ĐT bóng chuyền nữ Việt Nam nhận thưởng nóng sau trận đấu kịch tính với Thái Lan', '2023-05-15', 'Liên đoàn bóng chuyền Việt Nam và công ty Động Lực quyết định thưởng nóng cho đội tuyển bóng chuyền nữ Việt Nam sau những nỗ lực rất đáng ghi nhận trong trận chung kết SEA Games 32.', 'Thêm một lần nữa, đội tuyển bóng chuyền nữ Việt Nam lại lỗi hẹn với tấm HCV SEA Games sau khi để thua 1-3 trước Thái Lan trong trận chung kết diễn ra vào tối qua (14/5).\r\n\r\nDù không thể đánh bại được người Thái, tuy nhiên những nỗ lực của các cô gái bóng chuyền Việt Nam vẫn xứng đáng được ghi nhận. Chúng ta đã khiến cho tuyển bóng chuyền nữ Thái Lan, những người được đánh giá rất cao về đẳng cấp, phải thực sự gặp nhiều khó khăn và vất vả trong trận đấu tranh HCV.\r\n\r\nThậm chí, đội tuyển nữ Việt Nam có thời điểm tiến đến rất gần một chiến tích lịch sử. Sau khi gỡ hòa 1-1 qua hai set đầu, Trần Thị Thanh Thúy và các đồng đội còn liên tục dẫn trước đối thủ ở set 3. Tuy nhiên, các học trò của HLV Nguyễn Tuấn Kiệt không thể tận dụng thời cơ, để đối thủ ngược dòng cay đắng. Đến set thứ 4, tinh thần của các cô gái Việt Nam đi xuống, và mặc dù rất cố gắng nhưng vẫn để thua trước đẳng cấp và bản lĩnh của đội tuyển Thái Lan.\r\n\r\nDù không thể giành được tấm HCV SEA Games, nhưng với màn trình diễn nỗ lực và để lại nhiều ấn tượng trong trận chung kết, tuyển bóng chuyền nữ Việt Nam vẫn nhận được những phần thưởng nóng.\r\n\r\nCụ thể, toàn đội được Liên đoàn bóng chuyền Việt Nam trao thưởng 200 triệu và Công ty Động Lực trao thưởng 100 triệu đồng. Bên cạnh đó, mỗi VĐV cũng được thưởng 25 triệu đồng cho tấm HCB SEA Games theo quy định Nhà nước.\r\n\r\nVới những sự động viên kịp thời nói trên, hy vọng rằng đoàn quân của HLV Nguyễn Tuấn Kiệt sẽ có thêm động lực để tiếp tục phấn đấu cho những mục tiêu tiếp theo.', 'https://onsports.vn/post/dt-bong-chuyen-nu-viet-nam-nhan-thuong-nong-sau-tran-dau-kich-tinh-voi-thai-lan', 'https://stvinaprod.vtvcab.vn/6ba63d5c-0d25-4d77-b56f-c40daac402cc.png', 1, 1, 4, 'dt-bong-chuyen-nu-viet-nam-nhan-thuong-nong-sau-tran-dau-kich-tinh-voi-thai-lan', 1, 1),
(63, 'HLV Tuấn Kiệt tiết lộ lý do giúp bóng chuyền nữ Việt Nam ngược dòng khó tin trước người Thái', '2023-05-03', 'Nhờ tinh thần quyết tâm, sự bền bỉ cùng với khả năng truyền lửa chủa HLV Nguyễn Tuấn Kiệt, đội tuyển bóng chuyền nữ Việt Nam vẫn có thể lội ngược dòng một cách khó tin trước Thái Lan dù đã bị dẫn trước 2 set đấu.', 'Bị đối thủ dẫn trước 2 set nhưng các cô gái bóng chuyền Việt Nam vẫn lội ngược dòng ngoạn mục. Đó là nhờ vào tinh thần chiến đấu, sự quyết tâm, bền bỉ và không thể không nhắc đến khả năng truyền lửa của HLV Nguyễn Tuấn Kiệt.\r\n\r\nTrước đó, đội tuyển bóng chuyền nữ Việt Nam đánh bại 4 ứng cử viên vô địch lần lượt là Hisamitsu Springs, King Whale Taipei, Liaoning Donghua. Màn lội ngược dòng khó tin nói trên diễn ra trong trận đấu cuối cùng với đối thủ Thái Lan Diamond Food. Qua đó, các cô gái vàng của bóng chuyền Việt Nam đã lên ngôi vô địch các CLB nữ châu Á 2023 một cách đầy thuyết phục.\r\n\r\nSau trận, HLV Nguyễn Tuấn Kiệt chia sẻ: \"Ngay cả khi Việt Nam bị dẫn trước 2 set thì tôi cảm thấy vẫn bình thường, bởi chúng ta chỉ hết hy vọng khi nào trọng tài thổi còi kết thúc trận đấu mà thôi. Đối với môn bóng chuyền, việc thắng trước 2 set cũng không nói lên gì nhiều. Đội tuyển Việt Nam có nhiều nhân tố có thể tạo nên đột biến, bởi vậy chúng tôi có thể lấy lại thế trận khi điều chỉnh nhân sự\".\r\n\r\nThực tế là không chỉ ở trận chung kết với Diamond Food mà 2 trận đấu trước đó với King Whale Taipei, Liaoning Donghua đội tuyển bóng chuyền nữ Việt Nam đều bị đối thủ dẫn trước với những set thua cách biệt. Thế nhưng ở những thời điểm nguy nan nhất, yếu tố tâm lý và tinh thần của đội tuyển Việt Nam lại phát huy tác dụng và trở thành vũ khí lợi hại.\r\n\r\nỞ những thời điểm khó khăn ấy, HLV Nguyễn Tuấn Kiệt cũng không chỉ có những sự luân chuyển đội hình hợp lý mà còn rất biết cách truyền lửa và vực dậy tinh thần cho các học trò. Bí quyết của vị HLV 47 tuổi là không gây gáp lực lên học trò, dựa vào yếu tố màu cờ sắc áo và các cổ động viên để tạo nên động lực. Điển hình là khi mà các học trò của ông có dấu hiệu căng cứng tâm lý ở set 5 quyết định với Thái Lan:\r\n\r\n\"Ở hiệp 5, tôi nói rằng đây là lúc mà các em phải chơi với sự thoải mái, bởi những gì các em thể hiện đã là rất tuyệt vời rồi. Đây là sân nhà và khán giả nhà cũng giống như những người nhà của chúng ta, họ đã đặt rất nhiều tình cảm cho chúng ta và phải chơi thật cống hiến để đền đáp. Rất may là cuối cùng các em đều đã làm rất tốt.', 'https://onsports.vn/post/hlv-tuan-kiet-tiet-lo-ly-do-giup-bong-chuyen-nu-viet-nam-nguoc-dong-kho-tin-truoc-nguoi-thai', 'https://stvinaprod.vtvcab.vn/be49e649-af42-4923-99ca-e1bebec8f000.jpg', 1, 1, 4, 'hlv-tuan-kiet-tiet-lo-ly-do-giup-bong-chuyen-nu-viet-nam-nguoc-dong-kho-tin-truoc-nguoi-thai', 1, 1),
(64, 'HLV Nguyễn Tuấn Kiệt dành lời khen cho các học trò sau thất bại trước Thái Lan', '2023-05-15', 'Sau trận thua Thái Lan tại chung kết môn bóng chuyền nữ SEA Games 32, HLV Nguyễn Tuấn Kiệt cho biết dù thất bại nhưng vẫn có thể nhìn thấy những điểm sáng tích cực của tuyển bóng chuyền nữ Việt Nam.', 'Tối 14/5, đội tuyển bóng chuyền nữ Việt Nam bước vào trận chung kết tranh HCV SEA Games 32 với Thái Lan. Dù thi đấu nỗ lực nhưng Thanh Thúy và các đồng đội đã không thể giành được chiến thắng. Thầy trò HLV Nguyễn Tuấn Kiệt chấp nhận thất bại sau 4 set đấu.\r\n\r\nTuy không thể giành chiến thắng nhưng các cô gái bóng chuyền nữ Việt Nam đã thể hiện bản lĩnh, tinh thần và những nỗ lực không biết mệt mỏi trên sân. Đặt biệt trong set đấu thứ 3, Thanh Thúy và các đồng đội chỉ chấp nhận thất bại với tỉ số 30-32.\r\n\r\nChia sẻ sau trận đấu, HLV Nguyễn Tuấn Kiệt cho biết đây là trận chung kết thứ 4 của ông với bóng chuyền nữ Việt Nam. Dù thất bại nhưng toàn đội vẫn có những tín hiệu lạc quan, tích cực.\r\n\r\n\"Trong trận đấu ngày hôm nay, các em đã cống hiến hết khả năng của mình, nhưng nó cũng có một phần may rủi. Trong hiệp 3 thì chúng ta cũng có phần đáng tiếc nhưng Thái Lan đã thi đấu vô cùng bản lĩnh. Họ thay đổi lối chơi nhanh, chúng ta đã không bắt kịp. Chúng ta bỏ lơi vị trí phụ công và mất những điểm số quan trọng.\r\n\r\nDù thua nhưng chúng ta có thể nhìn thấy những hi vọng ở các em. 2 điểm sáng trong trận đấu với Thái Lan chính là tinh thần và dám chơi. Chúng ta phải phát huy được điều đó. Có thua thì cũng rèn luyện được rất nhiều. Từ đó chúng ta có thể hi vọng một ngày thi đấu sòng phẳng với Thái Lan\", HLV Nguyễn Tuấn Kiệt cho biết.\r\n\r\nSau trận chung kết SEA Games 32, tuyển bóng chuyền nữ Việt Nam về nước vào ngày 15/5. Toàn đội sẽ được nghỉ ngơi 1 tuần trước khi hội quân trở lại ở Quảng Ninh để chuẩn bị cho giải AVC Challenge 2023 do Liên đoàn bóng chuyền châu Á tổ chức. Theo kết quả bốc thăm từ tháng 3 năm nay, đội tuyển bóng chuyền nữ Việt Nam được bốc thăm vào bảng D cùng Uzbekistan, Mông Cổ. Giải này diễn ra từ ngày 18 tới 25/6 tại Indonesia.', 'https://onsports.vn/post/hlv-nguyen-tuan-kiet-danh-loi-khen-cho-cac-hoc-tro-sau-that-bai-truoc-thai-lan', 'https://stvinaprod.vtvcab.vn/0bcd82d8-9395-47db-b1d0-02258de1a501.jpg', 1, 1, 4, 'hlv-nguyen-tuan-kiet-danh-loi-khen-cho-cac-hoc-tro-sau-that-bai-truoc-thai-lan', 1, 1),
(65, 'Thắng đại diện Nhật Bản, bóng chuyền nữ Việt Nam rộng cửa vào bán kết', '2023-04-28', 'Trong trận đấu với CLB Hisamitsu Springs (Nhật Bản), Thanh Thúy đã liên tiếp ghi nhiều điểm số quan trọng giúp tuyển bóng chuyền nữ Việt Nam (Sport Center I) giành chiến thắng chung cuộc 3-1.', 'Tại lượt trận thứ 2 giải bóng chuyền vô địch các CLB bóng chuyền nữ châu Á (AVC 2023), tuyển nữ Việt Nam phải đối đầu với CLB Hisamitsu Springs của Nhật Bản. Với sự xuất sắc của Thanh Thúy, Lý Thị Luyến, Kiều Trinh, đoàn quân của HLV Tuấn Kiệt xuất sắc giành chiến thắng với tỷ số 3-1 (25-20, 17-25, 25-21, 25-15).\r\n\r\nHisamitsu Springs là đội bóng có bề dày thành tích ấn tượng tại AVC. CLB đến từ Nhật Bản từng 2 lần vô địch giải đấu vào các năm 2002, 2014 và 3 lần đứng ở vị trí á quân năm 2001, 2015, 2017.\r\n\r\nChiến thắng này giúp Thanh Thúy và các đồng đội có cơ hội rất lớn để lọt vào bán kết. Hiện tại, tuyển bóng chuyền nữ Việt Nam có được 5 điểm, tiếp tục dẫn đầu bảng, hơn đội đứng thứ hai King Whale Taipei (Đài Loan, Trung Quốc) 3 điểm.\r\n\r\nTại lượt trận cuối, đoàn quân của HLV Nguyễn Tuấn Kiệt sẽ trực tiếp đối đầu với CLB King Whale Taipei. Kịch bản duy nhất khiến tuyển nữ bóng chuyền Việt Nam bị loại là không giành được điểm nào trước King Whale Taipei, đồng thời Paykan phải giành trọn vẹn 6 điểm trong 2 trận đấu còn lại. Tuy nhiên, kịch bản này khó có khả năng xảy ra khi Paykan được đánh giá yếu nhất bảng đấu.\r\n\r\nTrận đấu giữa tuyển nữ bóng chuyền Việt Nam với King Whale Taipei sẽ diễn ra vào lúc 19h30 ngày 29/4.', 'https://onsports.vn/post/thang-dai-dien-nhat-ban-bong-chuyen-nu-viet-nam-rong-cua-vao-ban-ket', 'https://stvinaprod.vtvcab.vn/d43b4192-e9e5-4456-bafa-441a61176e13.jpg', 1, 1, 4, 'thang-dai-dien-nhat-ban-bong-chuyen-nu-viet-nam-rong-cua-vao-ban-ket', 1, 1),
(66, 'Bích Tuyền không thể tham dự SEA Games 32 cùng ĐT bóng chuyền Việt Nam', '2023-04-27', 'Mới đây, HLV trưởng Nguyễn Tuấn Kiệt đã xác nhận Nguyễn Thị Bích Tuyền không thể tham dự SEA Games khi thay thế Trần Tú Linh vào đội hình ĐT bóng chuyền Việt Nam.', 'Trước đó, Bích Tuyền đã không thể tham dự giải bóng chuyền vô địch các CLB châu Á 2023 đang diễn ra tại Vĩnh Phúc do chấn thương lưng. Được biết, chấn thương của chủ công sinh năm 2000 khá nghiêm trọng và cô không thể sang Campuchia để tham dự SEA Games 32 cùng ĐT bóng chuyền Việt Nam. Vì vậy, ban huấn luyện đã đăng ký bổ sung Trần Tú Linh để đội tuyển đầy đủ về mặt lực lượng.\r\n\r\nTại SEA Games 31, Bích Tuyền đã cùng ĐT bóng chuyền Việt Nam giành HCB. Tay chuyền người Vĩnh Long được đánh giá là VĐV xuất sắc và NHM mong chờ màn \"song kiếm hợp bích\" của cô và Thanh Thuý. Tuy nhiên, việc Bích Tuyền không thể tham dự SEA Games gây nhiều tiếc nuối cho NHM cũng như ĐT bóng chuyền Việt Nam.\r\n\r\nHiện tại, ĐT bóng chuyền nữ Việt Nam đang có mặt tại Vĩnh Phúc để tham dự giải bóng chuyền vô địch các CLB châu Á 2023. Giải đấu chính là bước chạy đà cho SEA Games 32. Vào lúc 19h30 hôm nay (27/4) sẽ diễn ra trận đấu giữa ĐT Việt Nam vs CLB Hisamitsu Springs (Nhật Bản) ở lượt trận thứ 2 bảng A.', 'https://onsports.vn/post/bich-tuyen-khong-the-tham-du-sea-games-32-cung-dt-bong-chuyen-viet-nam', 'https://stvinaprod.vtvcab.vn/2661dc5d-6288-4b69-b1af-e6b2e7387b6d.jpg', 1, 1, 4, 'bich-tuyen-khong-the-tham-du-sea-games-32-cung-dt-bong-chuyen-viet-nam', 1, 1),
(67, 'Thanh Thúy lập kỷ lục ấn tượng tại giải bóng chuyền châu Á', '2023-04-26', 'Tại trận ra quân giải bóng chuyền vô địch các CLB nữ châu Á (AVC), Thanh Thúy đã có màn trình diễn vô cùng ấn tượng.', 'Đội tuyển bóng chuyền nữ Việt Nam đã có chiến thắng kịch tính 3-2 trước CLB Paykan (Iran) tại trận ra quân giải AVC 2023. Trong trận đấu này, Thanh Thúy tiếp tục là một điểm sáng rực rỡ với khả năng tấn công xuất sắc và những pha đập bóng uy lực có độ chính xác cao.\r\n\r\nSau 5 set đấu, Thanh Thúy đã ghi tổng cộng 39 điểm, là cầu thủ ghi điểm xuất sắc nhất bên phía tuyển bóng chuyền Việt Nam. Trong đó, có 35 điểm tấn công, 2 điểm block và 2 điểm phát sóng. Đây cũng là số điểm kỷ lục mà Thanh Thúy ghi được trong màu áo đội tuyển bóng chuyền nữ Việt Nam.\r\n\r\nSau chiến thắng 3-2 trước CLB Paykan, tuyển bóng chuyền nữ Việt Nam sẽ còn 2 trận đấu quan trọng tại vòng bảng. Trong đó, ngày 27/4, thầy trò HLV Nguyễn Tuấn Kiệt sẽ đối đầu với Hisamitsu Springs (Nhật Bản). Đây cũng là đội bóng mà Thúy không còn xa lạ khi cô thường xuyên phải đối đầu trong những mùa giải gần đây thi đấu tại Nhật Bản.\r\n\r\nTại lượt trận cuối cùng của vòng bảng, tuyển bóng chuyền nữ Việt Nam sẽ đối đầu với CLB King Whale Taipe.', 'https://onsports.vn/post/thanh-thuy-lap-ky-luc-an-tuong-tai-giai-bong-chuyen-chau-a', 'https://stvinaprod.vtvcab.vn/36e8a1d0-9219-407f-9f8d-9c0b74964144.jpg', 1, 1, 4, 'thanh-thuy-lap-ky-luc-an-tuong-tai-giai-bong-chuyen-chau-a', 1, 1),
(68, 'Thanh Thúy \"gánh team\", bóng chuyền nữ Việt Nam khởi đầu ấn tượng tại giải châu Á', '2023-04-26', 'Tại trận ra quân giải Giải bóng chuyền vô địch các CLB nữ châu Á, bóng chuyền nữ Việt Nam (Sport Center I) đã có chiến thắng kịch tính trước Paykan.', 'Ở set đấu đầu tiên, tuyển bóng chuyền nữ Việt Nam đã gây sức ép khá lớn bất chấp việc để đối phương ghi điểm trước. Trần Thị Thanh Thúy tiếp tục thể hiện tốt vai trò đầu tàu, liên tiếp ghi nhiều điểm số ấn tượng giúp đội chủ nhà giành chiến thắng với tỉ số 25-22.\r\n\r\nSang set đấu thứ 2, mọi thứ dần trở nên khó khăn với đoàn quân HLV Nguyễn Tuấn Kiệt. Paykan trở lại đầy mạnh mẽ và dẫn sâu tới 8-3. Dù rất nỗ lực nhưng Thanh Thúy và các đồng đội phải chấp nhận thất bại với tỉ số 21-25.\r\n\r\nSang set thứ 3, CLB Paykan tiếp tục dẫn trước nhưng không thể sớm bứt lên. Việt Nam nỗ lực không để khoảng cách vượt quá 2 điểm và sau đó gỡ hòa 8-8. Các cô gái Việt Nam tiếp tục tận dụng tốt thời cơ để giành chiến thắng ván này với tỉ số 25-18.\r\n\r\nBước sang set thứ 4, Paykan lại có chiến thắng kịch tính với tỉ số 25-22. Điều này buộc trận đấu phải bước vào ván thứ 5 để phân định thắng thua. Tại set quyết định, với màn trình diễn ấn tượng của Thanh Thúy và Lý Thị Luyến, tuyển bóng chuyền nữ Việt Nam đã giành chiến thắng với tỉ số 15-10. Chung cuộc, thầy trò HLV Nguyễn Tuấn Kiệt giành chiến thắng với tỉ số 3-2.\r\n\r\nSau trận này, tuyển bóng chuyền nữ Việt Nam sẽ được nghỉ một ngày, trước khi có trận thứ 2 gặp Hisamitsu Springs (Nhật Bản) vào ngày 27/4.', 'https://onsports.vn/post/thanh-thuy-ganh-team-bong-chuyen-nu-viet-nam-khoi-dau-an-tuong-tai-giai-chau-a', 'https://stvinaprod.vtvcab.vn/21d98861-f530-4c70-a169-2194687656a1.jpeg', 1, 1, 4, 'thanh-thuy-ganh-team-bong-chuyen-nu-viet-nam-khoi-dau-an-tuong-tai-giai-chau-a', 1, 1),
(69, 'ĐT bóng chuyền nam Philippines vẫn tiếp tục góp mặt tại SEA Games 32', '2023-04-18', 'Trong danh sách công bố bảng đấu bóng chuyền nam, BTC SEA Games 32 quyết định không bốc thăm lại và Philippines vẫn sẽ góp mặt ở môn thi này tại Campuchia.', 'Tại lễ bốc thăm SEA Games 32, Uỷ ban Olympic quốc gia Philippines đã quên đăng ký đội bóng chuyền nam và chỉ đăng ký đội tuyển nữ. Sau khi nhận thừa nhận sai sót không đáng có, các nước đã đồng ý bốc thăm lại để bóng chuyền nam Philippines có cơ hội được tham gia thi đấu. Được sự đồng thuận, BTC SEA Games 32 quyết định không bốc thăm lại và xếp Philipines vào bảng A cùng với Indonesia, Campuchia và Singapore.\r\n\r\nTại kỳ SEA Games 32, Philippines một lần nữa gặp gỡ đối thủ đã loại họ ngay từ vòng bảng tại SEA Games 31 là Campuchia. Bên cạnh đó, nước chủ nhà Campuchia năm nay cũng đặt rất nhiều kỳ vọng ở môn thi này nên Philippines sẽ phải đối mặt với rất nhiều khó khăn để có tấm vé vào vòng trong.\r\n\r\nNgày 3/5 sẽ bắt đầu khởi tranh môn bóng chuyền trong nhà tại NTĐ bóng chuyền nằm trong phức hợp Olympic Campuchia. Các đội tuyển nam sẽ thi đấu từ ngày 3/5 đến 8/5, các đội tuyển nữ sẽ bắt đầu từ ngày 9/5 đến 14/5.', 'https://onsports.vn/post/dt-bong-chuyen-nam-philippines-van-tiep-tuc-gop-mat-tai-sea-games-32', 'https://stvinaprod.vtvcab.vn/f8eea738-d7a1-4a6e-8b51-8ae63566a7f3.jpeg', 6, 1, 4, 'dt-bong-chuyen-nam-philippines-van-tiep-tuc-gop-mat-tai-sea-games-32', 1, 1),
(70, 'Top 5 nữ VĐV cao nhất giải bóng chuyền VĐQG 2023: Bích Tuyền chỉ đứng thứ 3', '2023-02-28', 'Trong tổng số gần 150 VĐV nữ tham dự vòng I giải bóng chuyền VĐQG 2023, VĐV cao nhất tiếp tục thuộc về vị trí ngoại binh, 4 cầu thủ tiếp theo đều là những cái tên nội.', 'Sau khi danh sách 5 đội bóng chuyền nữ bảng B được công bố, một trong những vấn đề khá thú vị được nhiều khán giả quan tâm là chiều cao của các nữ VĐV tại vòng I giải bóng chuyền VĐQG 2023.\r\n\r\nVới chiều cao lên tới 1m97, Lara Vukasovic - ngoại binh của Hà Phú Thanh Hóa, là người nắm giữ vị trí số một. Nữ VĐV người Croatia sinh năm 1994, cô bắt đầu sự nghiệp thi đấu chuyên nghiệp từ năm 2010 và từng khoác áo Đại học California Mỹ để thi đấu tại NCAA trong 5 năm.\r\n\r\nVị trí thứ hai thuộc về một cái tên rất quen thuộc với người hâm mộ bóng chuyền Việt Nam - Lý Thị Luyến. Với chiều cao 1m91, cô cùng với Lara Vukasovic là hai nữ VĐV có chiều cao trên 1m9 của giải. Về mặt chuyên môn, nữ VĐV sinh năm 2000 đang chơi ngày càng tiến bộ dưới thời của HLV Wannachote. Với lợi thế về chiều cao, \"khủng long\" Lý Luyến hứa hẹn sẽ là phụ công hàng đầu của bóng chuyền nữ Việt Nam.\r\n\r\nXếp thứ ba, sau Lara Vukasovic và Lý Thị Luyến là Nguyễn Thị Bích Tuyền, tay ghi điểm số một của Ninh Bình LienVietPostBank. Cô gái người Vĩnh Long không chỉ sở hữu chiều cao ấn tượng mà còn có sức bật và cái cổ tay vô cùng uy lực, thường xuyên tạo nên những pha đánh bóng không thể cản phá.\r\n\r\nBùi Thị Ngà được xếp ở vị trí thứ tư với chiều cao 1m86. Tuy nhiên, khả năng ra sân thi đấu của Bùi Thị Ngà được đánh giá là khá thấp, dù cựu đội trưởng BTL Thông Tin vẫn xuất hiện trong danh sách đăng ký thi đấu của đội bóng này.\r\n\r\nCũng trong màu áo BTL Thông Tin và xếp ở vị trí thứ 5 là Phạm Thị Lệ Thảo. Mặc dù chưa được HLV Phạm Minh Dũng cho thi đấu thường xuyên nhưng với chiều cao 1m85, Lệ Thảo vẫn là cái tên có nhiều tiềm năng.\r\n\r\nNgoài top 5 vận động viên nói trên, có thể kể đến một số cái tên khác cũng sở hữu chiều cao ấn tượng như Trần Thị Khánh, Trần Tú Linh cùng cao 1m83, Đinh Thị Trà Giang, Nguyễn Thị Uyên, Đoàn Thị Xuân hay Trần Thị Mỹ Hằng cùng sở hữu chiều cao 1m82.', 'https://onsports.vn/post/top-5-nu-vdv-cao-nhat-giai-bong-chuyen-vdqg-2023-bich-tuyen-chi-dung-thu-3', 'https://stvinaprod.vtvcab.vn/4f95c404-3feb-4996-a2fb-6dc2f4642e1d.jpeg', 6, 2, 4, 'top-5-nu-vdv-cao-nhat-giai-bong-chuyen-vdqg-2023-bich-tuyen-chi-dung-thu-3', 1, 1),
(71, 'Vietinbank và Thể Công lên ngôi vô địch giải bóng chuyền trẻ U23 quốc gia', '2022-11-17', 'Vietinbank bảo vệ thành công chức vô địch sau trận đấu đầy khó khăn trước BTL Thông tin. Trong khi đó, Thể Công giành chiến thắng nghẹt thở trước Trà Vinh trong trận chung kết giải bóng chuyền U23 quốc gia.', 'Vietinbank mang đến giải bóng chuyền trẻ U23 quốc gia đội hình mạnh nhất với những VĐV đang thi đấu tại giải VĐQG. Bởi vậy, đoàn quân của HLV Kim Huệ đã thẳng tiến đến trận chung kết đầy thuyết phục.\r\n\r\nTrong trận đấu cuối cùng của giải đấu, Vietinbank đã không gặp ít khó khăn trước các cô gái trẻ của BTL Thông tin. Ở vòng bảng, các học trò của HLV Kim Huệ giành chiến thắng 3-1 trước đối thủ nhưng trận chung kết lại căng thẳng và kịch tính hơn rất nhiều.\r\n\r\nViettinbank giành chiến thắng trong 2 set đấu đầu tiên. Nhưng sau đó, đội bóng áo lính đã vươn lên mạnh mẽ và thắng đậm ở set thứ 3. Tại set thứ 4, 2 đội bám đuổi kịch tính và nhời đến may mắn và kinh nghiệm trận mạc, các học trò của HLV Kim Huệ mới giành chiến thắng 3-1 trước BTL Thông tin, qua đó bảo vệ thành công ngôi vô địch giải bóng chuyền trẻ U23 quốc gia.\r\n\r\nTrong khi đó, tại nội dung nam, các chàng trẻ của Thể Công đã có cuộc lội ngược dòng ngoạn mục trước Trà Vinh. Dù được đánh giá thấp hơn nhưng Trà Vinh thi đấu vô cùng cùng thăng hoa và toàn thắng trong 2 set đấu đầu tiên.\r\n\r\nSau đó, Thể Công đã vùng lên mạnh mẽ và giành chiến thắng 3 set còn lại để lên ngôi vô địch. Đây là trận chung kết thứ ba và là chức vô địch trẻ thứ 2 trong năm của Thể Công.', 'https://onsports.vn/post/vietinbank-va-the-cong-len-ngoi-vo-dich-giai-bong-chuyen-tre-u23-quoc-gia', 'https://stvinaprod.vtvcab.vn/3d9d9d32-ddfb-4b88-972a-1afa558b26b5.jpg', 1, 1, 4, 'vietinbank-va-the-cong-len-ngoi-vo-dich-giai-bong-chuyen-tre-u23-quoc-gia', 1, 1);
INSERT INTO `posts` (`id`, `title`, `date`, `description`, `content`, `link_post`, `image`, `country_id`, `category_id`, `sport_id`, `slug`, `hot_news`, `status`) VALUES
(72, 'Thanh Thuý sắp đầu quân cho đội bóng chuyền Thổ Nhĩ Kỳ?', '2023-03-22', 'Mới đây, thông tin Trần Thị Thanh Thuý được một nhà tuyển trạch Thổ Nhĩ Kỳ để mắt tới được đăng tải trên một trang tin tức bóng chuyền khiến NHM tò mò.', 'Được biết, nhà tuyển trạch Thổ Nhĩ Kỳ đã để mắt đến Thanh Thuý và tìm cách liên lạc với đại diện Việt Nam sau khi người này sang Nhật Bản theo dõi giải bóng chuyền Vleague. Theo đó, nhà tuyển trạch này sẽ tiếp tục theo dõi tay chuyền người Việt Nam ở các trận đấu tiếp theo để tìm hiểu thêm Thanh Thuý cũng như một số VĐV khác của Việt Nam cho mùa giải mới ở Thổ Nhĩ Kỳ.\r\n\r\nCuối tuần này, Thanh Thuý sẽ khép lại mùa giải đầu tiên của mình tại Nhật Bản cùng CLB PFU Blue Cats. Sau đó, cô nàng sẽ trở về Việt Nam nghỉ ngơi và giành thời gian cho gia đình trước khi cùng ĐT bóng chuyền Việt Nam tham dự các giải đấu Quốc tế sắp tới.\r\n\r\nMới đây, Thanh Thuý đã ký hợp đồng 2 năm với CLB PFU Blue Cats và nếu như thông tin người tuyển trạch Thổ Nhĩ Kỳ để mắt tới cô là thật thì đây sẽ là cơ hội lớn để tay chuyền người Việt Nam có thể sang Châu Âu và phát triển bản thân hơn nữa trong sự nghiệp bóng chuyền của mình.', 'https://onsports.vn/post/thanh-thuy-sap-dau-quan-cho-doi-bong-chuyen-tho-nhi-ky', 'https://stvinaprod.vtvcab.vn/48ff6410-7a80-489c-a570-d05bb5f82416.jpeg', 1, 1, 4, 'thanh-thuy-sap-dau-quan-cho-doi-bong-chuyen-tho-nhi-ky', 1, 1),
(73, 'HLV Phạm Minh Dũng trở thành HLV trưởng đội bóng chuyền nữ Bộ tư lệnh Thông tin', '2023-03-22', 'Trong buổi giao nhiệm vụ trước khi tham dự Cúp Hùng Vương 2023 ngày 20/3, HLV Phạm Minh Dũng chính thức được bổ nhiệm làm HLV trưởng của đội bóng chuyền nữ BTL Thông tin.', 'Chiều ngày 20/3, HLV Phạm Minh Dũng đã được Phó Tham mưu trưởng Binh chủng Thông tin liên lạc – đại tá Trần Bá Khương trao quyết định bổ nhiệm trở thành HLV trưởng của đội bóng chuyền nữ BTL Thông tin. Tham dự buổi giao nhiệm vụ còn có sự góp mặt của các đồng chí đại biểu Phòng Quân huấn, Ban Chính trị Bộ Tham mưu và chỉ huy, ban huấn luyện đội 1 đội bóng chuyền nữ Bộ tư lệnh Thông tin; Quyết định trợ lý HLV tuyến 2 cho đồng chí Đỗ Thị Minh.\r\n\r\nTháng 5/2021, HLV Phạm Minh Dũng được bổ nhiệm làm quyền HLV trưởng đội bóng áo lính, thay vị trí cho cựu HLV trưởng đội 1 là HLV Bùi Huy Sơn. Trong thời gian giữ vai trò làm quyền HLV trưởng, ông cùng các học trò đã giành nhiều thành tích đáng nể trong 2 năm qua như: Vô địch quốc gia năm 2021, Vô địch cúp bóng chuyền quân đội 2022 và Vô địch Đại hội thể thao toàn quốc lần 9-2022... Mới đây, đội bóng chuyền nữ BTL Thông tin đã vượt qua vòng 1 giải bóng chuyền VĐQG 2023 để có tấm vé tham dự Cúp Hùng Vương 2023.\r\n\r\nỞ giải đấu sắp tới, HLV Phạm Minh Dũng vẫn sẽ không thuê ngoại binh mà tin tưởng vào các cô gái của đội 1 độ nữ bóng chuyền BTL Thông tin gồm: Nguyệt Anh, Thanh Hương, Việt Hương, Phạm Thị Hiền, Khánh Huyền, Mỹ Hằng, Lê Khanh, Ly Ly và Thùy Linh, Nguyễn Thị Phương, Phương Thùy, Lệ Thảo, Kiều Trinh và Lâm Oanh. Bên cạnh đó, HLV Phạm Thị Yến, HLV Nguyễn Trọng Linh và HLV Phạm Thị Thu Trang là trợ lý của HLV Phạm Minh Dũng sẽ đồng hành cùng đội bóng trong các giải đấu sắp tới.\r\n\r\nNgày 23/3 sẽ bắt đầu khởi tranh Cúp Hùng Vương 2023, BTL Thông tin sẽ có màn đối đầu nảy lửa với đội bóng chuyền nữ Ninh Bình vào lúc 19h30. Với những thành tích mà toàn đội đã xây dựng được trong thời gian qua, đội bóng áo lính đặt mục tiêu giành thành tích cao nhất tại giải đấu này.', 'https://onsports.vn/post/hlv-pham-minh-dung-tro-thanh-hlv-truong-doi-bong-chuyen-nu-bo-tu-lenh-thong-tin', 'https://stvinaprod.vtvcab.vn/0c25532d-acf3-48aa-8678-d8ddd6f1576c.jpg', 1, 1, 4, 'hlv-pham-minh-dung-tro-thanh-hlv-truong-doi-bong-chuyen-nu-bo-tu-lenh-thong-tin', 1, 1),
(74, 'Modric nhận lương siêu \'khủng\' nếu gia nhập CLB Ả Rập Xê-út', '2023-04-23', 'Tờ AS tiết lộ, chính phủ Ả Rập Xê-út có kế hoạch liên hệ trực tiếp với Modric kèm theo một lời đề nghị hấp dẫn để tiền vệ này đến quốc gia vùng Vịnh chơi bóng ở mùa giải tiếp theo.', 'Hợp đồng giữa Luka Modric với Real Madrid sẽ đáo hạn vào ngày 30/6. Thời điểm này, cả hai bên đều chưa có động thái muốn tiếp tục gia hạn.\r\n\r\nDù sắp bước sang tuổi 38, Modric vẫn được khá nhiều CLB săn đón. Trong đó, có một đội bóng ở Ả Rập Xê-út sẵn sàng đưa ra mức thu nhập hấp dẫn để có được chữ ký của tuyển thủ Croatia.\r\n\r\nTờ AS đưa tin, chính phủ Ả Rập Xê-út sẽ gửi cho tiền vệ Real lời đề nghị đến quốc gia vùng Vịnh thi đấu 2 mùa giải với mức lương lên đến 50 triệu euro mỗi mùa. Ngoài ra, anh cũng được phép tự chọn 1 CLB để thi đấu.\r\n\r\nLời đề nghị này rõ ràng là quá hấp dẫn. Bởi vậy, Modric có thể cân nhắc giữa việc thi đấu tại châu Âu thêm một mùa nữa hay sang châu Á dưỡng già giống như người đồng đội cũ Cristiano Ronaldo.\r\n\r\nHiện tại, Modric vẫn đang có phong độ cao tại Real Madrid. Mùa này, anh góp công lớn giúp đội chủ sân Bernabeu đánh bại đại kình địch Barcelona ở bán kết Copa De Rey và lọt vào vòng bán kết UEFA Champions League.\r\n\r\nTrong hơn 10 năm khoác áo Real Madrid, Luka Modric giành được mọi danh hiệu cao quý nhất ở cấp CLB mà một cầu thủ bóng đá có thể đạt được.\r\n\r\nỞ tuyển quốc gia, tiền vệ người Croatia chưa có ý định giải nghệ mà muốn tiếp tục cống hiến ở EURO 2024 diễn ra tại Đức. Anh vẫn cho thấy mình còn sung sức khi đã chơi đến 187 phút ở hai trận gặp Xứ Wales và Thổ Nhĩ Kỳ vào tháng trước.\r\n\r\nChia sẻ về dự định tương lai, Modric từng thừa nhận: \"Tôi muốn ở lại Real Madrid vì giá trị của riêng do bản thân tạo ra cho CLB, chứ không cần một bản hợp đồng thương mại. Nếu CLB cho rằng tôi xứng đáng với một bản hợp đồng mới, tôi rất sẵn lòng ở lại\".', 'https://onsports.vn/post/modric-nhan-luong-sieu-khung-neu-gia-nhap-clb-a-rap-xe-ut', 'https://stvinaprod.vtvcab.vn/60dd723f-4188-4ce3-9433-2b0ef745bebc.jpg', 4, 2, 1, 'modric-nhan-luong-sieu-khung-neu-gia-nhap-clb-a-rap-xe-ut', 1, 1),
(75, 'Barcelona có thể vô địch La Liga sớm mấy vòng?', '2023-03-20', 'Sau chiến thắng ở trận El Clasico, Barcelona lúc này đã tạo ra khoảng cách 12 điểm nhiều hơn đại kình địch Real Madrid và đã chạm tay vào chức vô địch La Liga 2022/23.', 'Lịch thi đấu 12 vòng còn lại đang hoàn toàn ủng hộ Barcelona. Bởi ở những chuyến phải thi đấu xa nhà, họ chỉ phải đụng độ các đội thuộc nhóm yếu như Rayo Vallecano, Elche, Getafe, Espanyol, Valladolid và Celta. Còn với những đội bóng được đánh giá là thử thách và đang cạnh tranh vé dự Champions League như Atletico Madrid, Real Betis và Real Sociedad, thì Barca lại được tiếp đón trên thánh địa Camp Nou.\r\n\r\nSo với đại kình địch Real, Barcelona cũng có lợi thế về lịch thi đấu khi họ không còn tham dự đấu trường châu Âu sau khi bị Man United loại ở Europa League. Ngoài trận lượt về bán kết Cúp Nhà Vua Tây Ban Nha với chính Real Madrid vào đầu tháng 4, Barca có thể toàn tâm toàn ý cho La Liga.\r\n\r\nHiện tại, Barca không chỉ có lợi thế 12 điểm nhiều hơn Real Madrid, mà đội bóng chủ sân Camp Nou còn nhỉnh hơn ở hiệu số bàn thắng bại (+40 so với +30). Thông số duy nhất mà Barca \"thiệt\" hơn Real là thành tích đối đầu (thua 1-3 ở lượt đi, thắng 2-1 ở lượt về). Theo thể thức của La Liga, thành tích đối đầu sẽ được xét đầu tiên trong trường hợp 2 đội bằng điểm nhau. Chính vì thế, về lý thuyết thì nếu Real không sảy chân, Barcelona phải giành chiến thắng 8 và hòa 1 ở 9 trận đấu tiếp theo mới có thể đăng quang. Đó là thời điểm diễn ra vòng đấu thứ 35.\r\n\r\nVề phía Real, ngoài chuyện đang thất thế tại La Liga, họ cũng vẫn phải căng mình cho những trận đấu căng thẳng tại tứ kết Champions League (gặp Chelsea). Nếu vào bán kết, họ sẽ còn phải chạm trán Bayern hoặc Man City.\r\n\r\nThử thách là không nhỏ với đội bóng Hoàng gia Tây Ban Nha khi ngay sau trận lượt về Cúp Nhà Vua Tây Ban Nha với Barcelona và trước trận lượt đi vòng tứ kết Champions League với Chelsea, họ sẽ phải chạm trán một đối thủ rất khó chịu là Villarreal. Hiện \"Tàu Ngầm Vàng\" cũng đang cạnh tranh quyết liệt tấm vé dự cúp châu Âu mùa tới. Chưa hết, theo lịch thi đấu thì sau trận tứ kết lượt về với Chelsea tại Champions League, Real Madrid sẽ bước vào trận đấu với Ceta Vigo, một đối thủ đang có phong độ tốt trong thời gian qua.\r\n\r\nNhìn chung, Real Madrid trong giai đoạn đầu tháng 4 sẽ gặp phải nhiều thử thách, và điều đó có thể sẽ tạo nên bước ngoặt giúp Barcelona giành chức vô địch sớm hơn. Nếu thuận lợi, dự kiến Barca có thể đăng quang La Liga ở vòng đấu thứ 33.', 'https://onsports.vn/post/barcelona-co-the-vo-dich-la-liga-som-may-vong', 'https://stvinaprod.vtvcab.vn/2e70a1f3-6665-448a-bb36-fad19e86c5d0.jpg', 4, 2, 1, 'barcelona-co-the-vo-dich-la-liga-som-may-vong', 1, 1);

